import useSWR, { mutate } from "swr";

import gql from "graphql-tag";
import Link from "next/link";
import { hasuraUserClient } from "../../lib/hasura-user-client";
import { format, parseISO } from "date-fns";
import FullCalendar from "@fullcalendar/react"; // must go before plugins
import dayGridPlugin from "@fullcalendar/daygrid";
import "@fullcalendar/common/main.css";
import "@fullcalendar/daygrid/main.css";
import interactionPlugin from "@fullcalendar/interaction";
import timeGridPlugin from "@fullcalendar/timegrid";
import "../../../styles/global-styles.css";
const today = new Date();
const GetProjects = gql`
  query MyQuery {
    Project {
      id
      Title
      Description
      created_at
    }
  }
`;
export const getStaticProps = async () => {
  const hasura = hasuraUserClient();

  const initialData = await hasura.request(GetProjects);

  return {
    props: {
      initialData,
    },
    revalidate: 1,
  };
};

export default function MembersPage({ initialData }) {
  const hasura = hasuraUserClient();

  const { data } = useSWR(GetProjects, (query) => hasura.request(query), {
    initialData,
    revalidateOnMount: true,
  });
  const deleteProject = async (id) => {
    const query = gql`
      mutation delete_Project($id: Int!) {
        delete_Project(where: { id: { _eq: $id } }) {
          affected_rows
        }
      }
    `;

    try {
      await hasura.request(query, { id });
      mutate();
      window.location.reload();
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="page-content-wrapper">
      <div className="page-content" style={{ minHeight: 1500 }}>
        <div class="row">
          <div class="col-md-12">
            <div class="portlet light bordered">
              <div class="portlet-title">
                <div class="caption font-dark">
                  <i class="icon-settings font-dark"></i>
                  <span class="caption-subject bold uppercase">
                    {" "}
                    Projects List
                  </span>
                </div>
              </div>
              <div class="portlet-body">
                <div class="table-toolbar">
                  <div class="row">
                    <div class="col-md-6">
                      <div class="btn-group">
                        <button
                          id="sample_editable_1_2_new"
                          class="btn sbold green"
                        >
                          {" "}
                          Add New
                          <i class="fa fa-plus"></i>
                        </button>
                      </div>
                    </div>
                    <div class="col-md-6">
                      <div class="btn-group pull-right">
                        <button
                          class="btn green  btn-outline dropdown-toggle"
                          data-toggle="dropdown"
                        >
                          Tools
                          <i class="fa fa-angle-down"></i>
                        </button>
                        <ul class="dropdown-menu pull-right">
                          <li>
                            <a href="#">
                              <i class="fa fa-print"></i> Print{" "}
                            </a>
                          </li>
                          <li>
                            <a href="#">
                              <i class="fa fa-file-pdf-o"></i> Save as PDF{" "}
                            </a>
                          </li>
                          <li>
                            <a href="#">
                              <i class="fa fa-file-excel-o"></i> Export to Excel{" "}
                            </a>
                          </li>
                        </ul>
                      </div>
                    </div>
                  </div>
                </div>
                <table
                  class="table table-striped table-bordered table-hover table-checkable order-column"
                  id="sample_1_2"
                >
                  <thead>
                    <tr>
                      <th>
                        <label class="mt-checkbox mt-checkbox-single mt-checkbox-outline">
                          <input
                            type="checkbox"
                            class="group-checkable"
                            data-set="#sample_1_2 .checkboxes"
                          />
                          <span></span>
                        </label>
                      </th>
                      <th> Title </th>
                      <th> Description </th>
                      <th> Creation date </th>
                      <th> Actions </th>
                    </tr>
                  </thead>
                  <tfoot>
                    <tr>
                      <th> </th>
                      <th> </th>
                      <th> </th>
                      <th> </th>
                      <th> </th>
                    </tr>
                  </tfoot>
                  <tbody>
                    {data.Project.map(
                      ({ id, Title, Description, created_at }) => {
                        const formattedJoinedAt = format(
                          parseISO(created_at),
                          "yyyy-MM-dd"
                        );

                        return (
                          <tr key={id} class="odd gradeX">
                            <td>
                              <label class="mt-checkbox mt-checkbox-single mt-checkbox-outline">
                                <input
                                  type="checkbox"
                                  class="checkboxes"
                                  value="1"
                                />
                                <span></span>
                              </label>
                            </td>
                            <td>{Title}</td>
                            <td> {Description}</td>
                            <td class="center"> {formattedJoinedAt} </td>
                            <td>
                              <div class="btn-group">
                                <button
                                  class="btn btn-xs green dropdown-toggle"
                                  type="button"
                                  data-toggle="dropdown"
                                  aria-expanded="false"
                                >
                                  {" "}
                                  Actions
                                  <i class="fa fa-angle-down"></i>
                                </button>
                                <ul class="dropdown-menu pull-left" role="menu">
                                  <li>
                                    <a href="#">
                                      <i class="icon-user"></i> New Project{" "}
                                    </a>
                                  </li>
                                  <li class="divider"> </li>{" "}
                                  <li>
                                    <Link href={`/Project/${id}`}>
                                      <a>Edit</a>
                                    </Link>
                                  </li>
                                  <li>
                                    <a
                                      className="closeBtn"
                                      onClick={() => deleteProject(id)}
                                    >
                                      Delete Project
                                    </a>
                                  </li>
                                </ul>
                              </div>
                            </td>{" "}
                          </tr>
                        );
                      }
                    )}
                  </tbody>
                </table>{" "}
                <FullCalendar
                  plugins={[dayGridPlugin]}
                  initialView="dayGridMonth"
                  weekends={false}
                  events={data.Project.map(({ id, Title, created_at }) => {
                    const formattedJoinedAt = format(
                      parseISO(created_at),
                      "yyyy-MM-dd"
                    );
                    return { title: Title, date: formattedJoinedAt };
                  })}
                />{" "}
                <FullCalendar
                  plugins={[interactionPlugin, timeGridPlugin]}
                  initialView="timeGridWeek"
                  nowIndicator={true}
                  editable={true}
                  initialEvents={[{ title: "nice event", start: new Date() }]}
                />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
