import { useEffect } from "react";
import useSWR from "swr";

import { useRouter } from "next/router";
import { useForm } from "react-hook-form";
import { gql, hasuraUserClient } from "../../lib/hasura-user-client";
import { useAuthState, useAuthDispatch } from "../../context/auth";
const created_at = new Date().toISOString();

const GetCandidateIds = gql`
  {
    Candidate {
      id
      Email
      Last_Name
      Name
      domain
      phone_number
      Status
    }
  }
`;

const GetCandidateById = gql`
  query GetCategoryById($id: Int!) {
    Candidate_by_pk(id: $id) {
      id
      Email
      Last_Name
      Name
      domain
      phone_number
      Status
    }
  }
`;

export const getStaticPaths = async () => {
  const hasura = hasuraUserClient();

  const { Candidate } = await hasura.request(GetCandidateIds);

  return {
    paths: Candidate.map(({ id }) => ({
      params: {
        id: id.toString(),
      },
    })),
    fallback: false,
  };
};

export const getStaticProps = async ({ params }) => {
  const hasura = hasuraUserClient();
  const { id } = params;
  console.log(params);
  console.log(id);
  const initialData = await hasura.request(GetCandidateById, { id });

  return {
    props: {
      initialData,
    },
    revalidate: 1,
  };
};
export default function RegisterPage({ initialData }) {
  const { query } = useRouter();
  const router = useRouter();
  const hasura = hasuraUserClient();
  const { id } = query;
  const { isAuthenticated } = useAuthState();
  const { register: createUser } = useAuthDispatch();
  const {
    handleSubmit,
    register,
    errors,
    formState: { isSubmitting },
    setError,
  } = useForm({ defaultValues: { save_last_seen: true } });

  useEffect(() => {
    if (!isAuthenticated) router.push("/");
  }, [isAuthenticated]);

  const { data, mutate } = useSWR(
    [GetCandidateById, id],
    (query, id) => hasura.request(query, { id }),
    {
      initialData,
      revalidateOnMount: true,
    }
  );
  // if (isAuthenticated) return null;
  const onSubmit = async ({
    name,
    last_name,
    email,
    domain,
    phone_number,
    password,
    save_last_seen,
  }) => {
    const SetStatus = gql`
      mutation MyMutation($id: Int!) {
        update_Candidate_by_pk(pk_columns: { id: $id }, _set: { Status: 1 }) {
          id
          Status
        }
      }
    `;
    try {
      await hasura.request(SetStatus, { id: id });
      mutate();
      await createUser({
        name,
        last_name,
        email,
        domain,
        phone_number,
        password,
        save_last_seen,
        created_at,
      });

      await fetch("/api/AcceptEmail", {
        method: "POST",
        headers: { "content-type": "application/json" },
        body: JSON.stringify(data),
      });

      router.push("/CandidatList");
    } catch ({ message }) {
      setError("email", {
        type: "manual",
        message,
      });
    }
  };

  return (
    <div className="page-content-wrapper">
      <div className="page-content" style={{ minHeight: 1500 }}>
        <form class="register-form" onSubmit={handleSubmit(onSubmit)}>
          <div class="content">
            {" "}
            <h3 class="font-green">Register Candidate</h3>{" "}
            <div class="form-group">
              <input
                defaultValue={data.Candidate_by_pk.Name}
                class="form-control form-control-solid placeholder-no-fix"
                name="name"
                id="name"
                ref={register}
                placeholder="Your name"
              />
            </div>
            <div class="form-group">
              <input
                defaultValue={data.Candidate_by_pk.Last_Name}
                class="form-control form-control-solid placeholder-no-fix"
                name="last_name"
                id="last_name"
                ref={register}
                placeholder="Your name"
              />
            </div>
            <div class="form-group">
              <input
                defaultValue={data.Candidate_by_pk.phone_number}
                class="form-control form-control-solid placeholder-no-fix"
                name="phone_number"
                id="phone_number"
                ref={register}
                placeholder="Your name"
              />
            </div>{" "}
            <div class="form-group">
              <input
                defaultValue={data.Candidate_by_pk.domain}
                class="form-control form-control-solid placeholder-no-fix"
                name="domain"
                id="domain"
                ref={register}
                placeholder="Domain"
              />
            </div>
            {errors.name && <span>{errors.name.message}</span>}
            <div class="form-group">
              {" "}
              <input
                defaultValue={data.Candidate_by_pk.Email}
                class="form-control form-control-solid placeholder-no-fix"
                type="email"
                name="email"
                id="email"
                ref={register({
                  required: "You must provide a email.",
                  pattern: {
                    value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                    message: "You must provide a valid email",
                  },
                })}
                placeholder="Your email"
              />
              {errors.email && <span>{errors.email.message}</span>}
            </div>
            <div class="form-group">
              {" "}
              <input
                class="form-control form-control-solid placeholder-no-fix"
                type="password"
                name="password"
                id="password"
                ref={register({
                  required: "You must provide a password.",
                  minLength: {
                    message: "Your password must be at least 6 characters",
                    value: 6,
                  },
                })}
                placeholder="Choose a password"
              />
              {errors.password && <span>{errors.password.message}</span>}
            </div>
            {/* <div class="form-group">
              {" "}
              <label
                htmlFor="save_last_seen"
                class="rememberme check mt-checkbox mt-checkbox-outline"
              >
                <input
                  type="checkbox"
                  id="save_last_seen"
                  name="save_last_seen"
                  ref={register}
                />
                Show as online to other users<span></span>
              </label>
            </div> */}
            <div class="create-account">
              <button
                type="submit"
                disabled={isSubmitting}
                class="btn green uppercase"
              >
                Create account
              </button>
            </div>{" "}
          </div>
        </form>
      </div>
    </div>
  );
}
