import { useState, useEffect } from "react";
import { useRouter } from "next/router";
import { useForm, Controller } from "react-hook-form";
import Layout from "./Components/Layout";

import { useAuthState } from "../context/auth";
import { gql, hasuraAdminClient } from "../lib/hasura-admin-client";
import { hasuraUserClient } from "../lib/hasura-user-client";

const GetUsers = gql`
  {
    User {
      Name
      id
    }
  }
`;

const InsertTask = gql`
  mutation MyMutation(
    $Email: String!
    $Last_Name: String!
    $Name: String!
    $domain: String!
    $number: String!
  ) {
    insert_Candidate_one(
      object: {
        Email: $Email
        Last_Name: $Last_Name
        Name: $Name
        domain: $domain
        phone_number: $number
      }
    ) {
      Email
      Last_Name
      Name
      domain
      id
      phone_number
    }
  }
`;

export const getStaticProps = async () => {
  const { User } = await hasuraAdminClient.request(GetUsers);

  return {
    props: {
      User,
    },
  };
};

export default function Candidat({ User }) {
  const router = useRouter();
  const { isAuthenticated, user } = useAuthState();
  const hasura = hasuraUserClient();
  const {
    handleSubmit,
    register,
    control,
    errors,
    formState: { isSubmitting },
  } = useForm();
  const [selectedTab, setSelectedTab] = useState("write");

  useEffect(() => {
    if (!isAuthenticated) router.push("/");
  }, [isAuthenticated]);

  if (!isAuthenticated) return null;

  const onSubmit = async ({
    Email,
    Last_Name,
    Name,
    domain,
    id,
    phone_number,
  }) => {
    try {
      const { insert_Candidate_one } = await hasura.request(InsertTask, {
        Email,
        Last_Name,
        Name,
        domain,
        id,
        phone_number,
      });

      router.push(`/Candidat/${insert_Candidate_one.id}`);
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <>
      <Layout>
        <div className="page-content-wrapper">
          <div className="page-content" style={{ minHeight: 1500 }}>
            <div class="profile-content">
              <div class="row">
                <div class="col-md-12">
                  <div class="portlet light ">
                    <div class="portlet-body">
                      <div class="tab-content">
                        <div class="tab-pane active" id="tab_1_1">
                          <form role="form" onSubmit={handleSubmit(onSubmit)}>
                            <div class="form-group">
                              <label class="control-label">First Name</label>
                              <input
                                name="title"
                                id="title"
                                type="text"
                                placeholder="John"
                                class="form-control"
                              />{" "}
                            </div>
                            <div class="form-group">
                              <label class="control-label">Last Name</label>
                              <input
                                type="text"
                                placeholder="Doe"
                                class="form-control"
                              />{" "}
                            </div>
                            <div class="form-group">
                              <label class="control-label">Mobile Number</label>
                              <input
                                type="text"
                                placeholder="+1 646 580 DEMO (6284)"
                                class="form-control"
                              />{" "}
                            </div>
                            <div class="form-group">
                              <label class="control-label">Interests</label>
                              <input
                                type="text"
                                placeholder="Design, Web etc."
                                class="form-control"
                              />{" "}
                            </div>
                            <div class="form-group">
                              <label class="control-label">Occupation</label>
                              <input
                                type="text"
                                placeholder="Web Developer"
                                class="form-control"
                              />{" "}
                            </div>
                            <div class="form-group">
                              <label class="control-label">About</label>
                              <textarea
                                class="form-control"
                                rows="3"
                                placeholder="We are KeenThemes!!!"
                              ></textarea>
                            </div>
                            <div class="form-group">
                              <label class="control-label">Website Url</label>
                              <input
                                type="text"
                                placeholder="http://www.mywebsite.com"
                                class="form-control"
                              />{" "}
                            </div>
                            <div class="margiv-top-10">
                              <button
                                type="submit"
                                class="btn green"
                                disabled="isSubmitting"
                              >
                                {" "}
                                Save Changes{" "}
                              </button>
                              <a href="#" class="btn default">
                                {" "}
                                Cancel{" "}
                              </a>
                            </div>
                          </form>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>{" "}
        </div>
      </Layout>
    </>
  );
}
