import { useEffect } from "react";
import { useRouter } from "next/router";
import { useForm } from "react-hook-form";

import { useAuthState } from "../context/auth";
import { gql, hasuraAdminClient } from "../lib/hasura-admin-client";
import { hasuraUserClient } from "../lib/hasura-user-client";
const joined = new Date().toISOString();
const GetUsers = gql`
  {
    User {
      Name
      id
    }
  }
`;

const InsertTask = gql`
  mutation InsertTask(
    $Email: String!
    $Last_Name: String!
    $Name: String!
    $domain: String!
    $phone_number: String!
    $joined: timestamptz!
  ) {
    insert_Candidate_one(
      object: {
        Email: $Email
        Last_Name: $Last_Name
        Name: $Name
        domain: $domain
        phone_number: $phone_number
        joined: $joined
      }
    ) {
      Email
      Last_Name
      Name
      domain
      id
      phone_number
      joined
    }
  }
`;

export const getStaticProps = async () => {
  const { User } = await hasuraAdminClient.request(GetUsers);

  return {
    props: {
      User,
    },
  };
};

export default function Candidat() {
  const router = useRouter();
  const { isAuthenticated, user } = useAuthState();
  const hasura = hasuraUserClient();
  const {
    handleSubmit,
    register,
    control,
    errors,
    formState: { isSubmitting },
  } = useForm();

  useEffect(() => {
    if (!isAuthenticated) router.push("/");
  }, [isAuthenticated]);

  if (!isAuthenticated) return null;

  const onSubmit = async ({ Email, Last_Name, Name, domain, phone_number }) => {
    console.log(phone_number);
    try {
      const { insert_Candidate_one } = await hasura.request(InsertTask, {
        Email,
        Last_Name,
        Name,
        domain,
        phone_number,
        joined,
      });

      router.push(`/CandidatList`);
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <>
      <div className="page-content-wrapper">
        <div className="page-content" style={{ minHeight: 1500 }}>
          <div className="profile-content">
            <div className="row">
              <div className="col-md-12">
                <div className="portlet light ">
                  <div className="portlet-body">
                    <div className="tab-content">
                      <div className="tab-pane active" id="tab_1_1">
                        <form onSubmit={handleSubmit(onSubmit)}>
                          <div className="form-group">
                            <label className="control-label">First Name</label>
                            <input
                              name="Name"
                              id="Name"
                              type="text"
                              placeholder="First Name"
                              className="form-control"
                              ref={register({
                                required: "You must provide a name",
                              })}
                            />{" "}
                          </div>
                          <div className="form-group">
                            <label className="control-label">Last Name</label>
                            <input
                              name="Last_Name"
                              id="Last_Name"
                              type="text"
                              placeholder="Last Name"
                              className="form-control"
                              ref={register({
                                required: "You must provide a Last Name",
                              })}
                            />{" "}
                          </div>
                          <div className="form-group">
                            <label className="control-label">Email</label>
                            <input
                              name="Email"
                              id="Email"
                              type="email"
                              placeholder="Email"
                              className="form-control"
                              ref={register({
                                required: "You must provide an email",
                              })}
                            />{" "}
                          </div>
                          <div className="form-group">
                            <label className="control-label">
                              Mobile Number
                            </label>
                            <input
                              name="phone_number"
                              id="phone_number"
                              type="text"
                              placeholder="+1 646 580 DEMO (6284)"
                              className="form-control"
                              ref={register({
                                required: "You must provide a mobile number",
                              })}
                            />{" "}
                          </div>

                          <div className="form-group">
                            <label className="control-label">Domain</label>
                            <input
                              name="domain"
                              id="domain"
                              type="text"
                              placeholder="Web Developer"
                              className="form-control"
                              ref={register({
                                required: "You must provide his domain",
                              })}
                            />{" "}
                          </div>
                          <div className="margiv-top-10">
                            <button
                              type="submit"
                              className="btn green"
                              disabled={isSubmitting}
                            >
                              {" "}
                              Save Changes{" "}
                            </button>
                          </div>
                        </form>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>{" "}
      </div>
    </>
  );
}
