import { useEffect } from "react";
import { useRouter } from "next/router";
import { useForm } from "react-hook-form";
import React from "react";
import Layoutlogin from "../pages/Components/Layoutlogin";
import { useAuthState, useAuthDispatch } from "../context/auth";

export default function RegisterPage() {
  const router = useRouter();
  const { isAuthenticated, user } = useAuthState();
  const { login } = useAuthDispatch();
  const {
    handleSubmit,
    register,
    errors,
    formState: { isSubmitting },
    setError,
  } = useForm({ defaultValues: { save_last_seen: true } });

  useEffect(() => {
    if (isAuthenticated) {
      if (user.Role === "Admin") {
        router.push("/");
      } else {
        router.push(`/Profile/${user.id}`);
      }
    }
  }, [isAuthenticated]);

  if (isAuthenticated) return null;

  const onSubmit = async ({ email, password, save_last_seen }) => {
    try {
      await login({ email, password, save_last_seen });
    } catch ({ message }) {
      setError("email", {
        type: "manual",
        message,
      });
    }
  };

  return (
    // <div className="page-content-wrapper">
    //   <div className="page-content" style={{ minHeight: 1500 }}>
    //     <img
    //       src="../assets/pages/img/AMAI-AI-Experts-white.inline.svg"
    //       style={{ height: 17 }}
    //       alt=""
    //     />{" "}
    //     <h3 class="form-title font-green">Sign In</h3>
    //     <div class="alert alert-danger display-hide">
    //       <button class="close" data-close="alert"></button>
