    //     </div>
    //     <form onSubmit={handleSubmit(onSubmit)} class="login-form">
    //       <div class="form-group">
    //         <input
    //           class="form-control form-control-solid placeholder-no-fix"
    //           type="email"
    //           name="email"
    //           id="email"
    //           ref={register({
    //             value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
    //             required: "You must provide a email.",
    //           })}
    //           placeholder="Your email"
    //         />
    //         {errors.email && <span>{errors.email.message}</span>}
    //       </div>
    //       <div>
    //         <label class="control-label visible-ie8 visible-ie9">
    //           Password
    //         </label>
    //         <input
    //           class="form-control placeholder-no-fix"
    //           type="password"
    //           name="password"
    //           id="password"
    //           ref={register({
    //             required: "You must provide a password.",
    //           })}
    //           placeholder="Enter your password"
    //         />
    //         {errors.password && <span>{errors.password.message}</span>}
    //       </div>

    //       <div class="form-actions">
    //         <button
    //           id="save_last_seen"
    //           name="save_last_seen"
    //           ref={register}
    //           class="btn green uppercase"
    //           type="submit"
    //           disabled={isSubmitting}
    //         >
    //           Login
    //         </button>{" "}
    //         {/* <label
    //           htmlFor="save_last_seen"
    //           class="rememberme check mt-checkbox mt-checkbox-outline"
    //         >
    //           <input
    //             type="checkbox"
    //             id="save_last_seen"
    //             name="save_last_seen"
    //             ref={register}
    //           />
    //           Show as online to other users <span></span>
    //         </label> */}
    //       </div>
    //     </form>
    //   </div>
    // </div>
    <div class="login">
      <div class="user-login-5">
        <div class="row bs-reset">
          <div class="col-md-6 login-container bs-reset">
            <img
              class="login-logo login-6"
              src="../assets/pages/img/login/login-invert.png"
            />
            <div class="login-content">
              <h1>Metronic Admin Login</h1>
              <p>
                {" "}
                Lorem ipsum dolor sit amet, coectetuer adipiscing elit sed diam
                nonummy et nibh euismod aliquam erat volutpat. Lorem ipsum dolor
                sit amet, coectetuer adipiscing.{" "}
              </p>
              <form onSubmit={handleSubmit(onSubmit)} class="login-form">
                <div class="form-group">
                  <input
                    class="form-control form-control-solid placeholder-no-fix"
                    type="email"
                    name="email"
                    id="email"
                    ref={register({
                      value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                      required: "You must provide a email.",
                    })}
                    placeholder="Your email"
                  />
                  {errors.email && <span>{errors.email.message}</span>}
                </div>
                <div>
                  <label class="control-label visible-ie8 visible-ie9">
                    Password
                  </label>
                  <input
                    class="form-control placeholder-no-fix"
                    type="password"
                    name="password"
                    id="password"
                    ref={register({
                      required: "You must provide a password.",
                    })}
                    placeholder="Enter your password"
                  />
                  {errors.password && <span>{errors.password.message}</span>}
                </div>

                <div class="form-actions">
                  <button
                    id="save_last_seen"
                    name="save_last_seen"
                    ref={register}
                    class="btn green uppercase"
                    type="submit"
                    disabled={isSubmitting}
                  >
                    Login
                  </button>{" "}
                  {/* <label
              htmlFor="save_last_seen"
              class="rememberme check mt-checkbox mt-checkbox-outline"
            >
