import useSWR, { mutate } from "swr";
import { useRouter } from "next/router";
import { useAuthState } from "../../context/auth";
import { useEffect } from "react";
import gql from "graphql-tag";
import Link from "next/link";
import { hasuraUserClient } from "../../lib/hasura-user-client";
import { format, parseISO } from "date-fns";
import FullCalendar from "@fullcalendar/react"; // must go before plugins
import dayGridPlugin from "@fullcalendar/daygrid";
import "@fullcalendar/common/main.css";
import "@fullcalendar/daygrid/main.css";
import "@fullcalendar/timegrid/main.css";
import "@fullcalendar/list/main.css";
import resourceTimelinePlugin from "@fullcalendar/resource-timeline";
import interactionPlugin from "@fullcalendar/interaction";
import timeGridPlugin from "@fullcalendar/timegrid";
import listPlugin from "@fullcalendar/list";
import { TextField } from "@material-ui/core";
import { registerLocale } from "react-datepicker";
import { useForm } from "react-hook-form";
const today = new Date();
const GetProjects = gql`
  query MyQuery {
    Project {
      id
      Title
      Description
      created_at
      delivery_date
      status
      company_name
    }
  }
`;
export const getStaticProps = async () => {
  const hasura = hasuraUserClient();

  const initialData = await hasura.request(GetProjects);

  return {
    props: {
      initialData,
    },
    revalidate: 1,
  };
};

export default function ListProject({ initialData }) {
  const hasura = hasuraUserClient();
  const router = useRouter();
  const { isAuthenticated, user } = useAuthState();
  useEffect(() => {
    if (!isAuthenticated) router.push("/");
  }, [isAuthenticated]);

  const {
    handleSubmit,
    register,
    control,
    errors,
    formState: { isSubmitting },
  } = useForm();
  const { data } = useSWR(GetProjects, (query) => hasura.request(query), {
    initialData,
    revalidateOnMount: true,
  });
  const deleteProject = async (id) => {
    const query = gql`
      mutation delete_Project($id: Int!) {
        delete_Project(where: { id: { _eq: $id } }) {
          affected_rows
        }
      }
    `;

    try {
      await hasura.request(query, { id });
      mutate();
      window.location.reload();
    } catch (error) {
      console.error(error);
    }
  };
  const handleDateClick = (arg) => {
    // bind with an arrow function
    alert(arg.dateStr);
  };
  const onSubmit = async ({ datetime }) => {
    console.log(datetime);
  };
  return (
    <div className="page-content-wrapper">
      <div className="page-content" style={{ minHeight: 1500 }}>
        <div class="row">
          <div class="col-md-12">
            <div class="portlet light bordered">
              <div class="portlet-title">
                <div class="caption font-dark">
                  <i class="icon-settings font-dark"></i>
                  <span class="caption-subject bold uppercase">
                    {" "}
                    Projects List
                  </span>
                </div>
              </div>
              <div class="portlet-body">
                <div class="table-toolbar">
                  <div class="row">
                    <div class="col-md-6">
                      <div class="btn-group">
                        <button
                          id="sample_editable_1_2_new"
                          class="btn sbold green"
                        >
                          {" "}
                          Add New
                          <i class="fa fa-plus"></i>
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
                <table
                  class="table table-striped table-bordered table-hover table-checkable order-column"
                  id="sample_1_2"
                >
                  <thead>
                    <tr>
                      <th>
                        <label class="mt-checkbox mt-checkbox-single mt-checkbox-outline">
                          <input
                            type="checkbox"
                            class="group-checkable"
                            data-set="#sample_1_2 .checkboxes"
                          />
                          <span></span>
                        </label>
                      </th>
                      <th> Title </th>
                      <th> Description </th>
                      <th> Creation date </th>
                      <th> Actions </th>
                    </tr>
                  </thead>
                  <tfoot>
                    <tr>
                      <th> </th>
                      <th> </th>
                      <th> </th>
                      <th> </th>
                      <th> </th>
                    </tr>
                  </tfoot>
                  <tbody>
                    {data.Project.map(
                      ({ id, Title, Description, created_at }) => {
                        const formattedJoinedAt = format(
                          parseISO(created_at),
                          "yyyy-MM-dd"
                        );

                        return (
                          <tr key={id} class="odd gradeX">
                            <td>
                              <label class="mt-checkbox mt-checkbox-single mt-checkbox-outline">
                                <input
                                  type="checkbox"
                                  class="checkboxes"
                                  value="1"
                                />
                                <span></span>
                              </label>
                            </td>
                            <td>{Title}</td>
                            <td> {Description}</td>
                            <td class="center"> {formattedJoinedAt} </td>
                            <td>
                              <div class="btn-group">
                                <button
                                  class="btn btn-xs green dropdown-toggle"
                                  type="button"
                                  data-toggle="dropdown"
                                  aria-expanded="false"
                                >
                                  {" "}
                                  Actions
                                  <i class="fa fa-angle-down"></i>
                                </button>
                                <ul class="dropdown-menu pull-left" role="menu">
                                  <li>
                                    <a href="#">More info</a>
                                  </li>
                                  <li class="divider"> </li>{" "}
                                  <li>
                                    <Link href={`/Project/${id}`}>
                                      <a>Edit</a>
                                    </Link>
                                  </li>{" "}
                                  <li>
                                    <Link href={`/Assign/${id}`}>
                                      <a>Assign Project</a>
                                    </Link>
                                  </li>
                                  <li>
                                    <a
                                      className="closeBtn"
                                      onClick={() => deleteProject(id)}
                                    >
                                      Delete Project
                                    </a>
                                  </li>
                                </ul>
                              </div>
                            </td>{" "}
                          </tr>
                        );
                      }
                    )}
                  </tbody>
                </table>{" "}
              </div>{" "}
              <div class="portlet light portlet-fit portlet-datatable bordered">
                <div class="portlet-title">
                  <div class="caption">
                    <i class="icon-settings font-green"></i>
                    <span class="caption-subject font-green sbold uppercase">
                      Tools
                    </span>
                  </div>
                  <div class="actions">
                    <div class="btn-group">
                      <a
                        class="btn red btn-outline btn-circle"
                        href="javascript:;"
                        data-toggle="dropdown"
                      >
                        <i class="fa fa-share"></i>
                        <span class="hidden-xs"> Trigger Tools </span>
                        <i class="fa fa-angle-down"></i>
                      </a>
                      <ul class="dropdown-menu pull-right" id="sample_3_tools">
                        <li>
                          <a
                            href="javascript:;"
                            data-action="0"
                            class="tool-action"
                          >
                            <i class="icon-printer"></i> Print
                          </a>
                        </li>
                        <li>
                          <a
                            href="javascript:;"
                            data-action="1"
                            class="tool-action"
                          >
                            <i class="icon-check"></i> Copy
                          </a>
                        </li>
                        <li>
                          <a
                            href="javascript:;"
                            data-action="2"
                            class="tool-action"
                          >
                            <i class="icon-doc"></i> PDF
                          </a>
                        </li>
                        <li>
                          <a
                            href="javascript:;"
                            data-action="3"
                            class="tool-action"
                          >
                            <i class="icon-paper-clip"></i> Excel
                          </a>
                        </li>
                        <li>
                          <a
                            href="javascript:;"
                            data-action="4"
                            class="tool-action"
                          >
                            <i class="icon-cloud-upload"></i> CSV
                          </a>
                        </li>
                        <li class="divider"> </li>
                        <li>
                          <a
                            href="javascript:;"
                            data-action="5"
                            class="tool-action"
                          >
                            <i class="icon-refresh"></i> Reload
                          </a>
                        </li>
                      </ul>
                    </div>
                  </div>
                </div>
                <div class="portlet-body">
                  <div class="table-container">
                    <table
                      class="table table-striped table-bordered table-hover"
                      id="sample_3"
                    >
                      <thead>
                        <tr>
                          <th> Client</th>
                          <th> Project title</th>
                          <th> description </th>
                          <th> created at </th>
                          <th> status </th>
                          <th> delivery date </th>
                        </tr>
                      </thead>
                      <tbody>
                        {" "}
                        {data.Project.map(
                          ({
                            id,
                            Title,
                            Description,
                            status,
                            created_at,
                            delivery_date,
                            company_name,
                          }) => {
                            const formattedJoinedAt = format(
                              parseISO(created_at),
                              "yyyy-MM-dd"
                            );

                            const formattedddate = format(
                              parseISO(delivery_date),
                              "yyyy-MM-dd"
                            );
                            return (
                              <tr key={id}>
                                {" "}
                                <td>{company_name} </td>
                                <td>{Title} </td>
                                <td>{Description}</td>
                                <td> {formattedJoinedAt}</td>
                                <td> {status} </td>
                                <td> {formattedddate} </td>
                              </tr>
                            );
                          }
                        )}
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
