import React from "react";
import Link from "next/link";
import { useAuthState, useAuthDispatch } from "../context/auth";
import Image from "react-bootstrap/Image";
import useSWR, { mutate } from "swr";

import gql from "graphql-tag";
import { useRouter } from "next/router";

import { hasuraUserClient } from "../lib/hasura-user-client";

const Notification_count = gql`
  query MyQuery {
    notification {
      name
      seen
      user_id
      id
      description
    }
    notification_aggregate {
      aggregate {
        count(columns: id)
      }
    }
  }
`;
export const getStaticProps = async () => {
  const hasura = hasuraUserClient();

  const initialData = await hasura.request(Notification_count);
  console.log(initialData);

  return {
    props: {
      initialData,
    },
    revalidate: 1,
  };
};

export default function Notification({ initialData }) {
  const { logout } = useAuthDispatch();
  const { isAuthenticated, user } = useAuthState();
  const hasura = hasuraUserClient();

  const { data } = useSWR(
    Notification_count,
    (query) => hasura.request(query),
    {
      initialData,
      revalidateOnMount: true,
    }
  );
  console.log(data);
  return (
    <div className="page-content-wrapper">
      {" "}
      <div className="page-content" style={{ minHeight: 1500 }}></div>
    </div>
  );
}
