import useSWR, { mutate } from "swr";
import { useEffect } from "react";
import { useRouter } from "next/router";
import { useForm } from "react-hook-form";
import React, { useState } from "react";
import { Form } from "react-bootstrap";
import Link from "next/link";
import gql from "graphql-tag";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { useAuthState } from "../../context/auth";
import { hasuraUserClient } from "../../lib/hasura-user-client";

const Created = new Date().toISOString();

const InsertProject = gql`
  mutation InsertProject(
    $Description: String!
    $Title: String!
    $Created: timestamp!
    $delivery_date: timestamptz!
    $contact_date: timestamptz!
    $company_name: String!
    $additional_inf: String!
    $type: String!
    $income: String!
    $file: String!
  ) {
    insert_Project_one(
      object: {
        Description: $Description
        Title: $Title
        created_at: $Created
        delivery_date: $delivery_date
        assigned: false
        contact_date: $contact_date
        company_name: $company_name
        additional_inf: $additional_inf
        type: $type
        file: $file
        income: $income
      }
    ) {
      Description
      created_at
      Title
      delivery_date
      assigned
      contact_date
      company_name
      additional_inf
      type
      file
      income
    }
  }
`;

export default function Projects({ initialData }) {
  const [startDate, setStartDate] = useState(new Date());
  const [EndDate, setEndDate] = useState(new Date());
  const [File, setFile] = useState(null);

  const [createObjectURL, setCreateObjectURL] = useState(null);
  const { isAuthenticated, user } = useAuthState();

  const hasura = hasuraUserClient();
  const router = useRouter();
  const {
    handleSubmit,
    register,
    control,
    errors,
    formState: { isSubmitting },
  } = useForm();
  const uploadFileToClient = (event) => {
    if (event.target.files && event.target.files[0]) {
      const i = event.target.files[0];
      setFile(i);
      setCreateObjectURL(URL.createObjectURL(i));
    }
  };
  useEffect(() => {
    if (!isAuthenticated) router.push("/");
  }, [isAuthenticated]);

  const onSubmit = async ({
    Description,
    Title,
    company_name,
    type,
    income,
  }) => {
    const delivery_date = startDate.toISOString();
    const contact_date = EndDate.toISOString();
    console.log(delivery_date);
    try {
      const body = new FormData();
      body.append("file", File);
      const response = await fetch("/api/file/", {
        method: "POST",
        body,
      });
      const { insert_Project_one } = await hasura.request(InsertProject, {
        Description,
        Created,
        Title,
        delivery_date,
        contact_date,
        company_name,
        type,
        file: File.name,
        income,
      });

      router.push(`/Project/List`);
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className="page-content-wrapper">
      <div className="page-content" style={{ minHeight: 1500 }}>
        <div class="profile-content">
          <div class="row">
            <div class="col-md-12">
              <div class="portlet light ">
                <div class="portlet-body">
                  {" "}
                  <h3> Project </h3>
                  <hr></hr>{" "}
                  <div class="tab-content">
                    <div class="tab-pane active" id="tab_1_1">
                      <form onSubmit={handleSubmit(onSubmit)}>
                        {" "}
                        <div class="form-group">
                          <label class="control-label">Client</label>
                          <input
                            name="company_name"
                            id="company_name"
                            type="text"
                            placeholder="Client"
                            class="form-control"
                            ref={register}
                            required
                          />{" "}
                        </div>
                        <div class="form-group">
                          <label class="control-label">Project Title</label>
                          <input
                            name="Title"
                            id="Title"
                            type="text"
                            placeholder="Project Title"
                            class="form-control"
                            ref={register}
                            required
                          />{" "}
                        </div>
                        <div class="form-group">
                          <label class="control-label">
                            Project Description
                          </label>
                          <textarea
                            name="Description"
                            id="Description"
                            type="textarea"
                            placeholder="Description"
                            class="form-control"
                            ref={register}
                            required
                          />{" "}
                        </div>{" "}
                        <div class="form-group">
                          <label>Income</label>
                          <div class="form-group">
                            <input
                              name="income"
                              class="form-control"
                              id="income"
                              type="text"
                              ref={register}
                              required
                            />
                            <span class="help-block">
                              {" "}
                              123456 => â‚¬ ___.__1.234,56{" "}
                            </span>
                          </div>
                        </div>{" "}
                        <div class="form-group">
                          <label class="control-label">type</label>
                          <input
                            name="type"
                            id="type"
                            type="text"
                            placeholder="type"
                            class="form-control"
                            ref={register}
                            required
                          />{" "}
                        </div>
                        <div class="form-group">
                          Starting date :
                          <DatePicker
                            selected={startDate}
                            onChange={(date) => setStartDate(date)}
                            required
                          />{" "}
                        </div>{" "}
                        <div class="form-group">
                          Expected delivery date:
                          <DatePicker
                            selected={EndDate}
                            onChange={(date) => setEndDate(date)}
                            required
                          />{" "}
                        </div>{" "}
                        <div>
                          {" "}
                          <Form.Group controlId="formFile" className="mb-3">
                            <Form.Control
                              name="File"
                              type="file"
                              onChange={uploadFileToClient}
                              required
                            />
                          </Form.Group>
                        </div>
                        <br />
                        <br />
                        <div class="margiv-top-10">
                          <button
                            type="submit"
                            class="btn green"
                            disabled={isSubmitting}
                          >
                            {" "}
                            Save Changes{" "}
                          </button>
                        </div>
                      </form>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>{" "}
    </div>
  );
}
