import useSWR, { mutate } from "swr";

import gql from "graphql-tag";
import { format, parseISO } from "date-fns";
import { hasuraUserClient } from "../../lib/hasura-user-client";
import Link from "next/link";
import Spinner from "react-spinner-material";
import { useAuthState, useAuthDispatch } from "../../context/auth";
import { useEffect } from "react";

const today = new Date();

const GetUsers = gql`
  query MyQuery {
    leave {
      id
      reason
      start_date
      end_date
      count_d
      status
      user_id
      User {
        Name
        Last_Name
        email
        Avatar
      }
    }
  }
`;
export const getStaticProps = async () => {
  const hasura = hasuraUserClient();

  const initialData = await hasura.request(GetUsers);

  return {
    props: {
      initialData,
    },
    revalidate: 1,
  };
};

export default function MembersPage({ initialData }) {
  const hasura = hasuraUserClient();
  const { isAuthenticated, user } = useAuthState();
  useEffect(() => {
    if (!isAuthenticated) router.push("/");
  }, [isAuthenticated]);

  const { data } = useSWR(GetUsers, (query) => hasura.request(query), {
    initialData,
    revalidateOnMount: true,
  });
  const deleteATodo = async (id) => {
    const query = gql`
      mutation delete_User($id: Int!) {
        delete_User(where: { id: { _eq: $id } }) {
          affected_rows
        }
      }
    `;

    try {
      await hasura.request(query, { id });
      mutate();
      window.location.reload();
    } catch (error) {
      console.error(error);
    }
  };
  const query = gql`
    mutation MyMutation($id: Int = 10) {
      update_leave_by_pk(pk_columns: { id: $id }, _set: { status: 1 }) {
        status
        user_id
        start_date
        reason
        id
      }
    }
  `;
  const AddAcceptNotification = gql`
    mutation MyMutation($user_id: Int!) {
      insert_notification_one(
        object: {
          name: "Approve"
          seen: false
          description: "Leave Approved"
          user_id: $user_id
        }
      ) {
        seen
        name
        id
        description
        user_id
      }
    }
  `;
  const AddRejectedNotification = gql`
    mutation MyMutation($user_id: Int!) {
      insert_notification_one(
        object: {
          name: "Reject "
          seen: false
          description: "your demand was reject, please contact us for more"
          user_id: $user_id
        }
      ) {
        seen
        name
        id
        description
        user_id
      }
    }
  `;

  const Approve = async (id, user_id) => {
    if (confirm(`are you sure you want to approve this request ? `)) {
      try {
        await hasura.request(query, { id });
        mutate();
        window.location.reload();
      } catch (error) {
        console.error(error);
      }
    } else {
      return null;
    }
  };
  const Reject = async (id, user_id) => {
    const query = gql`
      mutation MyMutation($id: Int = 10) {
        update_leave_by_pk(
          pk_columns: { id: $id }
          _set: { status: 2, count_d: 0 }
        ) {
          status
          user_id
          start_date
          reason
          id
        }
      }
    `;
    let t = prompt(`are you sure you want to decline this request ? `, <p></p>);

    if (t) {
      try {
        await hasura.request(query, { id });
        mutate();
        const { insert_notification_one } = await hasura.request(
          AddRejectedNotification,
          { user_id }
        );
        window.location.reload();
      } catch (error) {
        console.error(error);
      }
    } else {
      return null;
    }
  };
  return (
    <div className="page-content-wrapper">
      <div className="page-content" style={{ minHeight: 1500 }}>
        <div class="row">
          {" "}
          <div class="col-lg-6 col-xs-12 col-sm-12">
            <div class="portlet light bordered">
              <div class="portlet-title tabbable-line">
                <div class="caption">
                  <i class=" icon-social-twitter font-dark hide"></i>
                  <span class="caption-subject font-dark bold uppercase">
                    Quick Actions
                  </span>
                </div>
                <ul class="nav nav-tabs">
                  <li class="active">
                    <a href="#tab_actions_pending" data-toggle="tab">
                      {" "}
                      Pending{" "}
                    </a>
                  </li>
                </ul>
              </div>
              <div class="portlet-body">
                <div class="tab-content">
                  <div class="tab-pane active" id="tab_actions_pending">
                    {" "}
                    {data.leave.map(
                      ({
                        id,
                        reason,
                        start_date,
                        end_date,
                        count_d,
                        status,
                        User,
                        user_id,
                      }) => {
                        const formattedJoinedAt = format(
                          parseISO(start_date),
                          "dd-MM"
                        );
                        const formattedend = format(
                          parseISO(end_date),
                          "dd-MM"
                        );
                        return (
                          <div class="mt-actions">
                            {status === 0 ? (
                              <div class="mt-action">
                                <div class="mt-action-img">
                                  <img
                                    class="image_inner_containerHeader"
                                    src={`../../${User.Avatar}`}
                                  />{" "}
                                </div>
                                <div class="mt-action-body">
                                  <div class="mt-action-row">
                                    <div class="mt-action-info ">
                                      <div class="mt-action-icon ">
                                        <i class="icon-action-redo"></i>
                                      </div>
                                      <div class="mt-action-details ">
                                        <span class="mt-action-author">
                                          {User.Name} {User.Last_Name}
                                        </span>
                                        <p class="mt-action-desc">
                                          Leaving request
                                        </p>
                                      </div>
                                    </div>
                                    <div class="mt-action-datetime ">
                                      <span class="mt-action-date">
                                        from {formattedJoinedAt} to{" "}
                                        {formattedend}
                                      </span>
                                      <span class="mt-action-dot bg-green"></span>
                                      <span class="mt=action-time">
                                        {count_d} days
                                      </span>
                                    </div>
                                    <div class="mt-action-buttons ">
                                      <div class="btn-group btn-group-circle">
                                        <button
                                          style={{ fontSize: 10 }}
                                          type="button"
                                          class="btn btn-outline green btn-sm"
                                          onClick={() => Approve(id, user_id)}
                                        >
                                          Appove
                                        </button>{" "}
                                        <button
                                          style={{ fontSize: 10 }}
                                          type="button"
                                          class="btn btn-outline red btn-sm"
                                          onClick={() => Reject(id, user_id)}
                                        >
                                          Reject
                                        </button>
                                      </div>
                                    </div>
                                  </div>
                                </div>
                              </div>
                            ) : (
                              <div></div>
                            )}
                          </div>
                        );
                      }
                    )}
                  </div>
                </div>
              </div>
            </div>
          </div>{" "}
          <div class="col-lg-6 col-xs-12 col-sm-12">
            <div class="portlet light bordered">
              <div class="portlet-title tabbable-line">
                <div class="caption">
                  <i class=" icon-social-twitter font-dark hide"></i>
                  <span class="caption-subject font-dark bold uppercase">
                    Quick Actions
                  </span>
                </div>
                <ul class="nav nav-tabs">
                  <li class="active">
                    <a href="#tab_actions_pending" data-toggle="tab">
                      {" "}
                      Checked{" "}
                    </a>
                  </li>
                </ul>
              </div>
              <div class="portlet-body">
                <div class="tab-content">
                  <div class="tab-pane active" id="tab_actions_pending">
                    {data.leave.map(
                      ({
                        id,
                        reason,
                        start_date,
                        end_date,
                        count_d,
                        status,
                        User,
                      }) => {
                        const formattedJoinedAt = format(
                          parseISO(start_date),
                          "dd-MM"
                        );
                        const formattedend = format(
                          parseISO(end_date),
                          "dd-MM"
                        );
                        return (
                          <div class="mt-actions">
                            <div class="mt-action">
                              <div class="mt-action-img">
                                <img
                                  class="image_inner_containerHeader"
                                  src={`../../${User.Avatar}`}
                                />{" "}
                              </div>
                              <div class="mt-action-body">
                                <div class="mt-action-row">
                                  <div class="mt-action-info ">
                                    <div class="mt-action-icon ">
                                      <i class="icon-action-redo"></i>
                                    </div>
                                    <div class="mt-action-details ">
                                      <span class="mt-action-author">
                                        {User.Name} {User.Last_Name}
                                      </span>
                                      <p class="mt-action-desc">
                                        Leaving request
                                      </p>
                                    </div>
                                  </div>{" "}
                                  <div class="mt-action-datetime ">
                                    <span class="mt-action-date">
                                      from {formattedJoinedAt} to {formattedend}
                                    </span>
                                    <span class="mt-action-dot bg-green"></span>
                                    <span class="mt=action-time">
                                      {count_d} days
                                    </span>
                                  </div>
                                  <div class="mt-action-buttons ">
                                    <div class="btn-group btn-group-circle">
                                      {" "}
                                      {status === 0 ? (
                                        <span class="label label-sm label-info">
                                          {" "}
                                          pending{" "}
                                        </span>
                                      ) : status === 1 ? (
                                        <span class="label label-sm label-success">
                                          {" "}
                                          Approved{" "}
                                        </span>
                                      ) : (
                                        <span class="label label-sm label-danger">
                                          {" "}
                                          Rejected{" "}
                                        </span>
                                      )}
                                    </div>
                                  </div>
                                </div>
                              </div>
                            </div>
                          </div>
                        );
                      }
                    )}
                  </div>
                </div>
              </div>
            </div>{" "}
          </div>
          <div class="col-md-12">
            <div class="portlet light bordered">
              <div class="portlet-title">
                <div class="caption font-dark">
                  <i class="icon-settings font-dark"></i>
                  <span class="caption-subject bold uppercase">
                    {" "}
                    Leaving Requests
                  </span>
                </div>
              </div>
              <div class="portlet-body">
                <div class="table-toolbar">
                  <div class="row">
                    <div class="col-md-6">
                      {" "}
                      <div class="actions">
                        <div class="btn-group">
                          <a
                            class="btn red btn-outline btn-circle"
                            href="javascript:;"
                            data-toggle="dropdown"
                          >
                            <i class="fa fa-share"></i>
                            <span class="hidden-xs"> Tools </span>
                            <i class="fa fa-angle-down"></i>
                          </a>
                          <ul
                            class="dropdown-menu pull-right"
                            id="sample_3_tools"
                          >
                            <li>
                              <a
                                href="javascript:;"
                                data-action="0"
                                class="tool-action"
                              >
                                <i class="icon-printer"></i> Print
                              </a>
                            </li>
                            <li>
                              <a
                                href="javascript:;"
                                data-action="1"
                                class="tool-action"
                              >
                                <i class="icon-check"></i> Copy
                              </a>
                            </li>
                            <li>
                              <a
                                href="javascript:;"
                                data-action="2"
                                class="tool-action"
                              >
                                <i class="icon-doc"></i> PDF
                              </a>
                            </li>
                            <li>
                              <a
                                href="javascript:;"
                                data-action="3"
                                class="tool-action"
                              >
                                <i class="icon-paper-clip"></i> Excel
                              </a>
                            </li>
                            <li>
                              <a
                                href="javascript:;"
                                data-action="4"
                                class="tool-action"
                              >
                                <i class="icon-cloud-upload"></i> CSV
                              </a>
                            </li>
                            <li class="divider"> </li>
                            <li>
                              <a
                                href="javascript:;"
                                data-action="5"
                                class="tool-action"
                              >
                                <i class="icon-refresh"></i> Reload
                              </a>
                            </li>
                          </ul>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
                <div class="portlet-body">
                  <div class="table-container">
                    <table
                      class="table table-striped table-bordered table-hover"
                      id="sample_3"
                    >
                      <thead>
                        <tr>
                          <th> Reason </th>
                          <th> User </th>
                          <th> Email </th>
                          <th> Status </th>
                          <th> created at </th>
                        </tr>
                      </thead>
                      <tfoot>
                        <tr>
                          <th> </th>
                          <th> </th>
                          <th> </th>
                          <th> </th>
                        </tr>
                      </tfoot>
                      <tbody>
                        {data.leave.map(
                          ({
                            id,
                            reason,
                            start_date,
                            end_date,
                            count_d,
                            status,
                            User,
                          }) => {
                            const formattedJoinedAt = format(
                              parseISO(start_date),
                              "MM/dd/yyyy"
                            );
                            return (
                              <tr key={id} class="odd gradeX">
                                <td>{reason}</td>
                                <td> {User.Name}</td>
                                <td> {User.email}</td>
                                {/* <div class="mt-action-buttons ">
                                  <div class="btn-group btn-group-circle">
                                    {" "}
                                    {status === 0 ? (
                                      <span class="label label-sm label-info">
                                        {" "}
                                        pending{" "}
                                      </span>
                                    ) : status === 1 ? (
                                      <span class="label label-sm label-success">
                                        {" "}
                                        Approved{" "}
                                      </span>
                                    ) : (
                                      <span class="label label-sm label-danger">
                                        {" "}
                                        Rejected{" "}
                                      </span>
                                    )}
                                  </div>
                                </div>{" "} */}
                                <td class="center">{formattedJoinedAt}</td>
                              </tr>
                            );
                          }
                        )}
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
