import useSWR, { mutate } from "swr";
import { useEffect } from "react";
import { useRouter } from "next/router";
import { useForm } from "react-hook-form";
import React, { useState } from "react";
import { Form } from "react-bootstrap";
import Link from "next/link";
import gql from "graphql-tag";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { format, intervalToDuration, parseISO } from "date-fns";
import FullCalendar from "@fullcalendar/react"; // must go before plugins
import dayGridPlugin from "@fullcalendar/daygrid";
import "@fullcalendar/common/main.css";
import "@fullcalendar/daygrid/main.css";
import "@fullcalendar/timegrid/main.css";
import "@fullcalendar/list/main.css";
import { TextField } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";

import resourceTimelinePlugin from "@fullcalendar/resource-timeline";
import interactionPlugin from "@fullcalendar/interaction";
import timeGridPlugin from "@fullcalendar/timegrid";
import listPlugin from "@fullcalendar/list";
import { hasuraUserClient } from "../../lib/hasura-user-client";
const today = new Date();
const useStyles = makeStyles((theme) => ({
  container: {
    display: "flex",
    flexWrap: "wrap",
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 200,
  },
}));
const GetEvents = gql`
  query MyQuery {
    Event {
      id
      Title
      description
      start_date
    }
  }
`;
const insertEvent = gql`
  mutation MyMutation(
    $Title: String!
    $description: String!
    $start_date: timestamp!
  ) {
    insert_Event_one(
      object: {
        Title: $Title
        description: $description
        start_date: $start_date
      }
    ) {
      Title
      description
      start_date
    }
  }
`;
export const getStaticProps = async () => {
  const hasura = hasuraUserClient();
  const initialData = await hasura.request(GetEvents);

  return {
    props: {
      initialData,
    },
    revalidate: 1,
  };
};

export default function Projects({ initialData }) {
  const classes = useStyles();
  const formattedJoinedAt = format(parseISO(today), "yyyy-MM-dd-h:mm");

  const [startDate, setStartDate] = useState(new Date());
  const [EndDate, setEndDate] = useState(new Date());
  const [File, setFile] = useState(null);
  const [createObjectURL, setCreateObjectURL] = useState(null);
  const {
    handleSubmit,
    register,
    control,
    errors,
    formState: { isSubmitting },
  } = useForm();
  const hasura = hasuraUserClient();
  const router = useRouter();
  const { data } = useSWR(GetEvents, (query) => hasura.request(query), {
    initialData,
    revalidateOnMount: true,
  });
  const onSubmit = async ({ Title, datetime, description }) => {
    console.log(datetime);
    const start_date = datetime;
    try {
      const { insert_Event_one } = await hasura.request(insertEvent, {
        Title,
        description,
        start_date,
      });

      router.push(`/Calendar`);
    } catch (err) {
      console.log(err);
    }
  };
  return (
    <div className="page-content-wrapper">
      <div className="page-content" style={{ minHeight: 1500 }}>
        <h1 class="page-title">
          {" "}
          Calendar
          <small>calendar page</small>
        </h1>

        <div class="row">
          <div class="col-md-12">
            <div class="portlet light portlet-fit bordered calendar">
              <div class="portlet-title">
                <div class="caption">
                  <i class=" icon-layers font-green"></i>
                  <span class="caption-subject font-green sbold uppercase">
                    Calendar
                  </span>
                </div>
              </div>
              <div class="portlet-body">
                <div class="row">
                  <div class="col-md-3 col-sm-12">
                    <h3 class="event-form-title margin-bottom-20">Event</h3>
                    <div id="external-events">
                      <form onSubmit={handleSubmit(onSubmit)}>
                        {" "}
                        <div class="form-group">
                          <input
                            type="text"
                            name="Title"
                            class="form-control"
                            placeholder="Event Type..."
                            id="event_title"
                            ref={register}
                          />{" "}
                        </div>{" "}
                        <div class="form-group">
                          <input
                            type="text"
                            name="description"
                            class="form-control"
                            placeholder="Description"
                            id="event_title"
                            ref={register}
                          />{" "}
                        </div>
                        <div class="form-group">
                          <TextField
                            id="datetime"
                            label="Next appointment"
                            name="datetime"
                            type="datetime-local"
                            defaultValue={today}
                            inputRef={register}
                            className={classes.textField}
                          />
                        </div>
                        <div class="form-group">
                          <a
                            type="submit"
                            class="btn green"
                            disabled={isSubmitting}
                          >
                            {" "}
                            Add Event{" "}
                          </a>
                        </div>
                      </form>
                      <hr />
                      <div id="event_box" class="margin-bottom-10"></div>
                      <label
                        class="mt-checkbox mt-checkbox-single mt-checkbox-outline"
                        for="drop-remove"
                      >
                        {" "}
                        remove after drop
                        <input
                          type="checkbox"
                          class="group-checkable"
                          id="drop-remove"
                        />
                        <span></span>
                      </label>
                      <hr class="visible-xs" />{" "}
                    </div>
                  </div>
                  <div class="col-md-9 col-sm-12">
                    <FullCalendar
                      plugins={[
                        dayGridPlugin,
                        timeGridPlugin,
                        interactionPlugin,
                        listPlugin,
                      ]}
                      headerToolbar={{
                        left: "prev,next today",
                        center: "title",
                        right: "dayGridMonth,timeGridWeek,timeGridDay,listWeek",
                      }}
                      initialView="dayGridMonth"
                      editable={true}
                      selectable={true}
                      selectMirror={true}
                      dayMaxEvents={true}
                      events={data.Event.map(({ id, Title, start_date }) => {
                        return { title: Title, date: start_date };
                      })}
                      eventColor="#378006"

                      // weekends={this.state.weekendsVisible}
                      // initialEvents={INITIAL_EVENTS} // alternatively, use the `events` setting to fetch from a feed
                      // select={this.handleDateSelect}
                      // eventContent={renderEventContent} // custom render function
                      // eventClick={this.handleEventClick}
                      // eventsSet={this.handleEvents} // called after events are initialized/added/changed/removed
                      /* you can update a remote database when these fire:
                eventAdd={function(){}}
                eventChange={function(){}}
                eventRemove={function(){}}
                */
                    />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
