import React from "react";
import Link from "next/link";
import { useAuthState, useAuthDispatch } from "../context/auth";
import Image from "react-bootstrap/Image";
import useSWR, { mutate } from "swr";

import gql from "graphql-tag";
import { useRouter } from "next/router";

import { hasuraUserClient } from "../lib/hasura-user-client";

const Notification_count = gql`
  query MyQuery {
    notification_aggregate {
      aggregate {
        count(columns: id)
      }
    }
  }
`;
export const getStaticProps = async () => {
  const hasura = hasuraUserClient();

  const initialData = await hasura.request(Notification_count);
  const c = initialData.notification_aggregate.aggregate.count;

  return {
    props: {
      initialData,
      c,
    },
    revalidate: 1,
  };
};

export default function Notification({ initialData, c }) {
  const { logout } = useAuthDispatch();
  const { isAuthenticated, user } = useAuthState();
  const hasura = hasuraUserClient();

  console.log(c);
  return <span class="badge badge-default">7</span>;
}
