import { useState } from "react";
import { useRouter } from "next/router";
import { hasuraUserClient } from "../../lib/hasura-user-client";
import { hasuraAdminClient, gql } from "../../lib/hasura-admin-client";
import useSWR, { mutate } from "swr";
import { useAuthState, useAuthDispatch } from "../../context/auth";

const GetCategoryIds = gql`
  {
    User {
      id
      Name
      Avatar
    }
  }
`;

const GetCategoryById = gql`
  query GetCategoryById($id: Int!) {
    User_by_pk(id: $id) {
      id
      Name
      Avatar
    }
  }
`;
export const getStaticPaths = async () => {
  const hasura = hasuraUserClient();

  const { User } = await hasura.request(GetCategoryIds);

  return {
    paths: User.map(({ id }) => ({
      params: {
        id: id.toString(),
      },
    })),
    fallback: false,
  };
};

export const getStaticProps = async ({ params }) => {
  const hasura = hasuraUserClient();
  const { id } = params;
  console.log(params);
  const initialData = await hasura.request(GetCategoryById, { id });
  console.log(initialData.User_by_pk.Avatar);

  return {
    props: {
      initialData,
    },
    revalidate: 1,
  };
};
export default function PrivatePage(initialData) {
  const { isAuthenticated, user } = useAuthState();
  const [image, setImage] = useState(null);
  const [usert, setUser] = useState(null);
  const [createObjectURL, setCreateObjectURL] = useState(null);
  const router = useRouter();
  const { id } = router.query;

  const uploadToClient = (event) => {
    if (event.target.files && event.target.files[0]) {
      const i = event.target.files[0];
      setImage(i);
      setCreateObjectURL(URL.createObjectURL(i));
    }
  };

  const uploadToServer = async (event) => {
    const hasura = hasuraUserClient();
    const body = new FormData();
    body.append("file", image);
    const response = await fetch("/api/file/", {
      method: "POST",
      body,
    });
    console.log(image.name);
    const Avatar = image.name;
    const UploadImage = gql`
      mutation MyMutation($Avatar: String!, $id: Int!) {
        update_User_by_pk(pk_columns: { id: $id }, _set: { Avatar: $Avatar }) {
          Avatar
          Name
        }
      }
    `;
    try {
      await hasura.request(UploadImage, { id, Avatar });
      mutate();
      window.location.reload();
    } catch (error) {
      console.error(error);
    }
    return;
  };
  const { data, mutate } = useSWR(
    [GetCategoryById, id],
    (query, id) => hasura.request(query, { id }),
    {
      initialData,
      revalidateOnMount: true,
    }
  );
  return (
    <div className="page-content-wrapper">
      <div className="page-content" style={{ minHeight: 1500 }}>
        <div className="row">
          <div className="col-md-12">
            <div className="profile-sidebar">
              <div className="portlet light profile-sidebar-portlet ">
                <div className="profile-userpic">
                  <img
                    src={`../${data.User_by_pk.Avatar}`}
                    className="img-responsive"
                    alt=""
                  />{" "}
                </div>
                <div className="portlet light ">
                  <div className="row list-separated profile-stat">
                    <div className="col-md-4 col-sm-4 col-xs-6">
                      <div className="uppercase profile-stat-title"> 37 </div>
                      <div className="uppercase profile-stat-text">
                        {" "}
                        Projects{" "}
                      </div>
                    </div>
                    <div className="col-md-4 col-sm-4 col-xs-6">
                      <div className="uppercase profile-stat-title"> 51 </div>
                      <div className="uppercase profile-stat-text"> Tasks </div>
                    </div>
                    <div className="col-md-4 col-sm-4 col-xs-6">
                      <div className="uppercase profile-stat-title"> 61 </div>
                      <div className="uppercase profile-stat-text">
                        {" "}
                        Uploads{" "}
                      </div>
                    </div>
                  </div>
                  <div>
                    <h4 className="profile-desc-title">About Marcus Doe</h4>
                    <span className="profile-desc-text">
                      {" "}
                      Lorem ipsum dolor sit amet diam nonummy nibh dolore.{" "}
                    </span>
                    <div className="margin-top-20 profile-desc-link">
                      <i className="fa fa-globe"></i>
                      <a href="http://www.keenthemes.com">www.keenthemes.com</a>
                    </div>
                    <div className="margin-top-20 profile-desc-link">
                      <i className="fa fa-twitter"></i>
                      <a href="http://www.twitter.com/keenthemes/">
                        @keenthemes
                      </a>
                    </div>
                    <div className="margin-top-20 profile-desc-link">
                      <i className="fa fa-facebook"></i>
                      <a href="http://www.facebook.com/keenthemes/">
                        keenthemes
                      </a>
                    </div>
                  </div>
                </div>
                <div className="profile-usertitle">
                  <div className="profile-usertitle-name"> Marcus Doe </div>
                  <div className="profile-usertitle-job"> Developer </div>
                </div>

                <div className="profile-userbuttons">
                  <button type="button" className="btn btn-circle green btn-sm">
                    Follow
                  </button>
                  <button type="button" className="btn btn-circle red btn-sm">
                    Message
                  </button>
                </div>

                <div className="profile-usermenu">
                  <ul className="nav">
                    <li>
                      <a href="page_user_profile_1.html">
                        <i className="icon-home"></i> Overview{" "}
                      </a>
                    </li>
                    <li className="active">
                      <a href="page_user_profile_1_account.html">
                        <i className="icon-settings"></i> Account Settings{" "}
                      </a>
                    </li>
                    <li>
                      <a href="page_user_profile_1_help.html">
                        <i className="icon-info"></i> Help{" "}
                      </a>
                    </li>
                  </ul>
                </div>
              </div>
            </div>
            <div className="profile-content">
              {" "}
              <div className="row">
                <div className="col-md-12">
                  <div className="portlet light ">
                    <div className="portlet-title tabbable-line">
                      <div className="caption caption-md">
                        <i className="icon-globe theme-font hide"></i>
                        <span className="caption-subject font-blue-madison bold uppercase">
                          Profile Account
                        </span>
                      </div>
                      <ul className="nav nav-tabs">
                        <li className="active">
                          <a href="#tab_1_1" data-toggle="tab">
                            Personal Info
                          </a>
                        </li>
                        <li>
                          <a href="#tab_1_2" data-toggle="tab">
                            Change Avatar
                          </a>
                        </li>
                        <li>
                          <a href="#tab_1_3" data-toggle="tab">
                            Change Password
                          </a>
                        </li>
                        <li>
                          <a href="#tab_1_4" data-toggle="tab">
                            Privacy Settings
                          </a>
                        </li>
                      </ul>
                    </div>
                    <div className="portlet-body">
                      <div className="tab-content">
                        <div className="tab-pane active" id="tab_1_1">
                          <form role="form" action="#">
                            <div className="form-group">
                              <label className="control-label">
                                First Name
                              </label>
                              <input
                                type="text"
                                placeholder="John"
                                className="form-control"
                              />{" "}
                            </div>
                            <div className="form-group">
                              <label className="control-label">Last Name</label>
                              <input
                                type="text"
                                placeholder="Doe"
                                className="form-control"
                              />{" "}
                            </div>
                            <div className="form-group">
                              <label className="control-label">
                                Mobile Number
                              </label>
                              <input
                                type="text"
                                placeholder="+1 646 580 DEMO (6284)"
                                className="form-control"
                              />{" "}
