import { useRouter } from "next/router";
import useSWR from "swr";
import { useForm, Controller } from "react-hook-form";

import { gql, hasuraUserClient } from "../../lib/hasura-user-client";
import { useEffect } from "react";
const getProjectsIds = gql`
  {
    Project {
      id
      Title
      Description
    }
  }
`;

const GetProjectByID = gql`
  query GetProjectByID($id: Int!) {
    Project_by_pk(id: $id) {
      id
      Title
      Description
    }
  }
`;
export const getStaticPaths = async () => {
  const hasura = hasuraUserClient();

  const { Project } = await hasura.request(getProjectsIds);

  return {
    paths: Project.map(({ id }) => ({
      params: {
        id: id.toString(),
      },
    })),
    fallback: false,
  };
};

export const getStaticProps = async ({ params }) => {
  const hasura = hasuraUserClient();
  const { id } = params;

  const initialData = await hasura.request(GetProjectByID, { id });

  return {
    props: {
      initialData,
    },
    revalidate: 1,
  };
};
const UpdateProject = gql`
  mutation update_Candidate_by_pk(
    $id: Int!
    $Title: String!
    $Description: String!
  ) {
    update_Project_by_pk(
      pk_columns: { id: $id }
      _set: { Title: $Title, Description: $Description }
    ) {
      Title
      Description
    }
  }
`;
export default function CandidateEdit({ initialData }) {
  const hasura = hasuraUserClient();
  const { query } = useRouter();
  const router = useRouter();

  const { id } = query;
  const {
    handleSubmit,
    register,
    control,
    errors,
    formState: { isSubmitting },
    defaultValues: { ...defaultValues },
  } = useForm();
  const { data, mutate } = useSWR(
    [GetProjectByID, id],
    (query, id) => hasura.request(query, { id }),
    {
      initialData,
      revalidateOnMount: true,
    }
  );

  const onSubmit = async ({ Title, Description }) => {
    try {
      const id = data.Project_by_pk.id;
      console.log(id);
      const { update_Project_by_pk } = await hasura.request(UpdateProject, {
        id,
        Title,
        Description,
      });

      mutate({
        ...data,
        ...update_Project_by_pk,
      });
      router.push(`/Project/List`);
    } catch (err) {
      console.log(err);
    }
  };
  return (
    <div className="page-content-wrapper">
      <div className="page-content" style={{ minHeight: 1500 }}>
        <div class="profile-content">
          <div class="row">
            <div class="col-md-12">
              <div class="portlet light ">
                <div class="portlet-body">
                  <div class="tab-content">
                    <div class="tab-pane active" id="tab_1_1">
                      <form onSubmit={handleSubmit(onSubmit)}>
                        <div class="form-group">
                          <label class="control-label">Title</label>
                          <input
                            defaultValue={data.Project_by_pk.Title}
                            name="Name"
                            id="Name"
                            type="text"
                            class="form-control"
                            ref={register}
                          />{" "}
                        </div>
                        <div class="form-group">
                          <label class="control-label">Description</label>
                          <input
                            defaultValue={data.Candidate_by_pk.Last_Name}
                            name="Last_Name"
                            id="Last_Name"
                            type="text"
                            placeholder="Last Name"
                            class="form-control"
                            ref={register({
                              required:
                                "You must select a category for your thread",
                            })}
                          />{" "}
                        </div>
                        <div class="form-group">
                          <label class="control-label">Mobile Number</label>
                          <input
                            defaultValue={data.Candidate_by_pk.phone_number}
                            name="phone_number"
                            id="phone_number"
                            type="text"
                            placeholder="+1 646 580 DEMO (6284)"
                            class="form-control"
                            ref={register({
                              required:
                                "You must select a category for your thread",
                            })}
                          />{" "}
                        </div>
                        <div class="form-group">
                          <label class="control-label">Email</label>
                          <input
                            name="Email"
                            id="Email"
                            type="email"
                            defaultValue={data.Candidate_by_pk.Email}
                            class="form-control"
                            ref={register({
                              required:
                                "You must select a category for your thread",
                            })}
                          />{" "}
                        </div>
                        <div class="form-group">
                          <label class="control-label">Domain</label>
                          <input
                            defaultValue={data.Candidate_by_pk.domain}
                            name="domain"
                            id="domain"
                            type="text"
                            placeholder="Web Developer"
                            class="form-control"
                            ref={register({
                              required:
                                "You must select a category for your thread",
                            })}
                          />{" "}
                        </div>
                        <div class="margiv-top-10">
                          <button
                            type="submit"
                            class="btn green"
                            disabled={isSubmitting}
                          >
                            {" "}
                            Save Changes{" "}
                          </button>
                          <a href="#" class="btn default">
                            {" "}
                            Cancel{" "}
                          </a>
                        </div>
                      </form>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>{" "}
    </div>
  );
}
