import { useState, useEffect } from "react";
import { useRouter } from "next/router";
import { useForm, Controller } from "react-hook-form";
import ReactMde from "react-mde";
import Markdown from "react-markdown";
import Layout from "./Components/Layout";

import { useAuthState } from "../context/auth";
import { gql, hasuraAdminClient } from "../lib/hasura-admin-client";
import { hasuraUserClient } from "../lib/hasura-user-client";

const GetUsers = gql`
  {
    User {
      Name
      id
    }
  }
`;

const InsertTask = gql`
  mutation InsertTask($categoryId: uuid!, $title: String!, $message: String!) {
    insert_task_one(
      object: {
        task_id: $categoryId
        name: $title
        posts: { data: { message: $message } }
      }
    ) {
      id
      title
      author {
        name
      }
      category {
        name
      }
      posts {
        message
      }
      created_at
    }
  }
`;

export const getStaticProps = async () => {
  const { User } = await hasuraAdminClient.request(GetUsers);

  return {
    props: {
      User,
    },
  };
};

export default function Candidat({ User }) {
  const router = useRouter();
  const { isAuthenticated, user } = useAuthState();
  const hasura = hasuraUserClient();
  const {
    handleSubmit,
    register,
    control,
    errors,
    formState: { isSubmitting },
  } = useForm();
  const [selectedTab, setSelectedTab] = useState("write");

  useEffect(() => {
    if (!isAuthenticated) router.push("/");
  }, [isAuthenticated]);

  if (!isAuthenticated) return null;

  const onSubmit = async ({ userId, title, message }) => {
    try {
      const { insert_task_one } = await hasura.request(InsertTask, {
        categoryId,
        title,
        message,
      });

      router.push(`/task/${insert_task_one.id}`);
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <>
      <Layout>
        <div className="page-content-wrapper">
          <div className="page-content" style={{ minHeight: 1500 }}>
            <div class="profile-content">
              <div class="row">
                <div class="col-md-12">
                  <div class="portlet light ">
                    <div class="portlet-body">
                      <div class="tab-content">
                        <div class="tab-pane active" id="tab_1_1">
                          <form role="form" action="#">
                            <div class="form-group">
                              <label class="control-label">First Name</label>
                              <input
                                type="text"
                                placeholder="John"
                                class="form-control"
                              />{" "}
                            </div>
                            <div class="form-group">
                              <label class="control-label">Last Name</label>
                              <input
                                type="text"
                                placeholder="Doe"
                                class="form-control"
                              />{" "}
                            </div>
                            <div class="form-group">
                              <label class="control-label">Mobile Number</label>
                              <input
                                type="text"
                                placeholder="+1 646 580 DEMO (6284)"
                                class="form-control"
                              />{" "}
                            </div>
                            <div class="form-group">
                              <label class="control-label">Interests</label>
                              <input
                                type="text"
                                placeholder="Design, Web etc."
                                class="form-control"
                              />{" "}
                            </div>
                            <div class="form-group">
                              <label class="control-label">Occupation</label>
                              <input
                                type="text"
                                placeholder="Web Developer"
                                class="form-control"
                              />{" "}
                            </div>
                            <div class="form-group">
                              <label class="control-label">About</label>
                              <textarea
                                class="form-control"
                                rows="3"
                                placeholder="We are KeenThemes!!!"
                              ></textarea>
                            </div>
                            <div class="form-group">
                              <label class="control-label">Website Url</label>
                              <input
                                type="text"
                                placeholder="http://www.mywebsite.com"
                                class="form-control"
                              />{" "}
                            </div>
                            <div class="margiv-top-10">
                              <a href="javascript:;" class="btn green">
                                {" "}
                                Save Changes{" "}
                              </a>
                              <a href="javascript:;" class="btn default">
                                {" "}
                                Cancel{" "}
                              </a>
                            </div>
                          </form>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>{" "}
        </div>
      </Layout>
    </>
  );
}
