import useSWR, { mutate } from "swr";

import gql from "graphql-tag";
import { format, parseISO } from "date-fns";
import { hasuraUserClient } from "../../lib/hasura-user-client";
import Link from "next/link";
import Spinner from "react-spinner-material";

const today = new Date();

const GetUsers = gql`
  query MyQuery {
    leave {
      id
      reason
      start_date
      end_date
      count_d
      status
      user_id
      User {
        Name
        Last_Name
        email
        Avatar
      }
    }
  }
`;
export const getStaticProps = async () => {
  const hasura = hasuraUserClient();

  const initialData = await hasura.request(GetUsers);

  return {
    props: {
      initialData,
    },
    revalidate: 1,
  };
};

export default function MembersPage({ initialData }) {
  const hasura = hasuraUserClient();

  const { data } = useSWR(GetUsers, (query) => hasura.request(query), {
    initialData,
    revalidateOnMount: true,
  });
  const deleteATodo = async (id) => {
    const query = gql`
      mutation delete_User($id: Int!) {
        delete_User(where: { id: { _eq: $id } }) {
          affected_rows
        }
      }
    `;

    try {
      await hasura.request(query, { id });
      mutate();
      window.location.reload();
    } catch (error) {
      console.error(error);
    }
  };
  const query = gql`
    mutation MyMutation($id: Int = 10) {
      update_leave_by_pk(pk_columns: { id: $id }, _set: { status: 1 }) {
        status
        user_id
        start_date
        reason
        id
      }
    }
  `;
  const AddAcceptNotification = gql`
    mutation MyMutation($user_id: Int!) {
      insert_notification_one(
        object: {
          name: "Approve"
          seen: false
          description: "Leave Approved"
          user_id: $user_id
        }
      ) {
        seen
        name
        id
        description
        user_id
      }
    }
  `;
  const AddRejectNotif = gql`
    mutation MyMutation {
      insert_notification_one(
        object: {
          name: "Leave"
          seen: false
          description: "new Leave demand is submitted, please verify it"
          user_id: 42
        }
      ) {
        seen
        name
        id
        description
        user_id
      }
    }
  `;

  const Approve = async (id, user_id) => {
    if (confirm(`are you sure you want to approve this request ? `)) {
      try {
        await hasura.request(query, { id });
        mutate();
        window.location.reload();
      } catch (error) {
        console.error(error);
      }
    } else {
      return null;
    }
  };
  const Reject = async (id, user_id) => {
    const query = gql`
      mutation MyMutation($id: Int = 10) {
        update_leave_by_pk(pk_columns: { id: $id }, _set: { status: 2 }) {
          status
          user_id
          start_date
          reason
          id
        }
      }
    `;
    if (confirm(`are you sure you want to decline this request ? `)) {
      try {
        await hasura.request(query, { id });
        mutate();
        const { insert_notification_one } = await hasura.request(
          AddNotification,
          { user_id }
        );
        window.location.reload();
      } catch (error) {
        console.error(error);
      }
    } else {
      return null;
    }
  };
  return (
    <div className="page-content-wrapper">
      <div className="page-content" style={{ minHeight: 1500 }}>
        <div class="row">
          {" "}
          <div class="col-lg-6 col-xs-12 col-sm-12">
            <div class="portlet light bordered">
              <div class="portlet-title tabbable-line">
                <div class="caption">
                  <i class=" icon-social-twitter font-dark hide"></i>
                  <span class="caption-subject font-dark bold uppercase">
                    Quick Actions
                  </span>
                </div>
                <ul class="nav nav-tabs">
                  <li class="active">
                    <a href="#tab_actions_pending" data-toggle="tab">
                      {" "}
                      Pending{" "}
                    </a>
                  </li>
                  <li>
                    <a href="#tab_actions_completed" data-toggle="tab">
                      {" "}
                      Completed{" "}
                    </a>
                  </li>
                </ul>
              </div>
              <div class="portlet-body">
                <div class="tab-content">
                  <div class="tab-pane active" id="tab_actions_pending">
                    {" "}
                    {data.leave.map(
                      ({
                        id,
                        reason,
                        start_date,
                        end_date,
                        count_d,
                        status,
                        User,
                        user_id,
                      }) => {
                        const formattedJoinedAt = format(
                          parseISO(start_date),
                          "dd-MM"
                        );
                        const formattedend = format(
                          parseISO(end_date),
                          "dd-MM"
                        );
                        return (
                          <div class="mt-actions">
                            {status === 0 ? (
                              <div class="mt-action">
                                <div class="mt-action-img">
                                  <img
                                    class="image_inner_containerHeader"
                                    src={`../../${User.Avatar}`}
                                  />{" "}
                                </div>
                                <div class="mt-action-body">
                                  <div class="mt-action-row">
                                    <div class="mt-action-info ">
                                      <div class="mt-action-icon ">
                                        <i class="icon-action-redo"></i>
                                      </div>
                                      <div class="mt-action-details ">
                                        <span class="mt-action-author">
                                          {User.Name} {User.Last_Name}
                                        </span>
                                        <p class="mt-action-desc">
                                          Leaving request
                                        </p>
                                      </div>
                                    </div>
                                    <div class="mt-action-datetime ">
                                      <span class="mt-action-date">
                                        from {formattedJoinedAt} to{" "}
                                        {formattedend}
                                      </span>
                                      <span class="mt-action-dot bg-green"></span>
                                      <span class="mt=action-time">
                                        {count_d} days
                                      </span>
                                    </div>
                                    <div class="mt-action-buttons ">
                                      <div class="btn-group btn-group-circle">
                                        <button
                                          style={{ fontSize: 10 }}
                                          type="button"
                                          class="btn btn-outline green btn-sm"
                                          onClick={() => Approve(id, user_id)}
                                        >
                                          Appove
                                        </button>{" "}
                                        <button
                                          style={{ fontSize: 10 }}
                                          type="button"
                                          class="btn btn-outline red btn-sm"
                                          onClick={() => Reject(id, user_id)}
                                        >
                                          Reject
                                        </button>
                                      </div>
                                    </div>
                                  </div>
                                </div>
                              </div>
                            ) : (
                              <div></div>
                            )}
                          </div>
                        );
                      }
                    )}
                  </div>
                  <div class="tab-pane" id="tab_actions_completed">
                    {data.leave.map(
                      ({
                        id,
                        reason,
                        start_date,
                        end_date,
                        count_d,
                        status,
                        User,
                      }) => {
                        const formattedJoinedAt = format(
                          parseISO(start_date),
                          "dd-MM"
                        );
                        const formattedend = format(
                          parseISO(end_date),
                          "dd-MM"
                        );
                        return (
                          <div class="mt-actions">
                            <div class="mt-action">
                              <div class="mt-action-img"></div>
                              <div class="mt-action-body">
                                <div class="mt-action-row">
                                  <div class="mt-action-info ">
                                    <div class="mt-action-icon ">
                                      <i class="icon-action-redo"></i>
                                    </div>
                                    <div class="mt-action-details ">
                                      <span class="mt-action-author"></span>
                                      <p class="mt-action-desc">
                                        Lorem Ipsum is simply dummy
                                      </p>
                                    </div>
                                  </div>
                                  <div class="mt-action-datetime ">
                                    <span class="mt-action-date">3 jun</span>
                                    <span class="mt-action-dot bg-red"></span>
                                    <span class="mt=action-time">
                                      9:30-13:00
                                    </span>
                                  </div>
                                  <div class="mt-action-buttons ">
                                    <div class="btn-group btn-group-circle">
                                      {" "}
                                      {status === 0 ? (
                                        <button
                                          type="button"
                                          class="btn btn-outline green btn-sm"
                                        >
                                          pending
                                        </button>
                                      ) : status === 1 ? (
                                        <button
                                          type="button"
                                          class="btn btn-outline red btn-sm"
                                        >
                                          approved
                                        </button>
                                      ) : (
                                        <button
                                          type="button"
                                          class="btn btn-outline red btn-sm"
                                        >
                                          rejected
                                        </button>
                                      )}
                                    </div>
                                  </div>
                                </div>
                              </div>
                            </div>
                          </div>
                        );
                      }
                    )}
                  </div>
                </div>
              </div>
            </div>
          </div>
          <div class="col-md-12">
            <div class="portlet light bordered">
              <div class="portlet-title">
                <div class="caption font-dark">
                  <i class="icon-settings font-dark"></i>
                  <span class="caption-subject bold uppercase">
                    {" "}
                    Users (Employees)
                  </span>
                </div>
              </div>
              <div class="portlet-body">
                <div class="table-toolbar">
                  <div class="row">
                    <div class="col-md-6">
                      {" "}
                      <div class="actions">
                        <div class="btn-group">
                          <a
                            class="btn red btn-outline btn-circle"
                            href="javascript:;"
                            data-toggle="dropdown"
                          >
                            <i class="fa fa-share"></i>
                            <span class="hidden-xs"> Tools </span>
                            <i class="fa fa-angle-down"></i>
                          </a>
                          <ul
                            class="dropdown-menu pull-right"
                            id="sample_3_tools"
                          >
                            <li>
                              <a
                                href="javascript:;"
                                data-action="0"
                                class="tool-action"
                              >
                                <i class="icon-printer"></i> Print
                              </a>
                            </li>
                            <li>
                              <a
                                href="javascript:;"
                                data-action="1"
                                class="tool-action"
                              >
                                <i class="icon-check"></i> Copy
                              </a>
                            </li>
                            <li>
                              <a
                                href="javascript:;"
                                data-action="2"
                                class="tool-action"
                              >
                                <i class="icon-doc"></i> PDF
                              </a>
                            </li>
                            <li>
                              <a
                                href="javascript:;"
                                data-action="3"
                                class="tool-action"
                              >
                                <i class="icon-paper-clip"></i> Excel
                              </a>
                            </li>
                            <li>
                              <a
                                href="javascript:;"
                                data-action="4"
                                class="tool-action"
                              >
                                <i class="icon-cloud-upload"></i> CSV
                              </a>
                            </li>
                            <li class="divider"> </li>
                            <li>
                              <a
                                href="javascript:;"
                                data-action="5"
                                class="tool-action"
                              >
                                <i class="icon-refresh"></i> Reload
                              </a>
                            </li>
