import { useEffect } from "react";
import { useRouter } from "next/router";
import { useForm } from "react-hook-form";
import React from "react";
import gql from "graphql-tag";
import { useAuthState, useAuthDispatch } from "../context/auth";
import Layoutlogin from "./Components/layoutlogin";
import { hasuraUserClient } from "../lib/hasura-user-client";

const Notification_count = gql`
  query MyQuery {
    notification_aggregate {
      aggregate {
        count(columns: id)
      }
    }
  }
`;
export const getStaticProps = async () => {
  const hasura = hasuraUserClient();

  const initialData = await hasura.request(Notification_count);
  const c = initialData.notification_aggregate.aggregate.count;

  return {
    props: {
      initialData,
      c,
    },
    revalidate: 1,
  };
};
export default function RegisterPage({ c }) {
  const router = useRouter();
  const { isAuthenticated } = useAuthState();
  const { login } = useAuthDispatch();
  const {
    handleSubmit,
    register,
    errors,
    formState: { isSubmitting },
    setError,
  } = useForm({ defaultValues: { save_last_seen: true } });

  useEffect(() => {
    if (isAuthenticated) {
      router.push("/");
    }
  }, [isAuthenticated]);

  if (isAuthenticated) return null;

  const onSubmit = async ({ email, password, save_last_seen }) => {
    try {
      await login({ email, password, save_last_seen });

      router.push("/");
    } catch ({ message }) {
      setError("email", {
        type: "manual",
        message,
      });
    }
  };

  return (
    <div className="login">
      <Header c={c} />
      <img
        src="../assets/pages/img/AMAI-AI-Experts-white.inline.svg"
        style={{ height: 17 }}
        alt=""
      />{" "}
      <div class="content">
        <h3 class="form-title font-green">Sign In</h3>

        <div class="alert alert-danger display-hide">
          <button class="close" data-close="alert"></button>
          <span> Enter any username and password. </span>
        </div>
        <form onSubmit={handleSubmit(onSubmit)} class="login-form">
          <div class="form-group">
            <input
              class="form-control form-control-solid placeholder-no-fix"
              type="email"
              name="email"
              id="email"
              ref={register({
                value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                required: "You must provide a email.",
              })}
              placeholder="Your email"
            />
            {errors.email && <span>{errors.email.message}</span>}
          </div>
          <div>
            <label class="control-label visible-ie8 visible-ie9">
              Password
            </label>
            <input
              class="form-control placeholder-no-fix"
              type="password"
              name="password"
              id="password"
              ref={register({
                required: "You must provide a password.",
              })}
              placeholder="Enter your password"
            />
            {errors.password && <span>{errors.password.message}</span>}
          </div>

          <div class="form-actions">
            <button
              id="save_last_seen"
              name="save_last_seen"
              ref={register}
              class="btn green uppercase"
              type="submit"
              disabled={isSubmitting}
            >
              Login
            </button>{" "}
            {/* <label
              htmlFor="save_last_seen"
              class="rememberme check mt-checkbox mt-checkbox-outline"
            >
              <input
                type="checkbox"
                id="save_last_seen"
                name="save_last_seen"
                ref={register}
              />
              Show as online to other users <span></span>
            </label> */}
          </div>
        </form>
      </div>
    </div>
  );
}
