import { useRouter } from "next/router";
import useSWR from "swr";
import { useForm } from "react-hook-form";
import Link from "next/link";
import { gql, hasuraUserClient } from "../../lib/hasura-user-client";
import { useAuthState, useAuthDispatch } from "../../context/auth";

const getProjectsIds = gql`
  {
    Project {
      id
      Title
      Description
    }
  }
`;

const GetProjectByID = gql`
  query GetProjectByID($id: Int!) {
    Project_by_pk(id: $id) {
      id
      Title
      Description
      company_name
      created_at
    }
  }
`;
const GetProjectByUser = gql`
  query User($id: Int!) {
    User(where: { project_id: { _is_null: true } }) {
      id
      Name
    }
  }
`;
export const getStaticPaths = async () => {
  const hasura = hasuraUserClient();

  const { Project } = await hasura.request(getProjectsIds);

  return {
    paths: Project.map(({ id }) => ({
      params: {
        id: id.toString(),
      },
    })),
    fallback: false,
  };
};

export const getStaticProps = async ({ params }) => {
  const hasura = hasuraUserClient();
  const { id } = params;

  const initialData = await hasura.request(GetProjectByID, { id });

  return {
    props: {
      initialData,
    },
    revalidate: 1,
  };
};
const UpdateProject = gql`
  mutation update_Candidate_by_pk(
    $id: Int!
    $Title: String!
    $Description: String!
    $company_name: String!
  ) {
    update_Project_by_pk(
      pk_columns: { id: $id }
      _set: {
        Title: $Title
        Description: $Description
        company_name: $company_name
      }
    ) {
      Title
      Description
      company_name
    }
  }
`;
export default function ProjectEdit({ initialData }) {
  const hasura = hasuraUserClient();
  const { query } = useRouter();
  const router = useRouter();
  const { isAuthenticated, user } = useAuthState();

  useEffect(() => {
    if (!isAuthenticated) router.push("/");
  }, [isAuthenticated]);

  if (!isAuthenticated) return null;
  const { id } = query;
  const {
    handleSubmit,
    register,
    control,
    errors,
    formState: { isSubmitting },
    defaultValues: { ...defaultValues },
  } = useForm();
  const { data, mutate } = useSWR(
    [GetProjectByID, id],
    (query, id) => hasura.request(query, { id }),
    {
      initialData,
      revalidateOnMount: true,
    }
  );

  const onSubmit = async ({ Title, Description, company_name }) => {
    try {
      const id = data.Project_by_pk.id;
      console.log(id);
      const { update_Project_by_pk } = await hasura.request(UpdateProject, {
        id,
        Title,
        Description,
        company_name,
      });

      mutate({
        ...data,
        ...update_Project_by_pk,
      });
      router.push(`/Project/List`);
    } catch (err) {
      console.log(err);
    }
  };
  return (
    <div className="page-content-wrapper">
      <div className="page-content" style={{ minHeight: 1500 }}>
        <div class="profile-content">
          <div class="row">
            <div class="col-md-12">
              <div class="portlet light ">
                <div class="portlet-body">
                  <div class="tab-content">
                    <div class="tab-pane active" id="tab_1_1">
                      <form onSubmit={handleSubmit(onSubmit)}>
                        <div class="form-group">
                          <label class="control-label">Title</label>
                          <input
                            defaultValue={data.Project_by_pk.Title}
                            name="Title"
                            id="Title"
                            type="text"
                            class="form-control"
                            ref={register}
                          />{" "}
                        </div>
                        <div class="form-group">
                          <label class="control-label">Description</label>
                          <input
                            defaultValue={data.Project_by_pk.Description}
                            name="Description"
                            id="Description"
                            type="text"
                            placeholder="Description"
                            class="form-control"
                            ref={register}
                          />{" "}
                        </div>{" "}
                        <div class="form-group">
                          <label class="control-label">Company</label>
                          <input
                            defaultValue={data.Project_by_pk.company_name}
                            name="company_name"
                            id="company_name"
                            type="text"
                            placeholder="company_name"
                            class="form-control"
                            ref={register}
                          />{" "}
                        </div>
                        <div class="margiv-top-10">
                          <button
                            type="submit"
                            class="btn green"
                            disabled={isSubmitting}
                          >
                            {" "}
                            Save Changes{" "}
                          </button>{" "}
                          {/* <Link href="/Project/List">
                            <a class="btn default"> Cancel </a>
                          </Link> */}
                        </div>
                      </form>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>{" "}
    </div>
  );
}
