import useSWR, { mutate } from "swr";
import { useForm } from "react-hook-form";
import gql from "graphql-tag";
import { useRouter } from "next/router";
import { hasuraUserClient } from "../../lib/hasura-user-client";
import { useState } from "react";

const from = new Date().toISOString();
const Users = gql`
  query MyQuery {
    User {
      id
    }
  }
`;
const GetTasks = gql`
  query MyQuery($id: Int!) {
    Task(where: { user_id: { _eq: $id } }) {
      id
      name
      user_id
      Description
      completed
      Project {
        Title
        id
        status
      }
    }
    Task_aggregate(where: { user_id: { _eq: $id } }) {
      aggregate {
        count
      }
    }
  }
`;
const Updatecompleted = gql`
  mutation MyMutation($id: Int!) {
    update_Task_by_pk(pk_columns: { id: $id }, _set: { completed: true }) {
      completed
    }
  }
`;
const InsertTask = gql`
  mutation InsertTask(
    $Description: String!
    $name: String!
    $from: timestamptz!
    $user_id: Int!
  ) {
    insert_Task_one(
      object: {
        Description: $Description
        name: $name
        Date: $from
        user_id: $user_id
        completed: false
      }
    ) {
      Description
      Date
      name
      user_id
    }
  }
`;
export const getStaticPaths = async () => {
  const hasura = hasuraUserClient();

  const { User } = await hasura.request(Users);

  return {
    paths: User.map(({ id }) => ({
      params: {
        id: id.toString(),
      },
    })),
    fallback: false,
  };
};
export const getStaticProps = async ({ params }) => {
  const hasura = hasuraUserClient();
  const { id } = params;

  console.log(params);

  const initialData = await hasura.request(GetTasks, { id });

  return {
    props: {
      initialData,
    },
    revalidate: 1,
  };
};

export default function Task({ initialData }) {
  const hasura = hasuraUserClient();
  const { query } = useRouter();
  const { id } = query;
  const [acc, setAcc] = useState(true);

  const { data } = useSWR(
    [GetTasks, id],

    (query, id) => hasura.request(query),
    {
      initialData,
      revalidateOnMount: true,
    }
  );
  const view = () => {
    if (data.Task_aggregate.aggregate.count === 0) {
      setAcc(false);
    } else
      alert(
        "oh wait there buddy ,you didn't complete all the tasks , cmon you can do it ! finish it all !"
      );
  };
  const router = useRouter();
  const {
    handleSubmit,
    register,
    control,
    errors,
    formState: { isSubmitting },
  } = useForm();
  const Complete = async ({ id }) => {
    console.log(id);
    try {
      const { update_Task_by_pk } = await hasura.request(Updatecompleted, {
        id,
      });
    } catch (err) {
      console.log(err);
    }
  };
  const refresh = () => {
    window.location.reload();
  };
  const onSubmit = async ({ Description, name }) => {
    console.log(from);
    try {
      const { insert_Task_one } = await hasura.request(InsertTask, {
        Description,
        from,
        name,
        user_id: id,
      });

      window.location.reload();
    } catch (err) {
      console.log(err);
    }
  };
  return (
    <div className="page-content-wrapper">
      {" "}
      <div className="page-content" style={{ minHeight: 1500 }}>
        <div class="profile-content">
          {" "}
          <div class="row">
            <div className="col-md-12">
              {" "}
              {data.Task_aggregate.aggregate.count === 0 ? (
                <div class="col-md-9">
                  <div class="card card-white">
                    <div class="card-body">
                      {" "}
                      <div class="portlet-title">
                        <div class="caption font-dark">
                          <i class="icon-settings font-dark"></i>
                          <span class="caption-subject bold uppercase">
                            {" "}
                            Project tasks
                          </span>
                        </div>{" "}
                        <div className="col-md-1"> d</div>
                      </div>{" "}
                      <hr />
                      <p>
                        Nothing to show here , oh wait you already completed
                        your tasks ! inform the <strong> ADMIN</strong> on your
                        right side !
                      </p>
                      <div>
                        <img
                          style={{
                            display: "block",
                            objectFit: "cover",
                            margin: "auto",
                            width: 1280,
                            height: 770,
                          }}
                          src="../nothing.png"
                        ></img>
                      </div>
                    </div>
                  </div>
                </div>
              ) : (
                <div class="col-md-9">
                  <div class="card card-white">
                    <div class="card-body" style={{ paddingBottom: 270 }}>
                      {" "}
                      <div class="portlet-title">
                        <div className="row">
                          <div className="col-md-10">
                            <h3 class="font-green">Project Tasks</h3> <hr />
                          </div>{" "}
                          <div className="col-md-2">
                            <div class="btn-group">
                              {" "}
                              <a
                                onClick={() => refresh()}
                                id="sample_editable_1_2_new"
                                class="btn sbold green"
                              >
                                {" "}
                                <i className="fa fa-refresh"></i>
                              </a>
                            </div>
                          </div>
                        </div>
                      </div>{" "}
                      <hr />
                      <ul class="nav nav-pills todo-nav">
                        <li
                          role="presentation"
                          class="nav-item all-task active"
                        >
                          <a href="#" class="nav-link">
                            All
                          </a>
                        </li>
                        <li role="presentation" class="nav-item active-task">
                          <a href="#" class="nav-link">
                            Active
                          </a>
                        </li>
                        <li role="presentation" class="nav-item completed-task">
                          <a href="#" class="nav-link">
                            Completed
                          </a>
                        </li>
                      </ul>{" "}
                      <div>
                        {data.Task.map(
                          ({ id, completed, name, Description, Project }) => {
                            return (
                              <div key={id} class="todo-list">
                                {completed === false ? (
                                  <div class="todo-item">
                                    <div class="checker">
                                      <span class="">
                                        <input
                                          name="Completed"
                                          type="checkbox"
                                          onChange={() => Complete({ id })}
                                        />
                                      </span>
                                    </div>{" "}
                                    {""}
                                    <span>
                                      {name}: {Description}
                                    </span>
                                    <div style={{ marginLeft: 23 }}>
                                      <strong> Project:</strong>
                                      {Project.Title}
                                    </div>
                                    {/* <a
                              href="javascript:void(0);"
                              class="float-right remove-todo-item"
                              onClick={() => deleteTodo({ id })}
                            >
                              <i class="icon-close"></i>
                            </a> */}
                                  </div>
                                ) : (
                                  <div></div>
                                )}
                              </div>
                            );
                          }
                        )}{" "}
                      </div>
                    </div>
                  </div>
                </div>
              )}
              <div className="col">
                <div className="col-md-3">
                  <div class="portlet light ">
                    <div class="portlet-body" style={{ paddingBottom: 70 }}>
                      {" "}
                      {acc ? (
                        <div class="card-body">
                          {" "}
                          <div class="portlet-title">
                            <span class="caption-subject bold uppercase">
                              inform manager
                            </span>
                          </div>{" "}
                          <hr />
                          <div className="col-md-6">
                            Completed? {""}
                            <button
                              className=" btn btn-circle blue btn-sm "
                              type="submit"
                              onClick={() => view()}
                            >
                              yes
                            </button>
                          </div>
                          <div className="col-md-6">
                            <button
                              onClick={() => whencompleted()}
                              className="btn btn-primary btn-sm"
                              type="submit"
                              disabled
                            >
                              {" "}
                              ping admin
                            </button>
                          </div>
                          <img
                            style={{
                              display: "block",
                              objectFit: "cover",
                              width: "auto",
                              height: 200,
                              margin: "auto",
                            }}
                            src="../yes.png"
                          ></img>
                        </div>
                      ) : (
                        <div class="card-body">
                          {" "}
                          <div class="portlet-title">
                            <span class="caption-subject bold uppercase">
                              inform admin
                            </span>
                          </div>{" "}
                          <hr />
                          <div className="col-md-6">
                            {" "}
                            Not yet? {""}
                            <button
                              className=" btn btn-circle red btn-sm "
                              type="submit"
                              onClick={() => dontshow()}
                            >
                              {" "}
                              no
                            </button>{" "}
                          </div>
                          <div className="col-md-6">
                            <button
                              onClick={() => whencompleted()}
                              className="btn btn-primary btn-sm active"
                              type="submit"
                            >
                              {" "}
                              ping admin
                            </button>
                          </div>{" "}
                          <img
                            style={{
                              display: "block",
                              objectFit: "cover",
                              width: "auto",
                              height: 200,
                            }}
                            src="../no.png"
                          ></img>
                        </div>
                      )}
                    </div>
                  </div>
                </div>{" "}
                <div className="col-md-3">
                  <div class="portlet light ">
                    <div class="portlet-body" style={{ paddingBottom: 70 }}>
                      {" "}
                      {data.Task_aggregate.aggregate.count === 0 ? (
                        <div class="card-body">
                          {" "}
                          <div class="portlet-title">
                            <span class="caption-subject bold uppercase">
                              More info
                            </span>
                          </div>{" "}
                          <hr />
                          <div>
                            hohohoho you completed all the tasks !! congaaatz
                          </div>
                          <img
                            style={{
                              display: "block",
                              objectFit: "cover",
                              width: "auto",
                              height: 200,
                              margin: "auto",
                            }}
                            src="../yees.png"
                          ></img>
                        </div>
                      ) : (
                        <div class="card-body">
                          {" "}
                          <div class="portlet-title">
                            <span class="caption-subject bold uppercase">
                              More info
                            </span>
                          </div>{" "}
                          <hr />
                          <div>
                            you still have{" "}
                            <strong>
                              {" "}
                              {data.Task_aggregate.aggregate.count}{" "}
                            </strong>{" "}
                            task to go !!! lets do it
                          </div>{" "}
                          <img
                            style={{
                              display: "block",
                              objectFit: "cover",
                              width: "auto",
                              height: 200,
                            }}
                            src="../notyet.png"
                          ></img>
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
