import useSWR, { mutate } from "swr";
import formatRelative from "date-fns/formatRelative";
import differenceInMinutes from "date-fns/differenceInMinutes";
import Link from "next/link";
import Layout from "../Components/Layout";
import gql from "graphql-tag";
import { useRouter } from "next/router";
import { hasuraUserClient } from "../../lib/hasura-user-client";
import { useAuthState, useAuthDispatch } from "../../context/auth";
const Todos = gql`
  query MyQuery {
    User {
      id
    }
  }
`;
const GetCandidats = gql`
  query MyQuery($id: Int!) {
    Todo(where: { user_id: { _eq: $id } }) {
      Name
      user_id
    }
  }
`;
export const getStaticPaths = async () => {
  const hasura = hasuraUserClient();

  const { User } = await hasura.request(Todos);

  return {
    paths: User.map(({ id }) => ({
      params: {
        id: id.toString(),
      },
    })),
    fallback: false,
  };
};
export const getStaticProps = async ({ params }) => {
  const hasura = hasuraUserClient();
  const { id } = params;

  console.log(params);

  const initialData = await hasura.request(GetCandidats, { id });

  return {
    props: {
      initialData,
    },
    revalidate: 1,
  };
};

export default function TodoList({ initialData }) {
  const hasura = hasuraUserClient();
  const { query } = useRouter();
  const { id } = query;
  const { data } = useSWR(
    [GetCandidats, id],

    (query, id) => hasura.request(query),
    {
      initialData,
      revalidateOnMount: true,
    }
  );

  return (
    <div className="page-content-wrapper">
      {" "}
      <div className="page-content" style={{ minHeight: 1500 }}>
        <div class="row">
          <div class="col-md-12">
            <div class="card card-white">
              <div class="card-body">
                <form action="javascript:void(0);">
                  <input
                    type="text"
                    class="form-control add-task"
                    placeholder="New Task..."
                  />
                </form>
                <ul class="nav nav-pills todo-nav">
                  <li role="presentation" class="nav-item all-task active">
                    <a href="#" class="nav-link">
                      All
                    </a>
                  </li>
                  <li role="presentation" class="nav-item active-task">
                    <a href="#" class="nav-link">
                      Active
                    </a>
                  </li>
                  <li role="presentation" class="nav-item completed-task">
                    <a href="#" class="nav-link">
                      Completed
                    </a>
                  </li>
                </ul>{" "}
                {data.Todo.map(({ Description, Name }) => {
                  return (
                    <div key={id} class="todo-list">
                      <div class="todo-item">
                        <div class="checker">
                          <span class="">
                            <input type="checkbox" />
                          </span>
                        </div>
                        <span>{Name}</span>
                        <a
                          href="javascript:void(0);"
                          class="float-right remove-todo-item"
                        >
                          <i class="icon-close"></i>
                        </a>
                      </div>
                      {/* <div class="todo-item">
                        <div class="checker">
                          <span class="">
                            <input type="checkbox" />
                          </span>
                        </div>
                        <span>Work on wordpress</span>
                        <a
                          href="javascript:void(0);"
                          class="float-right remove-todo-item"
                        >
                          <i class="icon-close"></i>
                        </a>
                      </div>

                      <div class="todo-item">
                        <div class="checker">
                          <span class="">
                            <input type="checkbox" />
                          </span>
                        </div>
                        <span>Organize office main department</span>
                        <a
                          href="javascript:void(0);"
                          class="float-right remove-todo-item"
                        >
                          <i class="icon-close"></i>
                        </a>
                      </div>
                      <div class="todo-item">
                        <div class="checker">
                          <span>
                            <input type="checkbox" />
                          </span>
                        </div>
                        <span>Error solve in HTML template</span>
                        <a
                          href="javascript:void(0);"
                          class="float-right remove-todo-item"
                        >
                          <i class="icon-close"></i>
                        </a>
                      </div> */}
                    </div>
                  );
                })}{" "}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
