import React from "react";
import gql from "graphql-tag";
import Header from "./Header/Header";
import { hasuraUserClient } from "../../lib/hasura-user-client";
import { useEffect } from "react";
import { useRouter } from "next/router";

const Notification_count = gql`
  query MyQuery {
    notification_aggregate {
      aggregate {
        count(columns: id)
      }
    }
  }
`;
export default function Notification({ children, c }) {
  useEffect(() => {
    updateLastSeen();
  });

  const updateLastSeen = async () => {
    const hasura = hasuraUserClient();
    const initialData = await hasura.request(Notification_count);
    const c = initialData.notification_aggregate.aggregate.count;

    await hasura.request(Notification_count);
    return {
      props: {
        initialData,
        c,
      },
      revalidate: 1,
    };
  };

  return children;
}
