import { useRouter } from "next/router";
import useSWR from "swr";
import { useForm } from "react-hook-form";
import Link from "next/link";
import { gql, hasuraUserClient } from "../../lib/hasura-user-client";
import { useAuthState, useAuthDispatch } from "../../context/auth";
import { useEffect } from "react";
import { TextField } from "@material-ui/core";
import { Form } from "react-bootstrap";
import { useState } from "react";

const getTaskIds = gql`
  {
    Task {
      id
      name
      Description
      Date
      user_id
      Project_id
    }
  }
`;
const getTaskById = gql`
  query GetTaskByID($id: Int!) {
    Task_by_pk(id: $id) {
      id
      name
      Description
      Date
      user_id
      User {
        Avatar
        Name
        email
        Domain
      }
      Project_id
    }
  }
`;
const GetUsersWithnT = gql`
  query getusers {
    User(where: { _not: { Tasks: {} } }) {
      id
      email
      Name
      Avatar
      Domain
    }
  }
`;
const GetAllUsers = gql`
  query getusers {
    User {
      id
      email
      Name
      Avatar
      Domain
      Tasks_aggregate {
        aggregate {
          count
        }
      }
    }
  }
`;
const UpdateTask = gql`
  mutation update_Task_by_pk($id: Int!, $name: String!, $Description: String!) {
    update_Task_by_pk(
      pk_columns: { id: $id }
      _set: { name: $name, Description: $Description }
    ) {
      id
      name
      Description
    }
  }
`;
const UpdateProject = gql`
  mutation update_Project_by_pk($id: Int!) {
    update_Project_by_pk(pk_columns: { id: $id }, _set: { assigned: true }) {
      id
      assigned
    }
  }
`;
const UpdateUser = gql`
  mutation update_Candidate_by_pk($id: Int!, $user_id: Int!) {
    update_Task_by_pk(pk_columns: { id: $id }, _set: { user_id: $user_id }) {
      id
      user_id
    }
  }
`;
const AddNotification = gql`
  mutation MyMutation($user_id: Int!) {
    insert_notification_one(
      object: {
        name: "Task"
        seen: false
        description: "you are assigned to a new task , please check your tasks in your profile"
        user_id: $user_id
      }
    ) {
      seen
      name
      id
      description
      user_id
    }
  }
`;
export const getStaticPaths = async () => {
  const hasura = hasuraUserClient();

  const { Task } = await hasura.request(getTaskIds);

  return {
    paths: Task.map(({ id }) => ({
      params: {
        id: id.toString(),
      },
    })),
    fallback: false,
  };
};
export const getStaticProps = async ({ params }) => {
  const hasura = hasuraUserClient();
  const { id } = params;

  const initialData = await hasura.request(getTaskById, { id });
  const datauser = await hasura.request(GetUsersWithnT);
  const users = await hasura.request(GetAllUsers);

  return {
    props: {
      initialData,
      datauser,
      users,
    },
    revalidate: 1,
  };
};
export default function AssignTask({ initialData, datauser, users }) {
  const hasura = hasuraUserClient();
  const { query } = useRouter();
  const router = useRouter();
  const { isAuthenticated, user } = useAuthState();

  useEffect(() => {
    if (!isAuthenticated) router.push("/");
  }, [isAuthenticated]);
  const { id } = query;
  const {
    handleSubmit,
    register,
    control,
    errors,
    formState: { isSubmitting },
    defaultValues: { ...defaultValues },
  } = useForm();
  const { data, mutate } = useSWR(
    [getTaskById, id],
    (query, id) => hasura.request(query, { id }),
    {
      initialData,
      revalidateOnMount: true,
    }
  );
  console.log(users.User.Task_aggregate);
  const onSubmit = async ({ name, Description }) => {
    if (
      confirm(`are you sure you want to assign the project to this employee? `)
    ) {
      try {
        const id = data.Task_by_pk.id;
        const { update_Task_by_pk } = await hasura.request(UpdateTask, {
          id,
          name,
          Description,
        });

        mutate({
          ...data,
          ...update_Task_by_pk,
        });
        window.location.reload();
      } catch (err) {
        console.log(err);
      }
    } else {
      return null;
    }
  };
  const updateUser = async ({ id }) => {
    try {
      console.log(id);
      const t_id = data.Task_by_pk.id;
      const id_p = data.Task_by_pk.Project_id;

      const { update_Task_by_pk } = await hasura.request(UpdateUser, {
        id: t_id,
        user_id: id,
      });
      const { insert_notification_one } = await hasura.request(
        AddNotification,
        { user_id: id }
      );
      const { update_Project_by_pk } = await hasura.request(UpdateProject, {
        id: id_p,
      });
      mutate({
        ...data,
        ...update_Task_by_pk,
      });
      window.location.reload();
    } catch (err) {
      console.log(err);
    }
  };
  return (
    <div className="page-content-wrapper">
      <div className="page-content" style={{ minHeight: 1500 }}>
        <div class="profile-content">
          <div class="row">
            <div class="col-md-12">
              <div class="portlet light ">
                <div class="portlet-body">
                  <div class="row">
                    {" "}
                    <div class="col-md-11">
                      {" "}
                      <h3 className="caption-subject font-blue-madison bold uppercase">
                        Update Project information
                      </h3>{" "}
                      <ul className="nav nav-tabs">
                        <li className="active">
                          <a href="#tab_1_1" data-toggle="tab">
                            Edit Task
                          </a>
                        </li>
                        <li>
                          <a href="#tab_1_2" data-toggle="tab">
                            Assign Task
                          </a>
                        </li>
                        <li>
                          <a href="#tab_1_3" data-toggle="tab">
                            change User
                          </a>
                        </li>
                      </ul>
                    </div>
                  </div>{" "}
                  <hr></hr>{" "}
                  <div className="tab-content">
                    <div className="tab-pane active" id="tab_1_1">
                      {" "}
                      <form onSubmit={handleSubmit(onSubmit)}>
                        {" "}
                        <div class="form-group">
                          <label class="control-label">Name</label>
                          <input
                            defaultValue={data.Task_by_pk.name}
                            name="name"
                            id="name"
                            type="text"
                            placeholder="Description"
                            class="form-control"
                            ref={register}
                          />{" "}
                        </div>
                        <div class="form-group">
                          <label class="control-label">Description</label>
                          <input
                            defaultValue={data.Task_by_pk.Description}
                            name="Description"
                            id="Description"
                            type="text"
                            placeholder="Description"
                            class="form-control"
                            ref={register}
                          />{" "}
                        </div>{" "}
                        <div class="form-group">
                          <button
                            type="submit"
                            class="btn green"
                            disabled={isSubmitting}
                          >
                            {" "}
                            Save Changes{" "}
                          </button>
                        </div>
                      </form>
                    </div>{" "}
                    <div className="tab-pane " id="tab_1_2">
                      {data.Task_by_pk.user_id ? (
                        <div>
                          {" "}
                          <div
                            style={{ marginRight: 1200 }}
                            class="alert alert-warning alert-dismissible fade in"
                          >
                            <strong>
                              Task already assigned! to{" "}
                              {data.Task_by_pk.User.email}
                            </strong>
                            <br></br>
                            <br></br>
                            <br></br>
                            <br></br>
                            <br></br>
                          </div>{" "}
                          <br></br>
                          <br></br>
                          <br></br>
                          <br></br>
                          <br></br> <br></br>
                        </div>
                      ) : (
                        <div>
                          <div class="row">
                            <div class="col-md-12">
                              <div class="portlet light portlet-fit bordered">
                                <div class="portlet-title">
                                  <div class="caption">
                                    <i class=" icon-layers font-green"></i>
                                    <span class="caption-subject font-green bold uppercase">
                                      Employees with no tasks
                                    </span>
                                  </div>
                                </div>{" "}
                                <div class="portlet-body">
                                  <div class="mt-element-card mt-card-round mt-element-overlay">
                                    <div class="row">
                                      -{" "}
                                      {datauser.User.map(
                                        ({ id, Name, Domain, Avatar }) => {
                                          return (
                                            <div
                                              class="col-lg-3 col-md-4 col-sm-6 col-xs-12"
                                              key={id}
                                            >
                                              <div class="mt-card-item">
                                                <div class="mt-card-avatar mt-overlay-1">
                                                  {Avatar ? (
                                                    <img
                                                      src={`../../${Avatar}`}
                                                      style={{
                                                        display: "inline",
                                                        width: 150,
                                                        height: 170,
                                                        objectFit: "cover",
