import { useEffect } from "react";
import { useRouter } from "next/router";
import { useForm } from "react-hook-form";

import { useAuthState, useAuthDispatch } from "../context/auth";
import { gql, hasuraAdminClient } from "../lib/hasura-admin-client";
import { hasuraUserClient } from "../lib/hasura-user-client";

const from = new Date().toISOString();

const GetUsers = gql`
  {
    User {
      Name
      id
      notifications_aggregate(where: { seen: { _eq: false } }) {
        aggregate {
          count
        }
      }
      notifications(where: { seen: { _eq: false } }) {
        id
        description
      }
    }
  }
`;
const GetTodos = gql`
  {
    Todo {
      Description
      Name
      Date
    }
  }
`;
const GetUserById = gql`
  query GetUserById($user_id: Int!) {
    User_by_pk(id: $user_id) {
      id
      notifications_aggregate(where: { seen: { _eq: false } }) {
        aggregate {
          count
        }
      }
      notifications(where: { seen: { _eq: false } }) {
        id
        description
      }
    }
  }
`;
const AddNotification = gql`
  mutation MyMutation($user_id: Int!) {
    insert_notification_one(
      object: {
        user_id: $user_id
        name: "ttt"
        seen: false
        description: "tttttt"
      }
    ) {
      user_id
      seen
      name
      id
      description
    }
  }
`;
const insertMultiple = gql`
  mutation MyMutation(
    $Description: String!
    $Name: String!
    $Date: timestamptz = ""
    $Description1: String!
    $Name1: String!
    $Date1: timestamptz = ""
    $Description2: String!
    $Name2: String!
    $Date2: timestamptz = ""
    $Description3: String!
    $Name3: String!
    $Date3: timestamptz = ""
    $Description4: String!
    $Name4: String!
    $Date4: timestamptz = ""
  ) {
    insert_Todo(
      objects: [
        { Description: $Description, Name: $Name, Date: $Date }
        { Description: $Description1, Name: $Name1, Date: $Date1 }
        { Description: $Description2, Name: $Name1, Date: $Date1 }
        { Description: $Description3, Name: $Name3, Date: $Date3 }
        { Description: $Description4, Name: $Name4, Date: $Date4 }
      ]
    ) {
      affected_rows
    }
  }
`;
const InsertTask = gql`
  mutation InsertTask(
    $Description: String!
    $Name: String!
    $from: timestamptz!
  ) {
    insert_Todo_one(
      object: { Description: $Description, Name: $Name, Date: $from }
    ) {
      Description
      Date
      Name
    }
  }
`;

export const getStaticProps = async () => {
  const { User } = await hasuraAdminClient.request(GetUsers);
  const { Todo } = await hasuraAdminClient.request(GetTodos);
  return {
    props: {
      User,
      Todo,
    },
  };
};

export default function Todos({ User, Todo }) {
  const router = useRouter();
  const { isAuthenticated, user } = useAuthState();
  const hasura = hasuraUserClient();
  const {
    handleSubmit,
    register,
    control,
    errors,
    formState: { isSubmitting },
  } = useForm();
  const { updateUser } = useAuthDispatch();
  useEffect(() => {
    if (!isAuthenticated) router.push("/");
  }, [isAuthenticated]);

  if (!isAuthenticated) return null;

  const onSubmit = async ({ Description, Name }) => {
    console.log(from);
    try {
      const { insert_Todo_one } = await hasura.request(InsertTask, {
        Description,
        from,
        Name,
      });

      window.location.reload();
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className="page-content-wrapper">
      <div className="page-content" style={{ minHeight: 1500 }}>
        <div class="profile-content">
          <div class="row">
            <div class="col-md-6">
              <div class="portlet light ">
                <div class="portlet-body">
                  <div class="tab-content">
                    <div class="tab-pane active" id="tab_1_1">
                      <form onSubmit={handleSubmit(onSubmit)}>
                        <div class="form-group">
                          <label class="control-label">Name</label>
                          <input
                            name="Name"
                            id="Name"
                            type="text"
                            placeholder="Todo Name"
                            class="form-control"
                            ref={register}
                            required
                          />{" "}
                        </div>
                        <div class="form-group">
                          <label class="control-label">Description</label>
                          <textarea
                            name="Description"
                            id="Description"
                            type="textarea"
                            placeholder="Description"
                            class="form-control"
                            ref={register}
                            required
                          />{" "}
                        </div>
                        {/* <select
                          name="user_id"
                          id="user_id"
                          ref={register({
                            required:
                              "You must select a category for your thread",
                          })}
                          className="appearance-none bg-transparent w-full"
                        >
                          {User.map(({ id, Name }) => (
                            <React.Fragment key={id}>
                              <option value={id}>{Name}</option>
                            </React.Fragment>
                          ))}
                        </select>{" "} */}
                        <div class="margiv-top-10">
                          <button
                            type="submit"
                            class="btn green"
                            disabled={isSubmitting}
                          >
                            {" "}
                            Save Changes{" "}
                          </button>
                        </div>
                      </form>
                    </div>
                  </div>
                </div>
              </div>
            </div>{" "}
            <div class="col-md-6">
              <div class="portlet light ">
                <div class="portlet-body">
                  <div class="table-container">
                    <table
                      class="table table-striped table-bordered table-hover"
                      id="sample_3"
                    >
                      <thead>
                        <tr>
                          <th> Name </th>
                          <th> Last name </th>
                          <th> Email </th>
                          <th> Status </th>
                          <th> Joined </th>
                          <th> Actions </th>
                        </tr>
                      </thead>
                      <tfoot>
                        <tr>
                          <th> </th>
                          <th> </th>
                          <th> </th>
                          <th> </th>
                          <th> </th>
                          <th> </th>
                        </tr>
                      </tfoot>
                      <tbody>
                        {Todo.map(
                          ({ id, Name, Last_Name, email, created_at }) => {
                            const formattedJoinedAt = format(
                              parseISO(created_at),
                              "MM/dd/yyyy"
                            );
                            return (
                              <tr key={id} class="odd gradeX">
                                <td>{Name}</td>
                                <td>{Last_Name}</td>
                                <td>{email}</td>
                                <td>
                                  {" "}
                                  <Spinner
                                    size={120}
                                    spinnerColor={"#333"}
                                    spinnerWidth={2}
                                    visible={true}
                                  />
                                </td>
                                <td class="center">{formattedJoinedAt}</td>
                                <td>
                                  <div class="btn-group">
                                    <button
                                      class="btn btn-xs green dropdown-toggle"
                                      type="button"
                                      data-toggle="dropdown"
                                      aria-expanded="false"
                                    >
                                      {" "}
                                      Actions
                                      <i class="fa fa-angle-down"></i>
                                    </button>
                                    <ul
                                      class="dropdown-menu pull-left"
                                      role="menu"
                                    >
                                      <li>
                                        <a
                                          className="closeBtn"
                                          onClick={() => deleteATodo(id)}
                                        >
                                          Edit employee
                                        </a>
                                      </li>
                                      <li>
                                        <a
                                          className="closeBtn"
                                          onClick={() => deleteATodo(id)}
                                        >
                                          Delete employee
                                        </a>
                                      </li>{" "}
                                      <li>
                                        <a
                                          className="closeBtn"
