import useSWR, { mutate } from "swr";

import gql from "graphql-tag";
import { useRouter } from "next/router";
import { hasuraUserClient } from "../../lib/hasura-user-client";
const Todos = gql`
  query MyQuery {
    User {
      id
    }
  }
`;
const GetCandidats = gql`
  query MyQuery($id: Int!) {
    todo_user(where: { user_id: { _eq: $id } }) {
      id
      todo_id
      completed
      user_id
      Todo {
        Description
        Name
        Type
        id
        Date
      }
    }
  }
`;
const UpdateCompleted = gql`
  mutation MyMutation($id: Int!) {
    update_todo_user_by_pk(pk_columns: { id: $id }, _set: { completed: true }) {
      id
    }
  }
`;
const getAdminIDs = gql`
  query MyQuery {
    User(where: { Role: { _eq: "Admin" } }) {
      id
    }
  }
`;
export const getStaticPaths = async () => {
  const hasura = hasuraUserClient();

  const { User } = await hasura.request(Todos);

  return {
    paths: User.map(({ id }) => ({
      params: {
        id: id.toString(),
      },
    })),
    fallback: false,
  };
};
export const getStaticProps = async ({ params }) => {
  const hasura = hasuraUserClient();
  const { id } = params;

  console.log(params);

  const initialData = await hasura.request(GetCandidats, { id });
  const Admins = await hasura.request(getAdminIDs);

  return {
    props: {
      initialData,
      Admins,
    },
    revalidate: 1,
  };
};

export default function TodoList({ initialData, Admins }) {
  const hasura = hasuraUserClient();
  const { query } = useRouter();
  const { id } = query;
  const { data } = useSWR(
    [GetCandidats, id],

    (query, id) => hasura.request(query),
    {
      initialData,
      revalidateOnMount: true,
    }
  );
  const Complete = async ({ id }) => {
    console.log(id);
    try {
      const { update_todo_user_by_pk } = await hasura.request(UpdateCompleted, {
        id,
      });
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className="page-content-wrapper">
      {" "}
      <div className="page-content" style={{ minHeight: 1500 }}>
        <div class="container">
          <div class="row">
            <div class="col-md-8">
              <div class="card card-white">
                <div class="card-body">
                  <ul class="nav nav-pills todo-nav">
                    <li role="presentation" class="nav-item all-task active">
                      <a href="#" class="nav-link">
                        All
                      </a>
                    </li>
                    <li role="presentation" class="nav-item active-task">
                      <a href="#" class="nav-link">
                        Active
                      </a>
                    </li>
                    <li role="presentation" class="nav-item completed-task">
                      <a href="#" class="nav-link">
                        Completed
                      </a>
                    </li>
                  </ul>{" "}
                  {data.todo_user.map(
                    ({ id, Description, Name, Completed, Todo }) => {
                      return (
                        <div key={id} class="todo-list">
                          {!Completed ? (
                            <div class="todo-item">
                              <div class="checker">
                                <span class="">
                                  <input
                                    name="Completed"
                                    type="checkbox"
                                    onChange={() => Complete({ id })}
                                  />
                                </span>
                              </div>{" "}
                              {""}
                              <span>
                                {Todo.Name}: {Todo.Description}
                              </span>
                              {/* <a
                                href="javascript:void(0);"
                                class="float-right remove-todo-item"
                                onClick={() => deleteTodo({ id })}
                              >
                                <i class="icon-close"></i>
                              </a> */}
                            </div>
                          ) : (
                            <div></div>
                          )}
                        </div>
                      );
                    }
                  )}{" "}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
