import { useRouter } from "next/router";
import useSWR from "swr";
import { useForm } from "react-hook-form";
import Link from "next/link";
import { gql, hasuraUserClient } from "../../lib/hasura-user-client";
import { useAuthState, useAuthDispatch } from "../../context/auth";
import { useEffect } from "react";
import { TextField } from "@material-ui/core";
import { Form } from "react-bootstrap";
import { useState } from "react";

const getTaskIds = gql`
  {
    Task {
      id
      name
      Description
      Date
      user_id
    }
  }
`;
const getTaskById = gql`
  query GetTaskByID($id: Int!) {
    Task_by_pk(id: $id) {
      id
      name
      Description
      Date
      user_id
    }
  }
`;
export const getStaticPaths = async () => {
  const hasura = hasuraUserClient();

  const { Task } = await hasura.request(getTaskIds);

  return {
    paths: Task.map(({ id }) => ({
      params: {
        id: id.toString(),
      },
    })),
    fallback: false,
  };
};
export const getStaticProps = async ({ params }) => {
  const hasura = hasuraUserClient();
  const { id } = params;

  const initialData = await hasura.request(getTaskById, { id });

  return {
    props: {
      initialData,
    },
    revalidate: 1,
  };
};
export default function AssignTask({ initialData }) {
  const hasura = hasuraUserClient();
  const { query } = useRouter();
  const router = useRouter();
  const { isAuthenticated, user } = useAuthState();
  const [File, setFile] = useState(null);
  const [state, setState] = useState(false);

  const [createObjectURL, setCreateObjectURL] = useState(null);

  useEffect(() => {
    if (!isAuthenticated) router.push("/");
  }, [isAuthenticated]);

  const { id } = query;
  const {
    handleSubmit,
    register,
    control,
    errors,
    formState: { isSubmitting },
    defaultValues: { ...defaultValues },
  } = useForm();
  const { data, mutate } = useSWR(
    [getTaskById, id],
    (query, id) => hasura.request(query, { id }),
    {
      initialData,
      revalidateOnMount: true,
    }
  );
  const onSubmit = async ({ name, Description, user_id }) => {
    try {
      const id = data.Project_by_pk.id;
      console.log(id);
      const { update_Project_by_pk } = await hasura.request(UpdateProject, {
        id,
        Title,
        Description,
        company_name,
        income,
        type,
      });

      mutate({
        ...data,
        ...update_Project_by_pk,
      });
      router.push(`/Project/List`);
    } catch (err) {
      console.log(err);
    }
  };
  return (
    <div className="page-content-wrapper">
      <div className="page-content" style={{ minHeight: 1500 }}>
        <div class="profile-content">
          <div class="row">
            <div class="col-md-12">
              <div class="portlet light ">
                <div class="portlet-body">
                  <div class="row">
                    {" "}
                    <div class="col-md-11">
                      {" "}
                      <h3 className="caption-subject font-blue-madison bold uppercase">
                        Update Project information
                      </h3>{" "}
                      <ul className="nav nav-tabs">
                        <li className="active">
                          <a href="#tab_1_1" data-toggle="tab">
                            Edit Task
                          </a>
                        </li>
                        <li>
                          <a href="#tab_1_2" data-toggle="tab">
                            Assign Task
                          </a>
                        </li>
                      </ul>
                    </div>
                  </div>{" "}
                  <hr></hr>{" "}
                  <div className="tab-content">
                    <div className="tab-pane active" id="tab_1_1">
                      {" "}
                      <form onSubmit={handleSubmit(onSubmit)}>
                        {" "}
                        <div class="form-group">
                          <label class="control-label">Name</label>
                          <input
                            defaultValue={data.Task_by_pk.name}
                            name="name"
                            id="name"
                            type="text"
                            placeholder="Description"
                            class="form-control"
                            ref={register}
                          />{" "}
                        </div>
                        <div class="form-group">
                          <label class="control-label">Description</label>
                          <input
                            defaultValue={data.Task_by_pk.Description}
                            name="Description"
                            id="Description"
                            type="text"
                            placeholder="Description"
                            class="form-control"
                            ref={register}
                          />{" "}
                        </div>{" "}
                        <br />
                        <div class="margiv-top-10">
                          <button
                            type="submit"
                            class="btn green"
                            disabled={isSubmitting}
                          >
                            {" "}
                            Save Changes{" "}
                          </button>
                        </div>
                      </form>
                    </div>{" "}
                    <div className="tab-pane " id="tab_1_2"></div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
