import useSWR, { mutate } from "swr";
import { useForm } from "react-hook-form";
import gql from "graphql-tag";
import { useRouter } from "next/router";
import { hasuraUserClient } from "../../lib/hasura-user-client";
const from = new Date().toISOString();
const Users = gql`
  query MyQuery {
    User {
      id
    }
  }
`;
const GetTasks = gql`
  query MyQuery($id: Int!) {
    Task(where: { user_id: { _eq: $id } }) {
      id
      name
      user_id
      Description
      completed
    }
  }
`;
const Updatecompleted = gql`
  mutation MyMutation($id: Int!) {
    update_Task_by_pk(pk_columns: { id: $id }, _set: { completed: true }) {
      completed
    }
  }
`;
const InsertTask = gql`
  mutation InsertTask(
    $Description: String!
    $name: String!
    $from: timestamptz!
    $user_id: Int!
  ) {
    insert_Task_one(
      object: {
        Description: $Description
        name: $name
        Date: $from
        user_id: $user_id
        completed: false
      }
    ) {
      Description
      Date
      name
      user_id
    }
  }
`;
export const getStaticPaths = async () => {
  const hasura = hasuraUserClient();

  const { User } = await hasura.request(Users);

  return {
    paths: User.map(({ id }) => ({
      params: {
        id: id.toString(),
      },
    })),
    fallback: false,
  };
};
export const getStaticProps = async ({ params }) => {
  const hasura = hasuraUserClient();
  const { id } = params;

  console.log(params);

  const initialData = await hasura.request(GetTasks, { id });

  return {
    props: {
      initialData,
    },
    revalidate: 1,
  };
};

export default function Task({ initialData }) {
  const hasura = hasuraUserClient();
  const { query } = useRouter();
  const { id } = query;
  const { data } = useSWR(
    [GetTasks, id],

    (query, id) => hasura.request(query),
    {
      initialData,
      revalidateOnMount: true,
    }
  );
  const router = useRouter();
  const {
    handleSubmit,
    register,
    control,
    errors,
    formState: { isSubmitting },
  } = useForm();
  const Complete = async ({ id }) => {
    console.log(id);
    try {
      const { update_Task_by_pk } = await hasura.request(Updatecompleted, {
        id,
      });
    } catch (err) {
      console.log(err);
    }
  };
  const deleteTodo = async ({ id }) => {
    const query = gql`
      mutation delete_Todo($id: Int!) {
        delete_Task(where: { id: { _eq: $id } }) {
          affected_rows
        }
      }
    `;

    try {
      await hasura.request(query, { id });
      mutate();
    } catch (error) {
      console.error(error);
    }
  };
  const onSubmit = async ({ Description, name }) => {
    console.log(from);
    try {
      const { insert_Task_one } = await hasura.request(InsertTask, {
        Description,
        from,
        name,
        user_id: id,
      });

