import Layout from "../pages/Components/Layout";
import { AuthProvider } from "../context/auth";

function MyApp({ Component, pageProps, apolloClient }) {
  const { isAuthenticated, user } = useAuthState();

  return (
    <AuthProvider>
      {" "}
      {!isAuthenticated ? (
        <Component {...pageProps} />
      ) : (
        <Layout>
          <Component {...pageProps} />{" "}
        </Layout>
      )}
    </AuthProvider>
  );
}

export default MyApp;
