import { useRouter } from "next/router";
import useSWR from "swr";
import { useForm } from "react-hook-form";
import Link from "next/link";
import { gql, hasuraUserClient } from "../../lib/hasura-user-client";
import { useAuthState, useAuthDispatch } from "../../context/auth";
import { useEffect } from "react";
import { TextField } from "@material-ui/core";
import { Form } from "react-bootstrap";
import { useState } from "react";

const today = new Date().toISOString().substring(0, 19);

const getProjectsIds = gql`
  {
    Project {
      id
      Description
      created_at
      Title
      delivery_date
      assigned
      contact_date
      company_name
      type
      file
      income
    }
  }
`;

const GetProjectByID = gql`
  query GetProjectByID($id: Int!) {
    Project_by_pk(id: $id) {
      id
      Description
      created_at
      Title
      delivery_date
      assigned
      contact_date
      company_name
      type
      file
      income
    }
  }
`;
const GetProjectByUser = gql`
  query User($id: Int!) {
    User(where: { project_id: { _is_null: true } }) {
      id
      Name
    }
  }
`;
export const getStaticPaths = async () => {
  const hasura = hasuraUserClient();

  const { Project } = await hasura.request(getProjectsIds);

  return {
    paths: Project.map(({ id }) => ({
      params: {
        id: id.toString(),
      },
    })),
    fallback: false,
  };
};

export const getStaticProps = async ({ params }) => {
  const hasura = hasuraUserClient();
  const { id } = params;

  const initialData = await hasura.request(GetProjectByID, { id });

  return {
    props: {
      initialData,
    },
    revalidate: 1,
  };
};
const UpdateProject = gql`
  mutation update_Candidate_by_pk(
    $id: Int!
    $Title: String!
    $Description: String!
    $company_name: String!
    $type: String!
    $income: String!
  ) {
    update_Project_by_pk(
      pk_columns: { id: $id }
      _set: {
        Title: $Title
        Description: $Description
        company_name: $company_name
        type: $type
        income: $income
      }
    ) {
      id
      Title
      Description
      created_at
      delivery_date
      assigned
      contact_date
      company_name
      type
      income
    }
  }
`;
export default function ProjectEdit({ initialData }) {
  const hasura = hasuraUserClient();
  const { query } = useRouter();
  const router = useRouter();
  const { isAuthenticated, user } = useAuthState();
  const [File, setFile] = useState(null);

  useEffect(() => {
    if (!isAuthenticated) router.push("/");
  }, [isAuthenticated]);

  const { id } = query;
  const {
    handleSubmit,
    register,
    control,
    errors,
    formState: { isSubmitting },
    defaultValues: { ...defaultValues },
  } = useForm();
  const { data, mutate } = useSWR(
    [GetProjectByID, id],
    (query, id) => hasura.request(query, { id }),
    {
      initialData,
      revalidateOnMount: true,
    }
  );
  const uploadFileToClient = (event) => {
    if (event.target.files && event.target.files[0]) {
      const i = event.target.files[0];
      setFile(i);
      setCreateObjectURL(URL.createObjectURL(i));
    }
  };
  const uploadFileToServer = async () => {
    const body = new FormData();
    body.append("file", File);
    const response = await fetch("/api/file/", {
      method: "POST",
      body,
    });

    const UploadFile = gql`
      mutation MyMutation($File: String!, $id: Int!) {
        update_User_by_pk(pk_columns: { id: $id }, _set: { File: $File }) {
          File
        }
      }
    `;
    try {
      const { update_Project_by_pk } = await hasura.request(UploadFile, {
        id,
        File: File.name,
      });
      updateUser(update_Project_by_pk);
      window.location.reload();
    } catch (error) {
      console.error(error);
    }
    return;
  };
  const onSubmit = async ({
    Title,
    Description,
    company_name,
    income,
    type,
  }) => {
    try {
      const id = data.Project_by_pk.id;
      console.log(id);
      const { update_Project_by_pk } = await hasura.request(UpdateProject, {
        id,
        Title,
        Description,
        company_name,
        income,
        type,
      });

      mutate({
        ...data,
        ...update_Project_by_pk,
      });
      router.push(`/Project/List`);
    } catch (err) {
      console.log(err);
    }
  };
  const addproject = () => {
    const pathname = `/Project/List`;
    location.replace(pathname);
  };
  return (
    <div className="page-content-wrapper">
      <div className="page-content" style={{ minHeight: 1500 }}>
        <div class="profile-content">
          <div class="row">
            <div class="col-md-12">
              <div class="portlet light ">
                <div class="portlet-body">
                  <div class="row">
                    {" "}
                    <div class="col-md-11">
                      {" "}
                      <h3 className="caption-subject font-blue-madison bold uppercase">
