import { useRouter } from "next/router";
import useSWR from "swr";
import { useForm, Controller } from "react-hook-form";

import Layout from "../Components/Layout";

import { gql, hasuraUserClient } from "../../lib/hasura-user-client";

const GetCategoryIds = gql`
  {
    Candidate {
      id
      Email
    }
  }
`;

const GetCategoryById = gql`
  query GetCategoryById($id: Int!) {
    Candidate_by_pk(id: $id) {
      id
      Email
    }
  }
`;
export const getStaticPaths = async () => {
  const hasura = hasuraUserClient();

  const { Candidate } = await hasura.request(GetCategoryIds);

  return {
    paths: Candidate.map(({ id }) => ({
      params: {
        id: id.toString(),
      },
    })),
    fallback: false,
  };
};

export const getStaticProps = async ({ params }) => {
  const hasura = hasuraUserClient();
  const { id } = params;
  console.log(id);
  const initialData = await hasura.request(GetCategoryById, { id });

  return {
    props: {
      initialData,
    },
    revalidate: 1,
  };
};

export default function CategoryPage({ initialData }) {
  const hasura = hasuraUserClient();
  const { query } = useRouter();
  const { id } = query;
  const {
    handleSubmit,
    register,
    control,
    errors,
    formState: { isSubmitting },
  } = useForm();
  const { data } = useSWR(
    [GetCategoryById, id],
    (query, id) => hasura.request(query, { id }),
    {
      initialData,
      revalidateOnMount: true,
    }
  );
  const onSubmit = async ({
    Email,
    Last_Name,
    Name,
    domain,
    id,
    phone_number,
    Status,
  }) => {
    const { update_Candidate_by_pk } = await hasura.request(UpdatePost, {
      Email,
      Last_Name,
      Name,
      domain,
      id,
      phone_number,
      Status,
    });
    const UpdatePost = gql`
      mutation update_Candidate_by_pk(
        $id: Int!
        $message: String!
        $Email: String!
        $Last_Name: String!
        $Name: String!
        $domain: String!
        $phone_number: String!
      ) {
        update_Candidate_by_pk(
          pk_columns: { id: $id }
          _set: {
            Email: $Email
            Last_Name: $Last_Name
            Name: $Name
            domain: $domain
            phone_number: $phone_number
          }
        ) {
          Email
          Last_Name
          Name
          domain
          id
          phone_number
          Status
        }
      }
    `;

    try {
      await graphQLClient.request(UpdatePost, variables);
      Router.push("/");
    } catch (error) {
      console.error(error);
      setErrorMessage(error.message);
    }
  };
  return (
    <Layout>
      <div className="page-content-wrapper">
        <div className="page-content" style={{ minHeight: 1500 }}>
          <div class="profile-content">
            <div class="row">
              <div class="col-md-12">
                <div class="portlet light ">
                  <div class="portlet-body">
                    <div class="tab-content">
                      <div class="tab-pane active" id="tab_1_1">
                        <form onSubmit={handleSubmit(onSubmit)}>
                          <div class="form-group">
                            <label class="control-label">First Name</label>
                            <input
                              name="Name"
                              id="Name"
                              type="text"
                              placeholder="First Name"
                              class="form-control"
                              ref={register({
                                required:
                                  "You must select a category for your thread",
                              })}
                            />{" "}
                          </div>
                          <div class="form-group">
                            <label class="control-label">Last Name</label>
                            <input
                              name="Last_Name"
                              id="Last_Name"
                              type="text"
                              placeholder="Last Name"
                              class="form-control"
                              ref={register({
                                required:
                                  "You must select a category for your thread",
                              })}
                            />{" "}
                          </div>
                          <div class="form-group">
                            <label class="control-label">Mobile Number</label>
                            <input
                              name="phone_number"
                              id="phone_number"
                              type="text"
                              placeholder="+1 646 580 DEMO (6284)"
                              class="form-control"
                              ref={register({
                                required:
                                  "You must select a category for your thread",
                              })}
                            />{" "}
                          </div>
                          <div class="form-group">
                            <label class="control-label">Email</label>
                            <input
                              name="Email"
                              id="Email"
                              type="email"
                              placeholder={data.Candidate_by_pk.Email}
                              class="form-control"
                              ref={register({
                                required:
                                  "You must select a category for your thread",
                              })}
                            />{" "}
                          </div>
                          <div class="form-group">
                            <label class="control-label">Domain</label>
                            <input
                              name="domain"
                              id="domain"
                              type="text"
                              placeholder="Web Developer"
                              class="form-control"
                              ref={register({
                                required:
                                  "You must select a category for your thread",
                              })}
                            />{" "}
                          </div>
                          <div class="margiv-top-10">
                            <button
                              type="submit"
                              class="btn green"
                              disabled={isSubmitting}
                            >
                              {" "}
                              Save Changes{" "}
                            </button>
                            <a href="#" class="btn default">
                              {" "}
                              Cancel{" "}
                            </a>
                          </div>
                        </form>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>{" "}
      </div>
    </Layout>
  );
}
