import { useEffect } from "react";
import { useRouter } from "next/router";
import { useForm } from "react-hook-form";
import { hasuraAdminClient, gql } from "../lib/hasura-admin-client";

import { useAuthState, useAuthDispatch } from "../context/auth";
const created_at = new Date().toISOString();
const GetUserByEmail = gql`
  query GetUserByEmail($email: String!) {
    User(where: { email: { _eq: $email } }) {
      id
    }
  }
`;
export default function RegisterPage() {
  const router = useRouter();
  const { isAuthenticated } = useAuthState();
  const { register: createUser } = useAuthDispatch();
  const {
    handleSubmit,
    register,
    errors,
    formState: { isSubmitting },
    setError,
  } = useForm({ defaultValues: { save_last_seen: true } });
  // useEffect(() => {
  //   if (isAuthenticated) {
  //     router.push("/");
  //   }
  // }, [isAuthenticated]);

  // if (isAuthenticated) return null;
  const onSubmit = async ({
    name,
    email,
    password,
    last_name,
    domain,
    phone_number,
  }) => {
    const {
      User: [foundUser],
    } = await hasuraAdminClient.request(GetUserByEmail, {
      email,
    });
    if (foundUser) {
      alert("invalid email");
    } else {
      try {
        await createUser({
          name,
          email,
          password,
          last_name,
          domain,
          phone_number,
          created_at,
        });
        // router.push("/UsersList");
        // await fetch("/api/emailhey", {
        //   method: "POST",
        //   headers: { "content-type": "application/json" },
        //   body: JSON.stringify({
        //     name,
        //     email,
        //     password,
        //   }),
        // });
      } catch ({ message }) {
        setError("email", {
          type: "manual",
          message,
        });
      }
    }
  };

  return (
    <div className="page-content-wrapper">
      <div className="page-content" style={{ minHeight: 1500 }}>
        {" "}
        <form class="register-form" onSubmit={handleSubmit(onSubmit)}>
          <div class="content">
            {" "}
            <h3 class="font-green">Add User</h3>{" "}
            <div class="form-group">
              <input
                class="form-control form-control-solid placeholder-no-fix"
                name="name"
                id="name"
                ref={register}
                placeholder="name"
              />
            </div>{" "}
            <div class="form-group">
              <input
                class="form-control form-control-solid placeholder-no-fix"
                name="last_name"
                id="last_name"
                ref={register}
                placeholder="last name"
              />
            </div>
            {errors.name && <span>{errors.name.message}</span>}
            <div class="form-group">
              {" "}
              <input
                class="form-control form-control-solid placeholder-no-fix"
                type="email"
                name="email"
                id="email"
                ref={register({
                  required: "You must provide a email.",
                  pattern: {
                    value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                    message: "You must provide a valid email",
                  },
                })}
                placeholder="Your email"
              />
              {errors.email && <span>{errors.email.message}</span>}
            </div>
            <div class="form-group">
              {" "}
              <input
                class="form-control form-control-solid placeholder-no-fix"
                type="password"
                name="password"
                id="password"
                ref={register({
                  required: "You must provide a password.",
                  minLength: {
                    message: "Your password must be at least 6 characters",
                    value: 6,
                  },
                })}
                placeholder="Choose a password"
              />
              {errors.password && <span>{errors.password.message}</span>}
            </div>
            <div class="form-group">
              <input
                class="form-control form-control-solid placeholder-no-fix"
                name="domain"
                id="domain"
                ref={register}
                placeholder="domain"
              />
            </div>{" "}
            <div class="form-group">
              <input
                class="form-control form-control-solid placeholder-no-fix"
                name="phone_number"
                id="phone_number"
                ref={register}
                placeholder="Phone number"
              />
            </div>{" "}
            {/* <div class="form-group">
            {" "}
            <label
              htmlFor="save_last_seen"
              class="rememberme check mt-checkbox mt-checkbox-outline"
            >
              <input
                type="checkbox"
                id="save_last_seen"
                name="save_last_seen"
                ref={register}
              />
              Show as online to other users<span></span>
            </label>
          </div> */}
            <div class="create-account">
              <button
                type="submit"
                disabled={isSubmitting}
                class="btn green uppercase"
              >
                Create account
              </button>
            </div>{" "}
          </div>
        </form>
      </div>
    </div>
  );
}
