import { Button, Alert } from "react-bootstrap";
import Link from "next/link";
import gql from "graphql-tag";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { format, intervalToDuration, parseISO, set } from "date-fns";
import FullCalendar from "@fullcalendar/react"; // must go before plugins
import dayGridPlugin from "@fullcalendar/daygrid";
import "@fullcalendar/common/main.css";
import "@fullcalendar/daygrid/main.css";
import "@fullcalendar/timegrid/main.css";
import "@fullcalendar/list/main.css";
import { TextField } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { useAuthState, useAuthDispatch } from "../../context/auth";
import interactionPlugin from "@fullcalendar/interaction";
import timeGridPlugin from "@fullcalendar/timegrid";
import listPlugin from "@fullcalendar/list";
import { hasuraUserClient } from "../../lib/hasura-user-client";
const today = new Date().toISOString().substring(0, 19);
const mindate = new Date().toISOString().substring(0, 10);
import { differenceInHours } from "date-fns";
import { differenceInDays } from "date-fns";
// import Alert from "@material-ui/lab/Alert";
import AlertTitle from "@material-ui/lab/AlertTitle";
import { isWithinInterval } from "date-fns";
import styled from "styled-components";

export default function leaveupdate({ initialData, id, DataCount, Count }) {
  return (
    <div className="page-content-wrapper">
      <div className="page-content" style={{ minHeight: 1500 }}>
        {/* <h1 class="page-title"> Calendar</h1> */}

        <div class="portlet light portlet-fit bordered calendar">
          <div class="portlet-title">
            <div class="caption">
              <i class=" icon-layers font-green"></i>
              <span class="caption-subject font-green sbold uppercase">
                Holidays
              </span>
            </div>
          </div>
          <div class="portlet-body " style={{ paddingTop: 45 }}>
            <div class="row">
              <div class="col-md-3 ">
                {20 - sumLeave <= 0 ? (
                  <div
                    style={{
                      display: hide ? "block" : "none",
                    }}
                  >
                    {" "}
                    <div class="alert alert-danger fade in">
                      {" "}
                      <strong>Error!</strong>
                      <p>
                        you have no days remaining on your calendar , feel free
                        to contact the administration .{" "}
                      </p>
                      <span
                        style={{
                          cursor: "pointer",
                        }}
                        onClick={toggle}
                      >
                        {" "}
                        <strong>Close </strong>
                      </span>{" "}
                    </div>{" "}
                    <br />{" "}
                  </div>
                ) : (
                  <div>
                    <div
                      style={{
                        display: hide ? "block" : "none",
                      }}
                    >
                      <div class="alert alert-warning alert-dismissible fade in">
                        {" "}
                        <strong>Warning!</strong>
                        <p>
                          {" "}
                          you have <strong>{20 - sumLeave} </strong>days remaing
                          in this year , please feel free to contact us if you
                          got an urgent situation for leave. have a nice day!{" "}
                        </p>
                        <span
                          style={{
                            cursor: "pointer",
                          }}
                          onClick={toggle}
                        >
                          {" "}
                          <strong>Close </strong>
                        </span>{" "}
                      </div>{" "}
                      <br />
                    </div>{" "}
                    <div id="external-events">
                      <form onSubmit={handleSubmit(onSubmit)}>
                        {" "}
                        <div class="form-group">
                          <strong>Reason:</strong>

                          <input
                            type="textarea"
                            name="reason"
                            class="form-control"
                            placeholder="Reason?..."
                            id="reason"
                            ref={register({
                              required:
                                "You must provide a reason in order to move forward",
                            })}
                          />
                          {errors.reason && (
                            <span>{errors.reason.message}</span>
                          )}
                        </div>{" "}
                        <div class="form-group">
                          Medical Issue?{" "}
                          <span
                            style={{
                              cursor: "pointer",
                            }}
                            onClick={togglefile}
                          >
                            {" "}
                            <strong>add proof </strong>
                          </span>{" "}
                        </div>
                        <div
                          class="form-group"
                          style={{
                            display: hidef ? "block" : "none",
                          }}
                        >
                          <input type="file" />
                        </div>
                        <div class="form-group">
                          <label>start day</label> <br />
                          <br />{" "}
                          <div class="form-group">
                            <TextField
                              id="start_date"
                              name="start_date"
                              type="date"
                              defaultValue={today}
                              className={classes.textField}
                              inputRef={register}
                              InputLabelProps={{
                                shrink: true,
                              }}
                              inputProps={{
                                min: `${mindate}`,
                              }}
                              required
                            />{" "}
                          </div>
                          <div class="form-group">
                            <label>end day</label>
                            <br />
                            <br />
                            <TextField
                              id="end_date"
                              name="end_date"
                              type="date"
                              defaultValue={today}
                              className={classes.textField}
                              inputRef={register}
                              InputLabelProps={{
                                shrink: true,
                              }}
                              inputProps={{ min: `${mindate}` }}
                              required
                            />
                          </div>
                        </div>
                        <div class="form-group">
                          <button
                            type="submit"
                            class="btn green"
                            disabled={isSubmitting}
                          >
                            {" "}
                            submit demand{" "}
                          </button>
                        </div>
                      </form>
                      <hr />
                      <div>
                        {" "}
                        <table class="table table-hover">
                          <thead>
                            <tr>
                              <th> Color</th>
                              <th>Status</th>
                            </tr>
                          </thead>
                          <tbody>
                            <tr>
                              <td>
                                {" "}
                                <div
                                  style={{
                                    width: 50,
                                    height: 50,
                                    objectFit: "cover",
                                    backgroundColor: "#58D68D",
                                  }}
                                />
                              </td>{" "}
                              <td>accepted</td>{" "}
                            </tr>
                            <tr>
                              <td>
                                {" "}
                                <div
                                  style={{
                                    width: 50,
                                    height: 50,
                                    objectFit: "cover",
                                    backgroundColor: "#F39C12",
                                  }}
                                />
                              </td>
                              <td> pending</td>
                            </tr>
                            <tr>
                              <td>
                                {" "}
                                <div
                                  style={{
                                    width: 50,
                                    height: 50,
                                    objectFit: "cover",
                                    backgroundColor: "#E74C3C",
                                  }}
                                />
                              </td>
                              <td>rejected</td>
                            </tr>
                          </tbody>
                        </table>
                      </div>
                      <hr class="visible-xs" />{" "}
                    </div>
                  </div>
                )}
                <div>
                  {" "}
                  Total leave days:{sumLeave}
                  <table class="table table-hover">
                    <thead>
                      <tr>
                        {" "}
                        <th> edit</th>
                        <th> reason</th>
                        <th>start date</th>
                        <th>end date</th>
                        <th>Status</th>
                      </tr>
                    </thead>
                    <tbody>
                      {data.leave.map(
                        ({ id, reason, start_date, end_date, status }) => {
                          const end = format(parseISO(end_date), "yyyy-MM-dd");
                          const start = format(
                            parseISO(start_date),
                            "yyyy-MM-dd"
                          );
                          return (
                            <tr key={id} class="odd gradeX">
                              {" "}
                              <td>
                                {status === 1 ? (
                                  <span class="label label-sm label-success">
                                    {" "}
                                    nothing to edit
                                  </span>
                                ) : status === 2 ? (
                                  <span class="label label-sm label-success">
                                    {" "}
                                    nothing to edit
                                  </span>
                                ) : (
                                  <a
                                    className=" btn-sm btn-warning"
                                    href={`/updateleave/${id}`}
                                  >
                                    Edit
                                  </a>
                                )}
                              </td>
                              <td>{reason}</td>
                              <td>{start_date}</td>
                              <td>{end_date}</td>{" "}
                              <td>
                                {status === 1 ? (
                                  <span class="label label-sm label-success">
                                    {" "}
                                    Approved{" "}
                                  </span>
                                ) : status === 2 ? (
                                  <span class="label label-sm label-danger">
                                    {" "}
                                    Declined{" "}
                                  </span>
                                ) : (
                                  <button
                                    className=" btn-sm btn-warning"
                                    onClick={() => cancelrequest(id)}
                                  >
                                    Cancel
                                  </button>
                                )}
                              </td>
                            </tr>
                          );
                        }
                      )}
                    </tbody>
                  </table>
                </div>
              </div>
              <div class="col-md-9">
                <FullCalendar
                  plugins={[
                    dayGridPlugin,
                    timeGridPlugin,
                    interactionPlugin,
                    listPlugin,
                  ]}
                  headerToolbar={{
                    left: "prev,next today",
                    center: "title",
                    right: "dayGridMonth,timeGridWeek,timeGridDay,listWeek",
                  }}
                  initialView="dayGridMonth"
                  editable={true}
                  selectable={true}
                  selectMirror={true}
                  dayMaxEvents={true}
                  weekNumbers={true}
                  themeSystem="bootstrap"
                  events={data.leave.map(
                    ({ id, reason, start_date, end_date, status }) => {
                      const end = format(parseISO(end_date), "yyyy-MM-dd");
                      const start = format(parseISO(start_date), "yyyy-MM-dd");
                      if (status === 1) {
                        return {
                          id: id,
                          start: start,
                          end: end,
                          backgroundColor: "#58D68D",
                          display: "background",
                        };
                      } else if (status === 0) {
                        return {
                          id: id,
                          start: start,
                          end: end,
                          backgroundColor: "#F39C12",
                          display: "background",
                        };
                      } else {
                        return {
                          id: id,
                          start: start,
                          end: end,
                          backgroundColor: "#E74C3C",
                          display: "background",
                        };
                      }
                    }
                  )}
                  dateClick={handleDateClick}
                  // initialEvents={INITIAL_EVENTS} // alternatively, use the `events` setting to fetch from a feed
                  // select={this.handleDateSelect}
                  // eventContent={renderEventContent} // custom render function
                  // eventsSet={this.handleEvents} // called after events are initialized/added/changed/removed
                  /* you can update a remote database when these fire:
              eventAdd={function(){}}
              eventChange={function(){}}
              // */
                  //       validRange={{
                  //         start: "2021-08-03",
                  //         end: "2021-08-07",
                  //       }}
                  weekends={false}
                  // select={color}
                />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
