import { useState } from "react";
import { useRouter } from "next/router";
import { hasuraUserClient } from "../../lib/hasura-user-client";
import { hasuraAdminClient, gql } from "../../lib/hasura-admin-client";
import useSWR, { mutate } from "swr";
import { useAuthState, useAuthDispatch } from "../../context/auth";
import { useForm, Controller } from "react-hook-form";

const GetUsersId = gql`
  {
    User {
      id
      Name
      Avatar
      email
      Role
    }
  }
`;

const GetUserById = gql`
  query GetUserById($id: Int!) {
    User_by_pk(id: $id) {
      id
      Name
      Avatar
      email
      Role
    }
  }
`;

const CountTasksByUserId = gql`
  query MyQuery($_eq: Int!) {
    Todo_aggregate(where: { user_id: { _eq: $_eq } }) {
      aggregate {
        count(columns: Completed)
      }
    }
  }
`;
//tobedone
//Countprojects
export const getStaticPaths = async () => {
  const hasura = hasuraUserClient();

  const { User } = await hasura.request(GetUsersId);

  return {
    paths: User.map(({ id }) => ({
      params: {
        id: id.toString(),
      },
    })),
    fallback: false,
  };
};

export const getStaticProps = async ({ params }) => {
  const hasura = hasuraUserClient();
  const { id } = params;
  const initialData = await hasura.request(GetUserById, { id });

  return {
    props: {
      initialData,
    },
    revalidate: 1,
  };
};
const UpdateUser = gql`
  mutation update_User_by_pk(
    $id: Int!
    $email: String!
    $Name: String!
    $password: String!
  ) {
    update_User_by_pk(
      pk_columns: { id: $id }
      _set: { email: $email, Name: $Name, password: $password }
    ) {
      email
      Name
      password
    }
  }
`;
export default function PrivatePage({ initialData }) {
  const [image, setImage] = useState(null);
  const { updateUser } = useAuthDispatch();

  const [createObjectURL, setCreateObjectURL] = useState(null);
  const router = useRouter();
  const { id } = router.query;
  const {
    handleSubmit,
    register,
    control,
    errors,
    formState: { isSubmitting },
    defaultValues: { ...defaultValues },
  } = useForm();
  const uploadToClient = (event) => {
    if (event.target.files && event.target.files[0]) {
      const i = event.target.files[0];
      setImage(i);
      setCreateObjectURL(URL.createObjectURL(i));
    }
  };

  const uploadToServer = async (event) => {
    const hasura = hasuraUserClient();
    const body = new FormData();
    body.append("file", image);
    const response = await fetch("/api/file/", {
      method: "POST",
      body,
    });
    const Avatar = image.name;
    const UploadImage = gql`
      mutation MyMutation($Avatar: String!, $id: Int!) {
        update_User_by_pk(pk_columns: { id: $id }, _set: { Avatar: $Avatar }) {
          Avatar
          Name
        }
      }
    `;
    try {
      const { c } = await hasura.request(UploadImage, { id, Avatar });

      mutate();

      window.location.reload();
    } catch (error) {
      console.error(error);
    }
    return;
  };
  const { data, mutate } = useSWR(
    [GetUserById, id],
    (query, id) => hasura.request(query, { id }),
    {
      updateUser,
      initialData,
      revalidateOnMount: true,
    }
  );
  const onSubmit = async ({ id, email, Name }) => {
    try {
      const id = data.User_by_pk.id;
      console.log(id);
      const { update_Candidate_by_pk } = await hasura.request(UpdatePost, {
        id,
        Email,
        Last_Name,
        Name,
        domain,
        phone_number,
      });

      mutate({
        ...data,
        ...update_Candidate_by_pk,
      });
      router.push(`/CandidatList`);
    } catch (err) {
      console.log(err);
    }
    useEffect(() => {
      reset(defaultValues); // asynchronously reset your form values
    }, [reset, defaultValues]);
  };
  return (
    <div className="page-content-wrapper">
      <div className="page-content" style={{ minHeight: 1500 }}>
        <div className="row">
          <div className="col-md-12">
            <div className="col-md-6">
              <div className="profile-sidebar">
                <div className="portlet light profile-sidebar-portlet ">
                  <div className="profile-userpic" style={{ display: "block" }}>
                    <img
                      style={{ width: 100 }}
                      src={`../../${data.User_by_pk.Avatar}`}
                      class="img-fluid img-thumbnail "
                      alt=""
                    />{" "}
                  </div>
                  <div className="portlet light ">
