import { useRouter } from "next/router";
import useSWR from "swr";
import { useForm } from "react-hook-form";
import Link from "next/link";
import { gql, hasuraUserClient } from "../../lib/hasura-user-client";
import { useAuthState, useAuthDispatch } from "../../context/auth";
import { useEffect } from "react";
import { TextField } from "@material-ui/core";
import { Form } from "react-bootstrap";

const today = new Date().toISOString().substring(0, 19);

const getProjectsIds = gql`
  {
    Project {
      id
      Description
      created_at
      Title
      delivery_date
      assigned
      contact_date
      company_name
      additional_inf
      type
      file
      income
    }
  }
`;

const GetProjectByID = gql`
  query GetProjectByID($id: Int!) {
    Project_by_pk(id: $id) {
      id
      Description
      created_at
      Title
      delivery_date
      assigned
      contact_date
      company_name
      additional_inf
      type
      file
      income
    }
  }
`;
const GetProjectByUser = gql`
  query User($id: Int!) {
    User(where: { project_id: { _is_null: true } }) {
      id
      Name
    }
  }
`;
export const getStaticPaths = async () => {
  const hasura = hasuraUserClient();

  const { Project } = await hasura.request(getProjectsIds);

  return {
    paths: Project.map(({ id }) => ({
      params: {
        id: id.toString(),
      },
    })),
    fallback: false,
  };
};

export const getStaticProps = async ({ params }) => {
  const hasura = hasuraUserClient();
  const { id } = params;

  const initialData = await hasura.request(GetProjectByID, { id });

  return {
    props: {
      initialData,
    },
    revalidate: 1,
  };
};
const UpdateProject = gql`
  mutation update_Candidate_by_pk(
    $id: Int!
    $Title: String!
    $Description: String!
    $company_name: String!
    $created_at: String!
    $delivery_date: String!
    $assigned: String!
    $contact_date: String!
    $company_name: String!
    $additional_inf: String!
    $type: String!
    $file: String!
    $income: String!
  ) {
    update_Project_by_pk(
      pk_columns: { id: $id }
      _set: {
        Title: $Title
        Description: $Description
        company_name: $company_name
        created_at: $created_at
        Title: $Title
        delivery_date: $delivery_date
        assigned: $assigned
        contact_date: $contact_date
        company_name: $company_name
        additional_inf: $additional_inf
        type: $type
        file: $file
        income: $income
      }
    ) {
      id
      Title
      Description
      created_at
      delivery_date
      assigned
      contact_date
      company_name
      additional_inf
      type
      file
      income
    }
  }
`;
