import useSWR, { mutate } from "swr";

import gql from "graphql-tag";
import Link from "next/link";
import { hasuraUserClient } from "../../lib/hasura-user-client";
import { format, parseISO } from "date-fns";
import FullCalendar from "@fullcalendar/react"; // must go before plugins
import dayGridPlugin from "@fullcalendar/daygrid";
import "@fullcalendar/common/main.css";
import "@fullcalendar/daygrid/main.css";
import "@fullcalendar/timegrid/main.css";
import "@fullcalendar/list/main.css";
import resourceTimelinePlugin from "@fullcalendar/resource-timeline";
import interactionPlugin from "@fullcalendar/interaction";
import timeGridPlugin from "@fullcalendar/timegrid";
import listPlugin from "@fullcalendar/list";
import { TextField } from "@material-ui/core";
import { registerLocale } from "react-datepicker";
import { useForm } from "react-hook-form";
const today = new Date();
const GetProjects = gql`
  query MyQuery {
    Project {
      id
      Title
      Description
      created_at
    }
  }
`;
export const getStaticProps = async () => {
  const hasura = hasuraUserClient();

  const initialData = await hasura.request(GetProjects);

  return {
    props: {
      initialData,
    },
    revalidate: 1,
  };
};

export default function MembersPage({ initialData }) {
  const hasura = hasuraUserClient();
  console.log(today);
  const {
    handleSubmit,
    register,
    control,
    errors,
    formState: { isSubmitting },
  } = useForm();
  const { data } = useSWR(GetProjects, (query) => hasura.request(query), {
    initialData,
    revalidateOnMount: true,
  });
  const deleteProject = async (id) => {
    const query = gql`
      mutation delete_Project($id: Int!) {
        delete_Project(where: { id: { _eq: $id } }) {
          affected_rows
        }
      }
    `;

    try {
      await hasura.request(query, { id });
      mutate();
      window.location.reload();
    } catch (error) {
      console.error(error);
    }
  };
  const handleDateClick = (arg) => {
    // bind with an arrow function
    alert(arg.dateStr);
  };
  const onSubmit = async ({
    Description,
    Title,
    company_name,
    additional_inf,
    type,
    income,
  }) => {
