import React from "react";
import Header from "./Header/Header";
import Footer from "./Footer/Footer";
import Sidebar from "./Sidebar/Sidebar";
import gql from "graphql-tag";
import { hasuraUserClient } from "../../lib/hasura-user-client";

const Notification_count = gql`
  query MyQuery {
    notification_aggregate {
      aggregate {
        count(columns: id)
      }
    }
  }
`;
export const getStaticProps = async () => {
  const hasura = hasuraUserClient();

  const initialData = await hasura.request(Notification_count);
  const c = initialData.notification_aggregate.aggregate.count;
  console.log(c);

  return {
    props: {
      initialData,
      c,
    },
    revalidate: 1,
  };
};
export default function Layout({ children, c }) {
  return (
    <>
      {" "}
      <div class="page-container">
        <Header />
        <Sidebar />
        {children}
        <Footer />
      </div>
    </>
  );
}
