import { FormControl,FormLabel,Button } from "react-bootstrap";
import { useForm } from "react-hook-form";

export default function MembersPage({ initialData }) {
  const {
    handleSubmit,
    register,
    control,
    errors,
    formState: { isSubmitting },
  } = useForm();
  const sendMail = async (data) => {

    try {
      await fetch("/api/contact", {
        "method": "POST",
        "headers": { "content-type": "application/json" },
        "body": JSON.stringify(data)
      })

            //if sucess do whatever you like, i.e toast notification
      setTimeout(() => reset(), 2000);
    } catch (error) {
        // toast error message. whatever you wish 
    }

  }
  return (
<form onSubmit={handleSubmit(sendMail)}>
  <div>

    <FormControl >
      <FormLabel htmlFor="subject">Subject</FormLabel>
      <input id='subject' type="text" name="subject"     ref={register({
                                required:
                                  "You must select a category for your thread",
                              })} />
    </FormControl>

    <FormControl>
      <FormLabel htmlFor="name" >Name</FormLabel>
      <input id='name' type="name" name="name"    ref={register({
                                required:
                                  "You must select a category for your thread",
                              })} />
    </FormControl>

    <FormControl>
      <FormLabel htmlFor="email" >Email address</FormLabel>
      <input id='email' type="email" name="email"     ref={register({
                                required:
                                  "You must select a category for your thread",
                              })}  />
    </FormControl>

    <FormControl>
      <FormLabel htmlFor="message">Message</FormLabel>
      <textarea id='message' type='textarea' name="message"     ref={register({
                                required:
                                  "You must select a category for your thread",
                              })} h={300} resize='none' />
    </FormControl>

    <FormControl>
      <Button type='submit' color='white' bg='gray.900' width='100%' h={55} mt={5}>
        Submit
        </Button>
    </FormControl>
   </div>
</form>
  );
}
