import { useRouter } from "next/router";
import useSWR from "swr";
import { useForm } from "react-hook-form";
import Link from "next/link";
import { gql, hasuraUserClient } from "../../lib/hasura-user-client";
import { Form } from "react-bootstrap";

const from = new Date().toISOString();

const getProjectsIds = gql`
  {
    Project {
      id
      Title
      Description
    }
  }
`;
const GetUsers = gql`
  query MyQuery {
    User {
      id
      Name
      Domain
      Avatar
    }
  }
`;
const insertmultiple = gql`
  mutation MyMutation(
    $Description: String!
    $Project_id: Int!
    $name: String!
    $from: timestamptz!
  ) {
    insert_Task(
      objects: {
        Date: $from
        Description: $Description
        completed: false
        Project_id: $Project_id
        name: $name
      }
    ) {
      affected_rows
    }
  }
`;
const GetUsersbyproject = gql`
  query MyQuery($id: Int!) {
    User(
      where: {
        _not: { project_assignments: { Task: { Project_id: { _eq: $id } } } }
      }
    ) {
      Name
      Avatar
      Last_Name
      email
    }
  }
`;
const getTasks = gql`
  query MyQuery($id: Int!) {
    Task(where: { Project_id: { _eq: $id } }) {
      Description
      Date
      completed
      name
    }
  }
`;

const GetUsersWorkingOnthisProject = gql`
  query MyQuery($id: Int!) {
    User(
      where: { project_assignments: { Task: { Project_id: { _eq: $id } } } }
    ) {
      id
      Name
      Domain
      Avatar
    }
  }
`;
const GetProjectByID = gql`
  query GetProjectByID($id: Int!) {
    Project_by_pk(id: $id) {
      id
      Title
      Description
      company_name
      income
    }
  }
`;
const assignu = gql`
  mutation MyMutation($project_id: Int!, $user_id: Int!) {
    insert_user_project_one(
      object: { project_id: $project_id, user_id: $user_id }
    ) {
      project_id
      user_id
    }
  }
`;
export const getStaticPaths = async () => {
  const hasura = hasuraUserClient();

  const { Project } = await hasura.request(getProjectsIds);

  return {
    paths: Project.map(({ id }) => ({
      params: {
        id: id.toString(),
      },
    })),
    fallback: false,
  };
};

export const getStaticProps = async ({ params }) => {
  const hasura = hasuraUserClient();
  const { id } = params;

  const initialData = await hasura.request(GetProjectByID, { id });
  const dataU = await hasura.request(GetUsers);
  const dataz = await hasura.request(GetUsersbyproject, { id });
  const dataf = await hasura.request(GetUsersWorkingOnthisProject, { id });
  const tasks = await hasura.request(getTasks, { id });

  return {
    props: {
      initialData,
      dataU,
      dataz,
      dataf,
      tasks,
    },
    revalidate: 1,
  };
};

export default function AssignP({ initialData, dataU, dataz, dataf }) {
  const hasura = hasuraUserClient();
  const { query } = useRouter();
  const router = useRouter();
  const { idP } = query;
  const {
    handleSubmit,
    register,
    control,
    errors,
    formState: { isSubmitting },
    defaultValues: { ...defaultValues },
  } = useForm();
  console.log(dataz.User);
  const assign = async ({ id }) => {
    console.log(id);
    if (
      confirm(`are you sure you want to assign the project to this employee? `)
    ) {
      try {
        const { insert_user_project_one } = await hasura.request(assignu, {
          project_id: query.id,
          user_id: id,
        });

        router.push(`/Project/List`);
      } catch (err) {
        console.log(err);
      }
    } else return null;
  };
  const onSubmit = async ({ Description, name, user_id }) => {
    console.log(from);
    try {
      const { insert_Task_one } = await hasura.request(insertmultiple, {
        Description,
        from,
        name,
        Project_id: query.id,
      });

      window.location.reload();
    } catch (err) {
      console.log(err);
    }
  };
  return (
    <div className="page-content-wrapper">
      <div className="page-content" style={{ minHeight: 1500 }}>
        <div class="portlet light portlet-fit bordered">
          <div className="portlet-title tabbable-line">
            <div className="caption caption-md">
              <i className="icon-globe theme-font hide"></i>
              <span className="caption-subject font-blue-madison bold uppercase">
                Profile Account
              </span>
            </div>
            <ul className="nav nav-tabs">
              <li className="active">
                <a href="#tab_1_1" data-toggle="tab">
                  Personal Info
                </a>
              </li>
              <li>
                <a href="#tab_1_2" data-toggle="tab">
                  Change Avatar
                </a>
              </li>
              <li>
                <a href="#tab_1_3" data-toggle="tab">
                  Change Password
                </a>
              </li>{" "}
              <li>
                <a href="#tab_1_4" data-toggle="tab">
                  Update CV
                </a>
              </li>
            </ul>
          </div>
          <div class="portlet-body" style={{ paddingBottom: 150 }}>
            <div class="pricing-content-1">
              <div class="row">
                <div class="col-md-3">
                  <div class="price-column-container border-active">
                    <div class="price-table-head bg-blue">
                      <h2 class="no-margin">Client</h2>
                    </div>
                    <div class="arrow-down border-top-blue"></div>
                    <div class="price-table-pricing">
                      <h3>
                        {/* <sup class="price-sign">£</sup> */}
                        {initialData.Project_by_pk.company_name}
                      </h3>
                    </div>
                    {/* <div class="price-table-content">
                      <div class="row mobile-padding">
                        <div class="col-xs-3 text-right mobile-padding">
                          <i class="icon-user"></i>
                        </div>
                        <div class="col-xs-9 text-left mobile-padding">
                          3 Members
                        </div>
                      </div>
                    </div> */}
                  </div>
                </div>
                <div class="col-md-3">
                  <div class="price-column-container border-active">
                    <div class="price-table-head bg-red">
                      <h2 class="no-margin">Title</h2>
                    </div>
                    <div class="arrow-down border-top-red"></div>
                    <div class="price-table-pricing">
                      <h3>{initialData.Project_by_pk.Title}</h3>
                    </div>
                  </div>
                </div>
                <div class="col-md-3">
                  <div class="price-column-container border-active">
                    <div class="price-table-head bg-green">
                      <h2 class="no-margin">Description</h2>
                    </div>
                    <div class="arrow-down border-top-green"></div>
                    <div class="price-table-pricing">
                      {/* <h4>{initialData.Project_by_pk.Description}</h4> */}
                    </div>
                  </div>
                </div>
                <div class="col-md-3">
                  <div class="price-column-container border-active">
                    <div class="price-table-head bg-purple">
                      <h2 class="no-margin">Files</h2>
                    </div>
                    <div class="arrow-down border-top-purple"></div>
                    <div class="price-table-pricing">
                      <h3>
                        <sup class="price-sign"></sup>
                        {/* {initialData.Project_by_pk.company_name} */}
                      </h3>
                    </div>
                    <div class="price-table-content"></div>
                    <div class="arrow-down arrow-grey"></div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>{" "}
        <div class="row">
          <div class="col-md-12">
            <div class="portlet light portlet-fit bordered">
              <div class="portlet-title">
                <div class="caption">
                  <i class=" icon-layers font-green"></i>
                  <span class="caption-subject font-green bold uppercase">
                    Task Management
                  </span>
                </div>
              </div>{" "}
              <div class="portlet-body">
                <div class="mt-element-card mt-card-round mt-element-overlay">
                  <div class="row">
                    {" "}
                    <div class="col-md-12">
                      <div class="portlet light ">
                        <div class="portlet-body">
                          <div class="tab-content">
                            <div class="tab-pane active" id="tab_1_1">
                              <form onSubmit={handleSubmit(onSubmit)}>
                                <div class="form-group">
                                  <label class="control-label">Name</label>
                                  <input
                                    name="name"
                                    id="name"
                                    type="text"
                                    placeholder="Todo Name"
                                    class="form-control"
                                    ref={register({
                                      required:
                                        "You must select a category for your thread",
                                    })}
                                  />{" "}
                                </div>
                                <div class="form-group">
                                  <label class="control-label">
                                    Description
                                  </label>
                                  <textarea
                                    name="Description"
                                    id="Description"
                                    type="textarea"
                                    placeholder="Description"
                                    class="form-control"
                                    ref={register({
                                      required:
                                        "You must select a category for your thread",
                                    })}
                                  />{" "}
                                </div>
                                {/* <select
                                  name="user_id"
                                  id="user_id"
                                  ref={register({
                                    required:
                                      "You must select a category for your thread",
                                  })}
                                  class="form-control select2"
                                  aria-label="Default select example"
                                >
                                  {dataz.User.map(({ id, Name }) => (
                                    <option value={id}>{Name}</option>
                                  ))}
                                </select>{" "} */}
                                <br></br>
                                <div class="margiv-top-10">
                                  <button
                                    type="submit"
                                    class="btn green"
                                    disabled={isSubmitting}
                                  >
                                    Submit
                                  </button>
                                </div>
                              </form>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>{" "}
        <div class="row">
          <div class="col-md-12">
            <div class="portlet light portlet-fit bordered">
              <div class="portlet-title">
                <div class="caption">
                  <i class=" icon-layers font-green"></i>
                  <span class="caption-subject font-green bold uppercase">
                    Suggested Employees
                  </span>
                </div>
              </div>{" "}
              <div class="portlet-body">
                <div class="mt-element-card mt-card-round mt-element-overlay">
                  <div class="row">
                    {" "}
                    {dataz.User.map(({ id, Name, Domain, Avatar }) => {
                      return (
                        <div
                          class="col-lg-3 col-md-4 col-sm-6 col-xs-12"
                          key={id}
                        >
                          <div class="mt-card-item">
                            <div class="mt-card-avatar mt-overlay-1">
                              {Avatar ? (
                                <img
                                  src={`../../${Avatar}`}
                                  style={{
                                    display: "inline",
                                    width: 150,
                                    height: 170,
                                    objectFit: "cover",
                                  }}
                                  alt=""
                                />
                              ) : (
                                <img
                                  src={`../profile.png`}
                                  style={{
                                    display: "inline",
                                    width: 150,
                                    height: 170,
                                    objectFit: "cover",
                                  }}
                                />
                              )}{" "}
                              <div class="mt-overlay">
                                <ul class="mt-info">
                                  <li>
                                    <Link
                                      class="btn default btn-outline"
                                      href={`/Profile/${id}`}
                                    >
                                      <i class="icon-magnifier"></i>
                                    </Link>
                                  </li>
                                </ul>
                              </div>
                            </div>{" "}
                            <div class="mt-card-content">
                              <h3 class="mt-card-name">{Name}</h3>
                              <p class="mt-card-desc font-grey-mint">
                                {Domain}
                              </p>{" "}
