import React from "react";
import Link from "next/link";
import { useAuthState, useAuthDispatch } from "../../../context/auth";
import Image from "react-bootstrap/Image";
import gql from "graphql-tag";
import useSWR, { mutate } from "swr";

import { hasuraUserClient } from "../../../lib/hasura-user-client";
const updateN = gql`
  mutation MyMutation($_eq: Int!) {
    update_notification(
      where: { user_id: { _eq: $_eq } }
      _set: { seen: true }
    ) {
      affected_rows
    }
  }
`;
const GetUserById = gql`
  query GetUserById($user_id: Int!) {
    User_by_pk(id: $user_id) {
      id
      notifications_aggregate(where: { seen: { _eq: false } }) {
        aggregate {
          count
        }
      }
      notifications(where: { seen: { _eq: false } }) {
        id
        description
      }
    }
  }
`;
const getNotifications = gql`
  query MyQuery {
    notification(where: { user_id: { _is_null: true } }) {
      description
      name
      seen
    }
    notification_aggregate(where: { user_id: { _is_null: true } }) {
      aggregate {
        count
      }
    }
  }
`;
export const getStaticProps = async () => {
  const hasura = hasuraUserClient();

  const initialData = await hasura.request(getNotifications);

  return {
    props: {
      initialData,
    },
    revalidate: 1,
  };
};
export default function Header({ initialData }) {
  const { logout } = useAuthDispatch();
  const { updateUser } = useAuthDispatch();
  const hasura = hasuraUserClient();
  const { isAuthenticated, user } = useAuthState();
  const UpdateUser = async () => {
    updateUser(user.notifications);
    console.log(user.notifications);
  };
  const { data } = useSWR(GetUsers, (query) => hasura.request(query), {
    initialData,
    revalidateOnMount: true,
  });
  const Complete = async () => {
    try {
      if (isAuthenticated) {
        const { update_notification } = await hasura.request(updateN, {
          _eq: user.id,
        });
        const { User_by_pk } = await hasura.request(GetUserById, {
          user_id: user.id,
        });
        updateUser(User_by_pk);
      }
      window.location.reload;
    } catch (err) {
      console.log(err);
    }
  };
  const submit = (id) => {
    const pathname = `/Task/${id}`;
    location.replace(pathname);
  };
  return (
    <div className="page-header navbar navbar-fixed-top ">
      {isAuthenticated ? (
        <div className="page-header navbar navbar-fixed-top ">
          <div className="page-header-inner ">
            <div className="page-logo">
              <Link href="/">
                <a href="#">
                  <div style={{ marginTop: -35 }}>
                    <img
                      src="../svg/AMAI-AI-Experts.inline.svg"
                      alt="logo"
                      className="logo-default"
                    />{" "}
                  </div>
                </a>
              </Link>
              <div className="menu-toggler sidebar-toggler ">
                <span></span>
              </div>
            </div>
            <a
              href="#"
              className="menu-toggler responsive-toggler"
              data-toggle="collapse"
              data-target=".navbar-collapse"
            >
              <span></span>
            </a>
            <div className="top-menu">
              <ul className="nav navbar-nav pull-right">
                <li
                  className="dropdown dropdown-extended dropdown-notification"
                  id="header_notification_bar"
                >
                  <a
                    href="#"
                    className="dropdown-toggle"
                    data-toggle="dropdown"
                    data-hover="dropdown"
                    data-close-others="true"
                  >
                    <i className="icon-bell"></i>
                    <span class="badge badge-default">
                      {user.notifications_aggregate.aggregate.count}
                    </span>
                  </a>
                  <ul className="dropdown-menu">
                    <li className="external">
                      <h3>
                        <span className="bold">
                          {user.notifications_aggregate.aggregate.count}
                          pending
                        </span>{" "}
                        notifications
                      </h3>
                      <a onClick={Complete}>view all</a>
                    </li>
                    <li>
                      <ul
                        className="dropdown-menu-list scroller"
                        data-handle-color="#637283"
                      >
                        {" "}
                        {user.notifications.map(({ id, description }) => {
                          return (
                            <li key={id}>
                              <a onClick={UpdateUser}>
                                <span className="time">just now</span>
                                <span className="details">
                                  {description}
                                  <span className="label label-sm label-icon label-success">
                                    <i className="fa fa-plus"></i>
                                  </span>{" "}
                                  {description}
                                </span>
                              </a>
                            </li>
                          );
                        })}
                      </ul>
                    </li>
                  </ul>
                </li>

                <li className="dropdown dropdown-user">
                  <a
                    href="#"
                    className="dropdown-toggle"
                    data-toggle="dropdown"
                    data-hover="dropdown"
                    data-close-others="true"
                  >
                    {" "}
                    <img
                      class="image_inner_containerHeader"
                      src={`../${user.Avatar}`}
                    />
                    <span className="username username-hide-on-mobile">
                      {" "}
                      {user.Name}{" "}
                    </span>
                    <i className="fa fa-angle-down"></i>
                  </a>
                  <ul className="dropdown-menu dropdown-menu-default">
                    <li>
                      <Link href={`../Profile/${user.id}`}>
                        <a>
                          <i className="icon-user"></i> My Profile{" "}
                        </a>
                      </Link>{" "}
                    </li>
                    <li>
                      <a onClick={() => submit(user.id)}>
                        <i className="icon-rocket"></i> My Tasks{" "}
                      </a>
                    </li>{" "}
                    <li></li>
                    <li>
                      <Link href="/Candidat">
                        <a>
                          <i className="icon-calendar"></i> My Calendar{" "}
                        </a>
                      </Link>
                    </li>
                    <li className="divider"> </li>
                    <li>
                      <a href="page_user_lock_1.html">
                        <i className="icon-lock"></i> Lock Screen{" "}
                      </a>
                    </li>
                    <Link href="/login">
                      <li>
                        <a href="page_user_login_1.html" onClick={logout}>
                          <i className="icon-key"></i> Log Out{" "}
                        </a>
                      </li>
                    </Link>
                  </ul>
                </li>
                <li className="dropdown dropdown-quick-sidebar-toggler">
                  <a href="#" className="dropdown-toggle">
