import useSWR, { mutate } from "swr";
import { useEffect } from "react";
import { useRouter } from "next/router";
import { useForm } from "react-hook-form";
import Link from "next/link";
import gql from "graphql-tag";

import { hasuraUserClient } from "../../lib/hasura-user-client";

const today = new Date();

const GetCandidats = gql`
  query MyQuery {
    Candidate {
      Email
      Last_Name
      Name
      domain
      id
      phone_number
      Status
    }
  }
`;

export const getStaticProps = async () => {
  const hasura = hasuraUserClient();

  const initialData = await hasura.request(GetCandidats);

  return {
    props: {
      initialData,
    },
    revalidate: 1,
  };
};

export default function MembersPage({ initialData }) {
  const hasura = hasuraUserClient();
  const router = useRouter();
  const {
    handleSubmit,
    register,
    control,
    errors,
    formState: { isSubmitting },
  } = useForm();
  const { data } = useSWR(GetCandidats, (query) => hasura.request(query), {
    initialData,
    revalidateOnMount: true,
  });
  const deleteATodo = async (id) => {
    const query = gql`
      mutation delete_Candidate($id: Int!) {
        delete_Candidate(where: { id: { _eq: $id } }) {
          affected_rows
        }
      }
    `;

    try {
      await hasura.request(query, { id });
      mutate();
      window.location.reload();
    } catch (error) {
      console.error(error);
    }
  };
  const updateStatus = async (id) => {
    const SetStatus = gql`
      mutation MyMutation($id: Int!) {
        update_Candidate_by_pk(pk_columns: { id: $id }, _set: { Status: 1 }) {
          id
          Status
        }
      }
    `;
    try {
      await hasura.request(SetStatus, { id });
      mutate();
      router.push(`/RegisterCandidatAccount/${id}`);
    } catch (error) {
      console.error(error);
    }
  };
  const DeclineCandidat = async (id) => {
    const SetStatus = gql`
      mutation MyMutation($id: Int!) {
        update_Candidate_by_pk(pk_columns: { id: $id }, _set: { Status: 0 }) {
          id
          Status
        }
      }
    `;
    try {
      await hasura.request(SetStatus, { id });
      mutate();
      window.location.reload();
    } catch (error) {
      console.error(error);
    }
  };
  const Modify = async ({ id }) => {
    console.log(id);
    router.push(`/CandidatOne/${id}`);
  };

  return (
    <div className="page-content-wrapper">
      <div className="page-content" style={{ minHeight: 1500 }}>
        <div class="profile-content">
          <div class="row">
            <div class="col-md-12">
              <div class="portlet light ">
                <div class="portlet-body">
                  <div class="tab-content">
                    <div class="tab-pane active" id="tab_1_1">
                      <form onSubmit={handleSubmit(onSubmit)}>
                        <div class="form-group">
                          <label class="control-label">Name</label>
                          <input
                            name="Name"
                            id="Name"
                            type="text"
                            placeholder="Todo Name"
                            class="form-control"
                            ref={register({
                              required:
                                "You must select a category for your thread",
                            })}
                          />{" "}
                        </div>
                        <div class="form-group">
                          <label class="control-label">Description</label>
                          <textarea
                            name="Description"
                            id="Description"
                            type="textarea"
                            placeholder="Description"
                            class="form-control"
                            ref={register({
                              required:
                                "You must select a category for your thread",
                            })}
                          />{" "}
                        </div>

                        <select
                          name="user_id"
                          id="user_id"
                          ref={register({
                            required:
                              "You must select a category for your thread",
                          })}
                          className="appearance-none bg-transparent w-full"
                        >
                          {User.map(({ id, Name }) => (
                            <React.Fragment key={id}>
                              <option value={id}>{Name}</option>
                            </React.Fragment>
                          ))}
                        </select>
                        <div class="margiv-top-10">
                          <button
                            type="submit"
                            class="btn green"
                            disabled={isSubmitting}
                          >
                            {" "}
                            Save Changes{" "}
                          </button>
                          <a href="#" class="btn default">
                            {" "}
                            Cancel{" "}
                          </a>
                        </div>
                      </form>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>{" "}
    </div>
  );
}
