import { useRouter } from "next/router";
import useSWR from "swr";
import { useForm } from "react-hook-form";
import Link from "next/link";
import { gql, hasuraUserClient } from "../../lib/hasura-user-client";
import { Form } from "react-bootstrap";
import { format, parseISO } from "date-fns";
const getTaskIds = gql`
  {
    Task {
      id
      name
      Description
      Date
      user_id
    }
  }
`;
const getTaskById = gql`
  query GetTaskByID($id: Int!) {
    Task_by_pk(id: $id) {
      id
      name
      Description
      Date
      user_id
    }
  }
`;
export const getStaticPaths = async () => {
  const hasura = hasuraUserClient();

  const { Task } = await hasura.request(getTaskIds);

  return {
    paths: Task.map(({ id }) => ({
      params: {
        id: id.toString(),
      },
    })),
    fallback: false,
  };
};
export const getStaticProps = async ({ params }) => {
  const hasura = hasuraUserClient();
  const { id } = params;

  const initialData = await hasura.request(getTaskById, { id });

  return {
    props: {
      initialData,
    },
    revalidate: 1,
  };
};
export default function AssignTask({}) {
  return (
    <div className="page-content-wrapper">
      <div className="page-content" style={{ minHeight: 1500 }}>
        <div class="profile-content">
          <div class="row">
            <div class="col-md-12">
              <div class="portlet light ">
                <div class="portlet-body">
                  <div class="row">
                    {" "}
                    <div class="col-md-11">
                      {" "}
                      <h3 className="caption-subject font-blue-madison bold uppercase">
                        Update Project information
                      </h3>{" "}
                      <ul className="nav nav-tabs">
                        <li className="active">
                          <a href="#tab_1_1" data-toggle="tab">
                            General Info
                          </a>
                        </li>
                        <li>
                          <a href="#tab_1_2" data-toggle="tab">
                            Update file/Contract
                          </a>
                        </li>
                        <li>
                          <a href="#tab_1_3" data-toggle="tab">
                            Update dates
                          </a>
                        </li>{" "}
                      </ul>
                    </div>
                    <div class="col-md-1">
                      {" "}
                      <div class="btn-group">
                        {" "}
                        <a
                          id="sample_editable_1_2_new"
                          class="btn sbold green"
                          onClick={() => addproject()}
                        >
                          Back <i className="icon-logout"></i>
                        </a>
                      </div>
                    </div>
                  </div>{" "}
                  <hr></hr>{" "}
                  <div className="tab-content">
                    <div className="tab-pane active" id="tab_1_1">
                      <form onSubmit={handleSubmit(onSubmit)}>
                        {" "}
                        <div class="form-group">
                          <label class="control-label">Client</label>
                          <input
                            defaultValue={data.Project_by_pk.company_name}
                            name="company_name"
                            id="company_name"
                            type="text"
                            placeholder="Client"
                            class="form-control"
                            ref={register}
                          />{" "}
                        </div>
                        <div class="form-group">
                          <label class="control-label">Project Title</label>
                          <input
                            defaultValue={data.Project_by_pk.Title}
                            name="Title"
                            id="Title"
                            type="text"
                            placeholder="Project Title"
                            class="form-control"
                            ref={register}
                          />{" "}
                        </div>
                        <div class="form-group">
                          <label class="control-label">
                            Project Description
                          </label>
                          <textarea
                            defaultValue={data.Project_by_pk.Description}
                            name="Description"
                            id="Description"
                            type="textarea"
                            placeholder="Description"
                            class="form-control"
                            ref={register}
                          />{" "}
                        </div>{" "}
                        <div class="form-group">
                          <label>Income</label>
                          <div class="form-group">
                            <input
                              defaultValue={data.Project_by_pk.income}
                              name="income"
                              class="form-control"
                              id="income"
                              type="text"
                              ref={register}
                            />
                            <span class="help-block"> </span>
                          </div>
                        </div>{" "}
                        <div class="form-group">
                          <label class="control-label">type</label>
                          <input
                            defaultValue={data.Project_by_pk.type}
                            name="type"
                            id="type"
                            type="text"
                            placeholder="type"
                            class="form-control"
                            ref={register}
                          />{" "}
                        </div>
                        {/*make it tabs  */}
                        <br />
                        <br />
                        <div class="margiv-top-10">
                          <button
                            type="submit"
                            class="btn green"
                            disabled={isSubmitting}
                          >
                            {" "}
                            Save Changes{" "}
                          </button>
                        </div>
                      </form>{" "}
                    </div>{" "}
                    <div className="tab-pane " id="tab_1_2"></div>
                    <div className="tab-pane " id="tab_1_3"></div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
