import { useEffect } from "react";
import { useRouter } from "next/router";
import { useForm } from "react-hook-form";

import { useAuthState, useAuthDispatch } from "../context/auth";

export default function RegisterPage() {
  const router = useRouter();
  const { isAuthenticated } = useAuthState();
  const { register: createUser } = useAuthDispatch();
  const {
    handleSubmit,
    register,
    errors,
    formState: { isSubmitting },
    setError,
  } = useForm({ defaultValues: { save_last_seen: true } });

  useEffect(() => {
    if (isAuthenticated) {
      router.push("/");
    }
  }, [isAuthenticated]);

  if (isAuthenticated) return null;

  const onSubmit = async ({ name, email, password, save_last_seen }) => {
    try {
      await createUser({ name, email, password, save_last_seen });

      router.push("/");
    } catch ({ message }) {
      setError("email", {
        type: "manual",
        message,
      });
    }
  };

  return (
    <body className="login">
      <form class="register-form" onSubmit={handleSubmit(onSubmit)}>
        <div class="content">
          {" "}
          <h3 class="font-green">Add User</h3>
          <input
            name="name"
            id="name"
            ref={register({
              required: "You must provide a name.",
            })}
            placeholder="Your name"
          />
          {errors.name && <span>{errors.name.message}</span>}
          <div>
            <input
              type="email"
              name="email"
              id="email"
              ref={register({
                required: "You must provide a email.",
                pattern: {
                  value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                  message: "You must provide a valid email",
                },
              })}
              placeholder="Your email"
            />
            {errors.email && <span>{errors.email.message}</span>}
          </div>
          <div>
            <input
              type="password"
              name="password"
              id="password"
              ref={register({
                required: "You must provide a password.",
                minLength: {
                  message: "Your password must be at least 6 characters",
                  value: 6,
                },
              })}
              placeholder="Choose a password"
            />
            {errors.password && <span>{errors.password.message}</span>}
          </div>
          <div>
            <label htmlFor="save_last_seen">
              <input
                type="checkbox"
                id="save_last_seen"
                name="save_last_seen"
                ref={register}
              />
              Show as online to other users
            </label>
          </div>
          <div>
            <button type="submit" disabled={isSubmitting}>
              Create account
            </button>
          </div>{" "}
        </div>
      </form>
    </body>
  );
}
