import { useState } from "react";
import { useRouter } from "next/router";
import { hasuraUserClient } from "../../lib/hasura-user-client";
import { gql } from "../../lib/hasura-admin-client";
import useSWR from "swr";
import { useAuthDispatch, useAuthState } from "../../context/auth";
import { useForm } from "react-hook-form";
import bcrypt from "bcryptjs";
import { useEffect } from "react";
import { format, intervalToDuration, parseISO, set } from "date-fns";

const GetUsersId = gql`
  {
    User {
      id
      Last_Name
      Name
      Avatar
      email
      Role
      Phone_Number
      Domain
      password
      File
    }
  }
`;

const GetUserById = gql`
  query GetUserById($id: Int!) {
    User_by_pk(id: $id) {
      id
      Name
      Avatar
      email
      Role
      Phone_Number
      Last_Name
      Domain
      password
      File
      Tasks_aggregate {
        aggregate {
          count
        }
      }
      LinkedIn
      Github
      Site
      User {
        Avatar
        email
        Name
        Last_Name
      }
    }
  }
`;
const GetTasks = gql`
  query MyQuery($id: Int!) {
    Task(where: { user_id: { _eq: $id } }) {
      id
      name
      user_id
      Description
      completed
      Project {
        Title
        id
        status
      }
    }
    Task_aggregate(
      where: { user_id: { _eq: $id }, completed: { _eq: false } }
    ) {
      aggregate {
        count
      }
    }
  }
`;
//tobedone
//Countprojects
const Countprojects = gql`
  query MyQuery($id: Int!) {
    Project_aggregate(where: { Tasks: { user_id: { _eq: $id } } }) {
      aggregate {
        count
      }
    }
  }
`;
const getLeave = gql`
  query MyQuery($id: Int!) {
    leave(where: { user_id: { _eq: $id } }) {
      id
      reason
      start_date
      end_date
      count_d
      status
      user_id
    }
  }
`;
export const getStaticPaths = async () => {
  const hasura = hasuraUserClient();

  const { User } = await hasura.request(GetUsersId);

  return {
    paths: User.map(({ id }) => ({
      params: {
        id: id.toString(),
      },
    })),
    fallback: false,
  };
};

export const getStaticProps = async ({ params }) => {
  const hasura = hasuraUserClient();
  const { id } = params;
  const initialData = await hasura.request(GetUserById, { id });
  const Countp = await hasura.request(Countprojects, { id });
  const tasks = await hasura.request(GetTasks, { id });
  const leave = await hasura.request(getLeave, { id });

  return {
    props: {
      initialData,
      Countp,
      tasks,
      leave,
    },
    revalidate: 1,
  };
};
const UpdateUser = gql`
  mutation update_User_by_pk(
    $id: Int!
    $Name: String!
    $Last_Name: String!
    $email: String!
    $Phone_Number: String!
    $LinkedIn: String!
    $Github: String!
    $Site: String!
  ) {
    update_User_by_pk(
      pk_columns: { id: $id }
      _set: {
        Name: $Name
        Last_Name: $Last_Name
        email: $email
        Phone_Number: $Phone_Number
        LinkedIn: $LinkedIn
        Github: $Github
        Site: $Site
      }
    ) {
      id
      Name
      Last_Name
      email
      Phone_Number
      LinkedIn
      Github
      Site
    }
  }
`;
const UpdatePassword = gql`
  mutation update_User_by_pk($id: Int!, $password: String!) {
    update_User_by_pk(pk_columns: { id: $id }, _set: { password: $password }) {
      id
    }
  }
`;
export default function PrivatePage({ initialData, Countp, tasks, leave }) {
  const [image, setImage] = useState(null);
  const [File, setFile] = useState(null);

  const { updateUser } = useAuthDispatch();
  const hasura = hasuraUserClient();

  const [createObjectURL, setCreateObjectURL] = useState(null);
  const router = useRouter();
  const { id } = router.query;
  const { isAuthenticated } = useAuthState();
  const [acc, setAcc] = useState(true);

  const {
    handleSubmit,
    register,
    control,
    errors,
    formState: { isSubmitting },
    defaultValues: { ...defaultValues },
  } = useForm();
  useEffect(() => {
    if (!isAuthenticated) router.push("/");
  }, [isAuthenticated]);

  const uploadToClient = (event) => {
    if (event.target.files && event.target.files[0]) {
      const i = event.target.files[0];
      setImage(i);
      setCreateObjectURL(URL.createObjectURL(i));
    }
  };
  const Account = () => {
    setAcc(false);
  };
  const view = () => {
    setAcc(true);
  };
  const removefile = () => {
    setImage(null);
  };
  const uploadFileToClient = (event) => {
    if (event.target.files && event.target.files[0]) {
      const i = event.target.files[0];
      setFile(i);
      setCreateObjectURL(URL.createObjectURL(i));
    }
  };
  const {
    register: register2,
    errors: errors2,
    handleSubmit: handleSubmit2,
  } = useForm();

  const uploadToServer = async () => {
    const body = new FormData();
    body.append("file", image);
    const response = await fetch("/api/file/", {
      method: "POST",
      body,
    });

    const UploadImage = gql`
      mutation MyMutation($Avatar: String!, $id: Int!) {
        update_User_by_pk(pk_columns: { id: $id }, _set: { Avatar: $Avatar }) {
          Avatar
          Name
        }
      }
    `;
    try {
      const { update_User_by_pk } = await hasura.request(UploadImage, {
        id,
        Avatar: image.name,
      });
      updateUser(update_User_by_pk);
      window.location.reload();
    } catch (error) {
      console.error(error);
    }
    return;
  };
  const uploadFileToServer = async () => {
    const body = new FormData();
    body.append("file", File);
    const response = await fetch("/api/file/", {
      method: "POST",
      body,
    });

    const UploadFile = gql`
      mutation MyMutation($File: String!, $id: Int!) {
        update_User_by_pk(pk_columns: { id: $id }, _set: { File: $File }) {
          File
        }
      }
    `;
    try {
      const { update_User_by_pk } = await hasura.request(UploadFile, {
        id,
        File: File.name,
      });
      updateUser(update_User_by_pk);
      window.location.reload();
    } catch (error) {
      console.error(error);
    }
    return;
  };
  const { data, mutate } = useSWR(
