import useSWR from "swr";
import formatRelative from "date-fns/formatRelative";
import differenceInMinutes from "date-fns/differenceInMinutes";
import Image from "react-bootstrap/Image";
import styled from "styled-components";

import { gql, hasuraUserClient } from "../lib/hasura-user-client";
import { differenceInHours } from "date-fns";

const today = new Date();

const GetUsers = gql`
  {
    User(order_by: { created_at: asc }) {
      id
      Name
      created_at
      last_seen
      Avatar
      last_seen_url
    }
  }
`;

const Dot = styled.span`
  background-color: #4cd137;
  position: absolute;
  right: 10px;
  bottom: 10px;
  height: 20px;
  width: 20px;
  border: 5px solid white;
  border-radius: 50%;
`;

const DotAway = styled.span`
  background-color: #f5c71a;
  position: absolute;
  right: 10px;
  bottom: 10px;
  height: 20px;
  width: 20px;
  border: 5px solid white;
  border-radius: 50%;
`;
export const getStaticProps = async () => {
  const hasura = hasuraUserClient();

  const initialData = await hasura.request(GetUsers);

  return {
    props: {
      initialData,
    },
    revalidate: 1,
  };
};

export default function MembersPage({ initialData }) {
  const hasura = hasuraUserClient();

  const { data } = useSWR(GetUsers, (query) => hasura.request(query), {
    initialData,
    revalidateOnMount: true,
  });

  return (
    <div className="page-content-wrapper">
      <div className="page-content" style={{ minHeight: 1500 }}>
        <div className="py-6">
          <h1 className="text-2xl md:text-3xl font-semibold text-primary-500">
            Members
          </h1>
        </div>

        <div className="grid grid-cols-1 gap-x-4 gap-y-8 sm:grid-cols-2 md:grid-cols-3">
          {data.User.map(
            ({ created_at, id, last_seen, Name, Avatar, last_seen_url }) => {
              const formattedJoinedAt = formatRelative(
                Date.parse(created_at),
                today,
                {
                  weekStartsOn: 1,
                }
              );

              const formattedLastSeen = differenceInMinutes(
                today,
                Date.parse(last_seen)
              );

              const formattedLastSeeninHours = differenceInHours(
                today,
                Date.parse(last_seen)
              );
              const isUserOnline = 1 >= formattedLastSeen;
              console.log(formattedLastSeen);
              console.log(isUserOnline);
              return (
                <div key={id}>
                  <div className="flex items-center">
                    <span className="relative mr-2 md:mr-4 flex items-center">
                      <span className="inline-block h-8 w-8 rounded-full overflow-hidden bg-gray-200">
                        <div class="image_outer_container">
                          {" "}
                          {isUserOnline ? <Dot /> : <DotAway />}
                          <div class="image_inner_container">
                            <img src={`../${Avatar}`} />{" "}
                          </div>
                        </div>
                      </span>{" "}
                    </span>

                    <div>
                      <h3 className="md:text-lg font-semibold text-gray-800">
                        {Name}
                      </h3>
                      <div className="text-xs text-gray-600">
                        Joined {formattedJoinedAt}
                      </div>
                      {formattedLastSeen} minutes ago
                      <div>routing path: {last_seen_url}</div>
                    </div>
                  </div>
                  <div class="row">
                    <div class="col-md-12">
                      <div class="portlet light portlet-fit bordered">
                        <div class="portlet-title">
                          <div class="caption">
                            <i class=" icon-layers font-green"></i>
                            <span class="caption-subject font-green bold uppercase">
                              Default
                            </span>
                          </div>
                        </div>
                        <div class="portlet-body">
                          <div class="mt-element-card mt-element-overlay">
                            <div class="row">
                              <div class="col-lg-3 col-md-4 col-sm-6 col-xs-12">
                                <div class="mt-card-item">
                                  <div class="mt-card-avatar mt-overlay-1">
                                    <img src={`../${Avatar}`} />
                                    <div class="mt-overlay">
                                      <ul class="mt-info">
                                        <li>
                                          <a
                                            class="btn default btn-outline"
                                            href="javascript:;"
                                          >
                                            <i class="icon-magnifier"></i>
                                          </a>
                                        </li>
                                        <li>
                                          <a
                                            class="btn default btn-outline"
                                            href="javascript:;"
                                          >
                                            <i class="icon-link"></i>
                                          </a>
                                        </li>
                                      </ul>
                                    </div>
                                  </div>
                                  <div class="mt-card-content">
                                    <h3 class="mt-card-name">{Name}</h3>
                                    <p class="mt-card-desc font-grey-mint">
                                      Managing Director
                                    </p>
                                    <div class="mt-card-social">
                                      <ul>
                                        <li>
                                          <a href="javascript:;">
                                            <i class="icon-social-facebook"></i>
                                          </a>
                                        </li>
                                        <li>
                                          <a href="javascript:;">
                                            <i class="icon-social-twitter"></i>
                                          </a>
                                        </li>
                                        <li>
                                          <a href="javascript:;">
                                            <i class="icon-social-dribbble"></i>
                                          </a>
                                        </li>
                                      </ul>
                                    </div>
                                  </div>
                                </div>
                              </div>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              );
            }
          )}
        </div>
      </div>
    </div>
  );
}
