import Layout from "../pages/Components/Layout";
import { AuthProvider } from "../context/auth";
import LastSeen from "./Components/LastSeen";
import gql from "graphql-tag";
import { hasuraUserClient } from "../lib/hasura-user-client";

const Notification_count = gql`
  query MyQuery {
    notification_aggregate {
      aggregate {
        count(columns: id)
      }
    }
  }
`;
export const getStaticProps = async () => {
  const hasura = hasuraUserClient();

  const initialData = await hasura.request(Notification_count);
  const c = initialData.notification_aggregate.aggregate.count;

  return {
    props: {
      initialData,
      c,
    },
    revalidate: 1,
  };
};
function MyApp({ Component, pageProps, data }) {
  return (
    <AuthProvider>
      <LastSeen>
        <Layout data={c}>
          <Component {...pageProps} />{" "}
        </Layout>
      </LastSeen>
    </AuthProvider>
  );
}

export default MyApp;
