import { useEffect } from "react";
import { useRouter } from "next/router";
import { useForm } from "react-hook-form";
import useSWR, { mutate } from "swr";

import { useAuthState, useAuthDispatch } from "../context/auth";
import { gql, hasuraAdminClient } from "../lib/hasura-admin-client";
import { hasuraUserClient } from "../lib/hasura-user-client";

const from = new Date().toISOString();

const GetUsers = gql`
  {
    User {
      Name
      id
      notifications_aggregate(
        where: { seen: { _eq: false } }
        order_by: { created_at: desc }
      ) {
        aggregate {
          count
        }
      }
      notifications(where: { seen: { _eq: false } }) {
        id
        description
      }
    }
  }
`;
const GetTodos = gql`
  {
    Todo(where: { Member: { _eq: "Employee" }, Type: { _eq: "Offboarding" } }) {
      id
      Description
      Name
    }
  }
`;
const GetTodoshr = gql`
  {
    Todo(where: { Member: { _eq: "Admin" }, Type: { _eq: "Offboarding" } }) {
      id
      Description
      Name
    }
  }
`;
const GetUserById = gql`
  query GetUserById($user_id: Int!) {
    User_by_pk(id: $user_id) {
      id
      notifications_aggregate(where: { seen: { _eq: false } }) {
        aggregate {
          count
        }
      }
      notifications(where: { seen: { _eq: false } }) {
        id
        description
      }
    }
  }
`;
const AddNotification = gql`
  mutation MyMutation($user_id: Int!) {
    insert_notification_one(
      object: {
        user_id: $user_id
        name: "ttt"
        seen: false
        description: "tttttt"
      }
    ) {
      user_id
      seen
      name
      id
      description
    }
  }
`;

const InsertTask = gql`
  mutation InsertTask(
    $Description: String!
    $Name: String!
    $from: timestamptz!
    $Member: String!
  ) {
    insert_Todo_one(
      object: {
        Description: $Description
        Name: $Name
        Date: $from
        Type: "Offboarding"
        Member: $Member
      }
    ) {
      Description
      Date
      Name
    }
  }
`;

export const getStaticProps = async () => {
  const { User } = await hasuraAdminClient.request(GetUsers);
  const initialData = await hasuraAdminClient.request(GetTodos);
  return {
    props: {
      User,
      initialData,
    },
  };
};

export default function OffBoarding({ User, initialData }) {
  const router = useRouter();
  const { isAuthenticated, user } = useAuthState();
  const hasura = hasuraUserClient();
  const {
    handleSubmit,
    register,
    control,
    errors,
    formState: { isSubmitting },
  } = useForm();
  const {
    register: register2,
    errors: errors2,
    handleSubmit: handleSubmit2,
  } = useForm();
  const { updateUser } = useAuthDispatch();
  useEffect(() => {
    if (!isAuthenticated) router.push("/");
  }, [isAuthenticated]);

  if (!isAuthenticated) return null;
  const { data } = useSWR(GetTodos, (query) => hasura.request(query), {
    initialData,
    revalidateOnMount: true,
  });
  const onSubmit = async ({ Description, Name, Member }) => {
    console.log(from);
    try {
      const { insert_Todo_one } = await hasura.request(InsertTask, {
        Description,
        from,
        Name,
        Member,
      });

      window.location.reload();
    } catch (err) {
      console.log(err);
    }
  };

  const Assign = async ({ user_id }) => {
    const u_id = user_id;
    console.log(u_id);
    const t = data.Todo.map(({ id }) => {
      return { todo_id: id, user_id: u_id, completed: false };
    });
    const query = gql`
      mutation insert_todo_user($objects: [todo_user_insert_input!]!) {
        insert_todo_user(objects: $objects) {
          affected_rows
        }
      }
    `;
    if (confirm(`are you sure you want to assign those tasks to ${u_id}`)) {
      try {
        const { insert_todo_user } = await hasura.request(query, {
          objects: t,
        });
        mutate();
        window.location.reload();
      } catch (error) {
        console.error(error);
      }
    } else {
      return null;
    }
  };
  const deleteTodo = gql`
    mutation MyMutation($id: Int!) {
      delete_Todo_by_pk(id: $id) {
        id
      }
    }
  `;

  const deleteATodo = async (id) => {
    if (confirm("are you sure you want to delete this todo?")) {
      try {
        await hasura.request(deleteTodo, { id });
        mutate();
        window.location.reload();
      } catch (error) {
        console.error(error);
      }
    } else {
      return null;
    }
  };
  return (
    <div className="page-content-wrapper">
      <div className="page-content" style={{ minHeight: 1500 }}>
        <div class="profile-content">
          <div class="row">
            <div class="col-md-6">
              <div class="portlet light ">
                <div class="portlet-body">
                  {" "}
                  <h3>Add new offboarding Task</h3>
                  <div class="tab-content">
                    <div class="tab-pane active" id="tab_1_1">
                      <form onSubmit={handleSubmit(onSubmit)}>
                        <div class="form-group">
                          <label class="control-label">Name</label>
                          <input
                            name="Name"
                            id="Name"
                            type="text"
                            placeholder="Todo Name"
                            class="form-control"
                            ref={register}
                            required
                          />{" "}
                        </div>
                        <div class="form-group">
                          <label class="control-label">Description</label>
                          <textarea
                            name="Description"
                            id="Description"
                            type="textarea"
                            placeholder="Description"
                            class="form-control"
                            ref={register}
                            required
                          />{" "}
