import useSWR, { mutate } from "swr";
import { useRouter } from "next/router";

import formidable from "formidable";
import fs from "fs";
import { hasuraAdminClient, gql } from "../../lib/hasura-admin-client";
import { hasuraUserClient } from "../../lib/hasura-user-client";

export const config = {
  api: {
    bodyParser: false,
  },
};

export default function seif() {
  const router = useRouter();
  const { id } = router.query;
  return id;
}
async (req, res) => {
  const form = new formidable.IncomingForm();
  form.parse(req, async function (err, fields, files) {
    await saveFile(files.file);
    return res.status(201).send("");
  });
};
const saveFile = async (file) => {
  const id = this.props.id;
  const hasura = hasuraUserClient();
  const data = fs.readFileSync(file.path);
  fs.writeFileSync(`./public/${file.name}`, data);
  await fs.unlinkSync(file.path);
  const Avatar = file.name;

  const UploadImage = gql`
    mutation MyMutation($Avatar: String!, $id: Int!) {
      update_User_by_pk(pk_columns: { id: $id }, _set: { Avatar: $Avatar }) {
        Avatar
        Name
      }
    }
  `;
  try {
    await hasura.request(UploadImage, { id, Avatar });
    mutate();
  } catch (error) {
    console.error(error);
  }
  return;
};
