                <div class="form-group">
                  <input
                    class="form-control placeholder-no-fix"
                    type="text"
                    autocomplete="off"
                    placeholder="Email"
                    name="email"
                  />{" "}
                </div>
                <div class="form-actions">
                  <button
                    type="button"
                    id="back-btn"
                    class="btn blue btn-outline"
                  >
                    Back
                  </button>
                  <button type="submit" class="btn blue uppercase pull-right">
                    Submit
                  </button>
                </div>
              </form>
            </div>
            <div class="login-footer">
