import React from 'react'
import Header from './Header/Header'
import Footer from './Footer/Footer'
import Sidebar from './Sidebar/Sidebar'
import Page from './Page/Page';
import Content from './Content/Content';import { useRouter } from "next/router";
const Notification_count = gql`
  query MyQuery {
    notification_aggregate {
      aggregate {
        count(columns: id)
      }
    }
  }
`;
export const getStaticProps = async () => {
  const hasura = hasuraUserClient();

  const initialData = await hasura.request(Notification_count);
  const c = initialData.notification_aggregate.aggregate.count;

  return {
    props: {
      initialData,
      c,
    },
    revalidate: 1,
  };
};
export default function Layout({ children }) {
    return (
      <>    <div class="page-container">

        <Header />
        <Sidebar/>   
          { children }
         <Footer />
         </div>
      </>
    );
  }
