import { gql } from "graphql-request";
import { useRouter } from "next/router";
import useSWR from "swr";
import { hasuraUserClient } from "../lib/hasura-user-client";
import Layout from "./Components/Layout";
import FullCalendar from "@fullcalendar/react"; // must go before plugins
import dayGridPlugin from "@fullcalendar/daygrid";
import "@fullcalendar/common/main.css";
import "@fullcalendar/daygrid/main.css";
import { format, parseISO } from "date-fns";
import "@fullcalendar/timegrid/main.css";
import "@fullcalendar/list/main.css";
import interactionPlugin from "@fullcalendar/interaction";
import timeGridPlugin from "@fullcalendar/timegrid";
import listPlugin from "@fullcalendar/list";
import { useAuthState, useAuthDispatch } from "../context/auth";
import { useEffect } from "react";
import { useState } from "react";
import styled from "styled-components";
import formatRelative from "date-fns/formatRelative";
import differenceInMinutes from "date-fns/differenceInMinutes";

import { differenceInHours } from "date-fns";
import { differenceInDays } from "date-fns";

const today = new Date();

const Dot = styled.span`
  background-color: #4cd137;
  position: absolute;
  left:97px;
  bottom:155px
  height: 20px;
  width: 20px;
  border: 5px solid white;
  border-radius: 50%;
`;

const DotAway = styled.span`
  background-color: #f5c71a;
  position: absolute;
  left:97px;
  bottom:155px
  height: 20px;
  width: 20px;
  border: 5px solid white;
  border-radius: 50%;
`;
const Users = gql`
  query MyQuery {
    User_aggregate {
      aggregate {
        count
      }
    }
  }
`;
const GetEvents = gql`
  query MyQuery {
    Event {
      id
      Title
      description
      start_date
    }
  }
`;
const Projects = gql`
  query Query {
    Project_aggregate {
      aggregate {
        count
      }
    }
  }
`;
const Candidats = gql`
  query Query {
    Candidate_aggregate(where: { Status: { _eq: 0 } }) {
      aggregate {
        count
      }
    }
  }
`;

const GetUsers = gql`
  {
    User(order_by: { created_at: asc }) {
      id
      Name
      created_at
      last_seen
      Avatar
      last_seen_url
    }
  }
`;

export const getStaticProps = async () => {
  const hasura = hasuraUserClient();

  const initialData = await hasura.request(Users);
  const ProjectData = await hasura.request(Projects);
  const CandidatsData = await hasura.request(Candidats);
  const Events = await hasura.request(GetEvents);
  const users = await hasura.request(GetUsers);

  return {
    props: {
      initialData,
      ProjectData,
      CandidatsData,
      Events,
      users,
    },
    revalidate: 1,
  };
};

export default function Home({
  initialData,
  ProjectData,
  CandidatsData,
  Events,
  users,
}) {
  const hasura = hasuraUserClient();
  const router = useRouter();
  const { isAuthenticated } = useAuthState();
  const [loaded, setLoaded] = useState(false);

  ///////////////////////////
  ///////redirectifnotloggedin

  useEffect(() => {
    if (!isAuthenticated) {
      router.push("/login");
    } else {
      setLoaded(true);
    }
  }, []);

  ////
  const { data } = useSWR(Users, (query) => hasura.request(query), {
    initialData,
    revalidateOnMount: true,
  });
  const { datay } = useSWR(GetUsers, (query) => hasura.request(query), {
    users,
    revalidateOnMount: true,
  });
  console.log(users);
  const { dataEvents } = useSWR(GetEvents, (query) => hasura.request(query), {
    Events,
    revalidateOnMount: true,
  });
  if (!loaded) {
    return <div></div>; //show nothing or a loader
  }
  return (
    <>
      <div className="page-content-wrapper">
        <div className="page-content" style={{ minHeight: 1500 }}>
          <h1 class="page-title"> Admin Dashboard</h1>
          <small>statistics, charts, recent events and reports</small>
          <div class="row">
            <div class="col-lg-3 col-md-3 col-sm-6 col-xs-12">
              <a class="dashboard-stat dashboard-stat-v2 blue" href="#">
                <div class="visual">
                  <i class="fa fa-comments"></i>
                </div>
                <div class="details">
                  <div class="number">
                    <span data-counter="counterup" data-value="1349">
                      {data.User_aggregate.aggregate.count}
                    </span>
                  </div>
                  <div class="desc"> Users </div>
                </div>
              </a>
            </div>
            <div class="col-lg-3 col-md-3 col-sm-6 col-xs-12">
              <a class="dashboard-stat dashboard-stat-v2 red" href="#">
                <div class="visual">
                  <i class="fa fa-bar-chart-o"></i>
                </div>
                <div class="details">
                  <div class="number">
                    <span data-counter="counterup" data-value="12,5"></span>
                    {ProjectData.Project_aggregate.aggregate.count}
                  </div>
                  <div class="desc"> Projects </div>
                </div>
              </a>
            </div>
            <div class="col-lg-3 col-md-3 col-sm-6 col-xs-12">
              <a class="dashboard-stat dashboard-stat-v2 green" href="#">
                <div class="visual">
                  <i class="fa fa-shopping-cart"></i>
                </div>
                <div class="details">
                  <div class="number">
                    <span data-counter="counterup" data-value="549">
                      {CandidatsData.Candidate_aggregate.aggregate.count}
                    </span>
                  </div>
                  <div class="desc"> Pending Candidats </div>
                </div>
              </a>
            </div>
            <div class="col-lg-3 col-md-3 col-sm-6 col-xs-12">
              <a class="dashboard-stat dashboard-stat-v2 purple" href="#">
                <div class="visual">
                  <i class="fa fa-globe"></i>
                </div>
                <div class="details">
                  <div class="number">
                    {" "}
                    +<span
                      data-counter="counterup"
                      data-value="89"
                    ></span>%{" "}
                  </div>
                  <div class="desc"> Brand Popularity </div>
                </div>
              </a>
            </div>{" "}
          </div>{" "}
          <div class="row">
            <div class="col-md-6">
              <div class="portlet light bordered">
                <div class="portlet-title">
                  <div class="caption">
                    <i class="icon-bubble font-dark hide"></i>
                  </div>
                  {users.User.map(
                    ({
                      created_at,
                      id,
                      last_seen,
                      Name,
                      Avatar,
                      last_seen_url,
                    }) => {
                      const formattedJoinedAt = formatRelative(
                        Date.parse(created_at),
                        today,
                        {
                          weekStartsOn: 1,
                        }
                      );

                      const formattedLastSeen = differenceInMinutes(
                        today,
                        Date.parse(last_seen)
                      );

                      const formattedLastSeeninHours = differenceInHours(
                        today,
                        Date.parse(last_seen)
                      );
                      const formattedLastSeenindays = differenceInDays(
                        today,
                        Date.parse(last_seen)
                      );
                      const isUserOnline = 2 >= formattedLastSeen;

                      return (
                        <div class="col-md-2">
                          <div class="mt-img">
                            {Avatar ? (
                              <div className="profile-userpic">
                                {" "}
                                <img
                                  style={{
                                    display: "inline",
                                    width: 100,
                                    height: 100,
                                    objectFit: "cover",
                                  }}
                                  src={`../${Avatar}`}
                                />{" "}
                                {isUserOnline ? <Dot /> : <DotAway />}
                              </div>
                            ) : (
                              <div className="profile-userpic">
                                <img
                                  style={{
                                    display: "inline",
                                    width: 100,
                                    height: 100,
                                    objectFit: "cover",
                                  }}
                                  src={`../profile.png`}
                                />
                              </div>
                            )}
                          </div>
                          <div class="mt-body">
                            <h3 class="mt-username">{Name}</h3>
                            <p class="mt-user-title">
                              {" "}
                              Lorem Ipsum is simply dummy text.{" "}
                            </p>
                          </div>
                        </div>
                      );
                    }
                  )}
                </div>
              </div>
            </div>
          </div>
          <FullCalendar
            plugins={[
              dayGridPlugin,
              timeGridPlugin,
              interactionPlugin,
              listPlugin,
            ]}
            headerToolbar={{
              left: "prev,next today",
              center: "title",
              right: "dayGridMonth,timeGridWeek,timeGridDay,listWeek",
            }}
            initialView="dayGridMonth"
            editable={true}
            selectable={true}
            selectMirror={true}
            dayMaxEvents={true}
            eventRemove
            events={Events.Event.map(({ id, Title, start_date }) => {
              return { title: Title, date: start_date };
            })}
            eventColor="#378006"

            // weekends={this.state.weekendsVisible}
            // initialEvents={INITIAL_EVENTS} // alternatively, use the `events` setting to fetch from a feed
            // select={this.handleDateSelect}
            // eventContent={renderEventContent} // custom render function
            // eventsSet={this.handleEvents} // called after events are initialized/added/changed/removed
            /* you can update a remote database when these fire:
                eventAdd={function(){}}
                eventChange={function(){}}
                */
          />
          <div class="clearfix"></div>
        </div>
      </div>
    </>
  );
}
