import useSWR, { mutate } from "swr";

import gql from "graphql-tag";
import { useRouter } from "next/router";
import PDFLayout from "./Components/PDFLayout";
import pdfHelper from "../lib/pdfHelper";
import { hasuraUserClient } from "../lib/hasura-user-client";

const today = new Date();

const GetUsers = gql`
  query MyQuery {
    User {
      id
      Name
      email
    }
  }
`;
export const getStaticProps = async () => {
  const hasura = hasuraUserClient();

  const initialData = await hasura.request(GetUsers);

  return {
    props: {
      initialData,
    },
    revalidate: 1,
  };
};
const pdf = async ({ req, res, query }) => {
  const exportPDF = query.exportPDF === "true";
  const isServer = !!req;

  if (isServer && exportPDF) {
    const buffer = await pdfHelper.componentToPDFBuffer(
      <PDFLayout>
        <Article />
      </PDFLayout>
    );

    // with this header, your browser will prompt you to download the file
    // without this header, your browse will open the pdf directly
    res.setHeader("Content-disposition", 'attachment; filename="article.pdf');

    // set content type
    res.setHeader("Content-Type", "application/pdf");

    // output the pdf buffer. once res.end is triggered, it won't trigger the render method
    res.end(buffer);
  }
};
export default function MembersPage({ initialData }) {
  const hasura = hasuraUserClient();
  const router = useRouter();

  const { data } = useSWR(GetUsers, (query) => hasura.request(query), {
    initialData,
    revalidateOnMount: true,
  });
  const deleteATodo = async (id) => {
    const query = gql`
      mutation delete_User($id: Int!) {
        delete_User(where: { id: { _eq: $id } }) {
          affected_rows
        }
      }
    `;

    try {
      await hasura.request(query, { id });
      mutate();
      window.location.reload();
    } catch (error) {
      console.error(error);
    }
  };
  return (
    <div className="page-content-wrapper">
      <div className="page-content" style={{ minHeight: 1500 }}>
        <div class="row">
          <div class="col-md-12">
            <div class="portlet light bordered">
              <div class="portlet-title">
                <div class="caption font-dark">
                  <i class="icon-settings font-dark"></i>
                  <span class="caption-subject bold uppercase">
                    {" "}
                    Individual column searching (select inputs)
                  </span>
                </div>
                <div class="actions">
                  <div
                    class="btn-group btn-group-devided"
                    data-toggle="buttons"
                  >
                    <label class="btn btn-transparent dark btn-outline btn-circle btn-sm active">
                      <input
                        type="radio"
                        name="options"
                        class="toggle"
                        id="option1"
                      />
                      Actions
                    </label>
                    <label class="btn btn-transparent dark btn-outline btn-circle btn-sm">
                      <input
                        type="radio"
                        name="options"
                        class="toggle"
                        id="option2"
                      />
                      Settings
                    </label>
                  </div>
                </div>
              </div>
              <div class="portlet-body">
                <div class="table-toolbar">
                  <div class="row">
                    <div class="col-md-6">
                      <div class="btn-group">
                        <button
                          id="sample_editable_1_2_new"
                          class="btn sbold green"
                        >
                          {" "}
                          Add New
                          <i class="fa fa-plus"></i>
                        </button>
                      </div>
                    </div>
                    <div class="col-md-6">
                      <div class="btn-group pull-right">
                        <button
                          class="btn green  btn-outline dropdown-toggle"
                          data-toggle="dropdown"
                        >
                          Tools
                          <i class="fa fa-angle-down"></i>
                        </button>
                        <ul class="dropdown-menu pull-right">
                          <li>
                            <a href="#">
